  // Update rubric grading
  const updateRubricGrading = (criterionId, level, comments = null) => {
    setRubricGrading(prev => {
      const existingGrading = prev[criterionId];
      const existingComments = existingGrading?.customComments || '';

      return {
        ...prev,
        [criterionId]: {
