          const reader = new FileReader();
          reader.onload = (e) => {
            attachment.base64Data = e.target.result;
            resolve(attachment);
          };
          reader.readAsDataURL(attachment.file);
        } else {
          resolve(attachment);
        }
      });
    })).then(processedAttachments => {
