 * @param {Array} rawData - Raw Excel data
 * @param {number} studentRowIndex - Index of row containing student data
 * @returns {number} - Header row index or -1 if not found
 */
const findHeaderRow = (rawData, studentRowIndex) => {
  // Look up to 5 rows back for headers
  for (let i = Math.max(0, studentRowIndex - 5); i < studentRowIndex; i++) {
    const row = rawData[i];
    if (!row || row.length === 0) continue;
    const headerScore = calculateHeaderScore(row);
    if (headerScore >= 3) {
      console.log(`üìã Found header row ${i} with score ${headerScore}:`, row);
      return i;
    }
  }

  // If no clear headers found, assume the row immediately before student data
  return Math.max(0, studentRowIndex - 1);
};

/**
 * Find the end of the data table
 * @param {Array} rawData - Raw Excel data
 * @param {number} startRow - Starting row index
 * @returns {number} - Last row index with data
 */
const findDataEndRow = (rawData, startRow) => {
  let lastDataRow = startRow;
  for (let i = startRow; i < rawData.length; i++) {
    const row = rawData[i];

    // Stop if we hit an empty row or summary information
    if (!row || row.length === 0) break;

    // Stop if we hit administrative text like "Total Students:" or "Signature"
    const rowText = row.join(' ').toLowerCase();
    if (rowText.includes('total') || rowText.includes('signature') || rowText.includes('professor') || rowText.includes('page ')) {
      break;
    }

    // Continue if row has substantial data (at least 3 non-empty cells)
    const nonEmptyCells = row.filter(cell => cell !== null && cell !== undefined && String(cell).trim() !== '').length;
    if (nonEmptyCells >= 3) {
      lastDataRow = i;
    } else if (i > startRow + 2) {
      // If we've seen some data and now have sparse rows, probably end of table
      break;
    }
  }
  return lastDataRow;
};

