    const cellStr = String(cell).trim().toLowerCase();
    if (cellStr.length === 0) return;

    // Check against all pattern categories
    Object.values(headerPatterns).forEach(patterns => {
      if (patterns.some(pattern => cellStr.includes(pattern) || pattern.includes(cellStr))) {
        score++;
      }
    });
  });
  console.log(`üìä Header score for row: ${score}`, row);
  return score;
};

