/**
 * Enhanced column mapping with institutional patterns
 * @param {Array} headers - Header row
 * @returns {Object} - Column index mapping
 */
const mapColumns = (headers) => {
    const mapping = {};

    // Enhanced patterns for institutional Excel files
    const patterns = {
        component: ['component', 'session', 'section', 'class'],
        id: ['id', 'student id', 'studentid', 'number', 'student number'],
        name: ['name', 'student name', 'full name', 'student'],
        email: ['email', 'e-mail', 'student email', 'emailaddress'],
        program: ['program', 'course', 'programcode', 'coursecode', 'course code'],
        level: ['level', 'year', 'semester', 'term', 'grade'],
        campus: ['campus', 'location', 'site'],
        status: ['status', 'enrollment', 'active', 'enrolled', 'standing']
    };

    // Map each header to a column type
    headers.forEach((header, index) => {
        const headerStr = String(header).trim().toLowerCase();

        if (headerStr.length === 0) return;

        // Check against each pattern category
        Object.entries(patterns).forEach(([key, patternList]) => {
            if (!mapping[key]) { // Only assign if not already mapped
                const match = patternList.some(pattern =>
                    headerStr.includes(pattern) || pattern.includes(headerStr)
                );

                if (match) {
                    mapping[key] = index;
                    console.log(`üìç Mapped "${header}" (index ${index}) to ${key}`);
                }
            }
        });
    });

    return mapping;
};

/**
 * Get value from row using column mapping
 * @param {Array} row - Data row
 * @param {number} columnIndex - Column index
 * @returns {string} - Cell value or empty string
 */
const getColumnValue = (row, columnIndex) => {
    if (columnIndex === undefined || columnIndex === null || columnIndex < 0) {
        return '';
    }

    const value = row[columnIndex];
    return value !== null && value !== undefined ? String(value).trim() : '';
};

/**
 * Validate student data for completeness and accuracy
 * @param {Array} students - Array of student objects
 * @returns {Object} - Validation results with statistics and issues
 */
export const validateStudentData = (students) => {
    const validation = {
        totalStudents: students.length,
        validStudents: 0,
        issues: [],
        completeness: {
            withId: 0,
            withName: 0,
            withEmail: 0,
            withProgram: 0,
            withLevel: 0,
            withCampus: 0,
            withStatus: 0
        },
        duplicateIds: [],
        invalidEmails: [],
        missingRequiredFields: []
    };

    const seenIds = new Set();

    students.forEach((student, index) => {
        let isValid = true;

        // Check required fields
        if (!student.id || student.id.trim() === '') {
            validation.issues.push(`Row ${index + 1}: Missing student ID`);
            validation.missingRequiredFields.push({
                row: index + 1,
                field: 'id',
                student: student.name || 'Unknown'
            });
            isValid = false;
        } else {
            validation.completeness.withId++;

            // Check for duplicate IDs
            if (seenIds.has(student.id)) {
                validation.duplicateIds.push({
                    id: student.id,
                    rows: [index + 1] // Could track multiple occurrences
                });
                validation.issues.push(`Row ${index + 1}: Duplicate student ID ${student.id}`);
                isValid = false;
            } else {
                seenIds.add(student.id);
            }
        }

        if (!student.name || student.name.trim() === '') {
            validation.issues.push(`Row ${index + 1}: Missing student name`);
            validation.missingRequiredFields.push({
                row: index + 1,
                field: 'name',
                student: student.id || 'Unknown'
            });
            isValid = false;
        } else {
            validation.completeness.withName++;
        }

        // Check optional fields for completeness tracking
        if (student.email && student.email.trim() !== '') {
            validation.completeness.withEmail++;

            // Basic email validation
            const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
            if (!emailRegex.test(student.email)) {
                validation.invalidEmails.push({
                    row: index + 1,
                    email: student.email,
                    student: student.name || student.id
                });
                validation.issues.push(`Row ${index + 1}: Invalid email format: ${student.email}`);
            }
        }

        if (student.program && student.program.trim() !== '') {
            validation.completeness.withProgram++;
        }

        if (student.level && student.level.trim() !== '') {
            validation.completeness.withLevel++;
        }

        if (student.campus && student.campus.trim() !== '') {
            validation.completeness.withCampus++;
        }

        if (student.status && student.status.trim() !== '') {
            validation.completeness.withStatus++;
        }

        if (isValid) {
            validation.validStudents++;
        }
    });

    // Calculate completeness percentages
    const total = students.length;
    validation.completenessPercentages = {
        id: total > 0 ? (validation.completeness.withId / total * 100).toFixed(1) : 0,
        name: total > 0 ? (validation.completeness.withName / total * 100).toFixed(1) : 0,
        email: total > 0 ? (validation.completeness.withEmail / total * 100).toFixed(1) : 0,
        program: total > 0 ? (validation.completeness.withProgram / total * 100).toFixed(1) : 0,
        level: total > 0 ? (validation.completeness.withLevel / total * 100).toFixed(1) : 0,
        campus: total > 0 ? (validation.completeness.withCampus / total * 100).toFixed(1) : 0,
        status: total > 0 ? (validation.completeness.withStatus / total * 100).toFixed(1) : 0
    };

    // Overall validation status
    validation.isValid = validation.validStudents === validation.totalStudents;
    validation.validationScore = total > 0 ? (validation.validStudents / total * 100).toFixed(1) : 0;

    return validation;
};

/**
 * Extract course metadata from the Excel file raw data
 * @param {Array} rawData - Raw Excel data as array of arrays
 * @param {Array} students - Parsed student data
 * @returns {Object} - Course metadata information
 */
const extractCourseMetadata = (rawData, students) => {
    const metadata = {
        courseName: '',
        courseCode: '',
        courseOffering: '',
        professors: '',
        term: '',
        campus: '',
        hours: '',
        component: '',
        section: '',
        totalStudents: students.length,
        gradeScale: '',
        department: ''
    };

    // Search through the first 15 rows for institutional metadata
    for (let i = 0; i < Math.min(rawData.length, 15); i++) {
        const row = rawData[i];
        if (!row || row.length === 0) continue;

        const rowText = row.join(' ').trim();

        // Look for course offering pattern: "Offered: ANM3012 - Character Dynamics and Motion"
        if (rowText.includes('Offered:')) {
            const match = rowText.match(/Offered:\s*([A-Z0-9]+)\s*-\s*(.+)/);
            if (match) {
                metadata.courseOffering = match[1]; // ANM3012
                metadata.courseName = match[2]; // Character Dynamics and Motion
            }
        }

        // Look for course pattern: "Course: ART301 - Bachelor of Animation Arts Section A - Main Campus"
        if (rowText.includes('Course:')) {
            const match = rowText.match(/Course:\s*([A-Z0-9]+)\s*-\s*(.+)/);
            if (match) {
                metadata.courseCode = match[1]; // ART301
                const fullName = match[2]; // Bachelor of Animation Arts Section A - Main Campus

                // Extract section and campus
                if (fullName.includes('Section')) {
                    const sectionMatch = fullName.match(/Section\s+([A-Z])/);
                    if (sectionMatch) {
                        metadata.section = `Section ${sectionMatch[1]}`;
                    }
                }

                if (fullName.includes('Campus')) {
                    const campusMatch = fullName.match(/(\w+)\s+Campus/);
                    if (campusMatch) {
                        metadata.campus = `${campusMatch[1]} Campus`;
                    }
                }

                // Extract base course name (before Section)
                const baseNameMatch = fullName.match(/^([^-]+?)(?:\s+Section|$)/);
                if (baseNameMatch && !metadata.courseName) {
                    metadata.courseName = baseNameMatch[1].trim();
                }
            }
        }

        // Look for hours: "Hours: 2025-07-01 to 2025-10-15 (80)"
        if (rowText.includes('Hours:')) {
            const match = rowText.match(/Hours:\s*(.+)/);
            if (match) {
                metadata.hours = match[1].trim();
            }
        }

        // Look for professors: "Professors: Jordan Parker, Taylor Nguyen"
        if (rowText.includes('Professors:')) {
            const match = rowText.match(/Professors:\s*(.+)/);
            if (match) {
                metadata.professors = match[1].trim();
            }
        }

        // Look for grade scale: "Grade Scale: Pass 60 (C)"
        if (rowText.includes('Grade Scale:')) {
            const match = rowText.match(/Grade Scale:\s*(.+)/);
            if (match) {
                metadata.gradeScale = match[1].trim();
            }
        }

        // Look for department: "Delivery Dept: B439 - Creative Industries (DR)"
        if (rowText.includes('Delivery Dept:')) {
            const match = rowText.match(/Delivery Dept:\s*(.+)/);
            if (match) {
                metadata.department = match[1].trim();
            }
        }

        // Look for term information
        if (rowText.includes('Fall') || rowText.includes('Winter') || rowText.includes('Spring') || rowText.includes('Summer')) {
            const termMatch = rowText.match(/(Fall|Winter|Spring|Summer)\s+(\d{4})/);
            if (termMatch) {
                metadata.term = `${termMatch[1]} ${termMatch[2]}`;
            }
        }
    }

    // Fallback to student data if metadata not found in headers
    if (students.length > 0) {
        const firstStudent = students[0];

        if (!metadata.courseCode && firstStudent.program) {
            metadata.courseCode = firstStudent.program;
        }

        if (!metadata.campus && firstStudent.campus) {
            metadata.campus = firstStudent.campus;
        }

        if (!metadata.component && firstStudent.component) {
            metadata.component = firstStudent.component;
        }
    }

    // Set default values based on known institutional structure
    if (!metadata.term) {
        metadata.term = 'Fall 2025';
    }

    if (!metadata.campus) {
        metadata.campus = 'Main Campus';
    }

    console.log('üìã Extracted course metadata:', metadata);
    return metadata;
};
