import React, { useState, useRef, useEffect } from 'react';
import { Download, Sparkles, FileText, ArrowRight, Lightbulb, Upload, HelpCircle, Settings } from 'lucide-react';
import { useAssessment } from './SharedContext';
import gradingPolicyService from '../services/gradingPolicyService';

// SimpleRichTextEditor component (from RubricCreator.js)
const SimpleRichTextEditor = React.forwardRef(({ value, onChange, placeholder }, ref) => {
  useEffect(() => {
    if (ref.current) {
      ref.current.innerHTML = value || '';
    }
  }, [value, ref]);

  const handlePaste = (e) => {
    e.preventDefault();
    const clipboard = e.clipboardData;
    const textData = clipboard.getData('text/plain');

    // Convert plain text to HTML, preserving line breaks
    const cleanContent = textData
      .replace(/&/g, '&amp;')
      .replace(/</g, '&lt;')
      .replace(/>/g, '&gt;')
      .replace(/\r\n|\r|\n/g, '<br>');

    // Insert the clean content
    document.execCommand('insertHTML', false, cleanContent);

    // Trigger onChange
    if (onChange) {
      onChange(ref.current.innerHTML);
    }
  };

  return (
    <div className="rich-text-editor">
      <div
        ref={ref}
        contentEditable
        onInput={(e) => onChange(e.target.innerHTML)}
        onPaste={handlePaste}
        className="min-h-32 p-3 border border-gray-300 rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent bg-white"
        style={{ minHeight: '120px' }}
        placeholder={placeholder}
        suppressContentEditableWarning={true}
      />
      <style>{`
        [contenteditable]:empty:before {
          content: attr(placeholder);
          color: #9ca3af;
          font-style: italic;
        }
        [contenteditable] ul {
          list-style-type: disc;
          margin-left: 1.5em;
          margin-top: 0.5em;
          margin-bottom: 0.5em;
        }
        [contenteditable] ol {
          list-style-type: decimal;
          margin-left: 1.5em;
          margin-top: 0.5em;
          margin-bottom: 0.5em;
