// src/App.js
import React, { useState, useEffect } from 'react';
import { AssessmentProvider, useAssessment } from './components/SharedContext';
import TabNavigation from './components/TabNavigation';
import AIRubricPromptGenerator from './components/AIRubricPromptGenerator';
import AssignmentPromptGenerator from './components/AssignmentPromptGenerator';
import RubricCreator from './components/RubricCreator';
import ClassListManager from './components/ClassListManager';
import GradingTemplate from './components/GradingTemplate';
import ExcelImportTest from './components/ExcelImportTest';
import './index.css';

// Scroll to Top Button Component
const ScrollToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isScrolling, setIsScrolling] = useState(false);

  // Show button when page is scrolled down
  useEffect(() => {
    const toggleVisibility = () => {
      // Show button when scrolled more than 400px
      if (window.pageYOffset > 400) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    // Add scroll listener
    window.addEventListener('scroll', toggleVisibility);

    // Cleanup
    return () => {
      window.removeEventListener('scroll', toggleVisibility);
    };
  }, []);

  // Smooth scroll to top
  const scrollToTop = () => {
    setIsScrolling(true);
    window.scrollTo({
      top: 0,
      behavior: 'smooth'
    });

    // Reset scrolling state after animation
    setTimeout(() => {
      setIsScrolling(false);
    }, 1000);
  };

  return (
    <>
      {isVisible && (
        <button
          onClick={scrollToTop}
          disabled={isScrolling}
          className={`
            fixed bottom-8 right-8 z-50
            w-12 h-12 
            bg-gradient-to-r from-blue-600 to-blue-700 
            hover:from-blue-700 hover:to-blue-800
            text-white rounded-full shadow-lg 
            hover:shadow-xl transform transition-all duration-300 
            ${isScrolling
              ? 'scale-95 opacity-75 cursor-not-allowed'
              : 'hover:scale-110 active:scale-95 cursor-pointer'
            }
            flex items-center justify-center
            border-2 border-white
            backdrop-blur-sm
          `}
          title="Scroll to top"
          aria-label="Scroll to top of page"
        >
          {isScrolling ? (
            // Loading spinner while scrolling
            <svg
              className="w-5 h-5 animate-spin"
              fill="none"
              viewBox="0 0 24 24"
            >
              <circle
                className="opacity-25"
                cx="12"
                cy="12"
                r="10"
                stroke="currentColor"
                strokeWidth="4"
              />
              <path
                className="opacity-75"
                fill="currentColor"
                d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"
              />
            </svg>
          ) : (
            // Up arrow icon
            <svg
              className="w-6 h-6"
              fill="none"
              stroke="currentColor"
              viewBox="0 0 24 24"
            >
              <path
                strokeLinecap="round"
                strokeLinejoin="round"
                strokeWidth={2.5}
                d="M5 15l7-7 7 7"
              />
            </svg>
          )}
        </button>
      )}

      {/* Optional: Scroll progress indicator */}
      {isVisible && (
        <div className="fixed bottom-[5.5rem] right-8 z-40">
          <div className="w-12 h-1 bg-gray-300 rounded-full overflow-hidden">
            <div
              className="h-full bg-gradient-to-r from-blue-600 to-blue-700 transition-all duration-300"
              style={{
                width: `${Math.min(
                  (window.pageYOffset / (document.documentElement.scrollHeight - window.innerHeight)) * 100,
                  100
                )}%`
              }}
            />
          </div>
        </div>
      )}
    </>
  );
};

const AppContent = () => {
  const { activeTab } = useAssessment();

  return (
    <div className="min-h-screen bg-gray-50 relative">
      <div className="bg-white shadow-sm">
        <div className="max-w-7xl mx-auto px-6 py-4">
          <div className="flex items-center justify-between">
            <div>
              <h1 className="text-2xl font-bold text-gray-900">Assessment Platform</h1>
              <p className="text-gray-600">Professional rubric creation, class management, and grading tools</p>
            </div>
            <div className="text-sm text-gray-500">
              Comprehensive Educational Assessment Suite v2.0
            </div>
          </div>
        </div>
      </div>

      <TabNavigation />

      <div className="tab-content">
        {activeTab === 'ai-prompt-generator' && <AIRubricPromptGenerator />}
        {activeTab === 'assignment-prompt-generator' && <AssignmentPromptGenerator />}
        {activeTab === 'rubric-creator' && <RubricCreator />}
        {activeTab === 'class-manager' && <ClassListManager />}
        {activeTab === 'grading-tool' && <GradingTemplate />}
        {activeTab === 'excel-import-test' && <ExcelImportTest />}
      </div>

      {/* Scroll to Top Button - Available on all pages */}
      <ScrollToTopButton />
    </div>
  );
};

const App = () => {
  return (
    <AssessmentProvider>
      <AppContent />
    </AssessmentProvider>
  );
};

export default App;
