import React, { useState, useRef } from 'react';
import { Upload, CheckCircle, AlertCircle } from 'lucide-react';
import { parseExcelFile, parseCSVFile } from '../utils/excelParser';

const ExcelImportTest = () => {
    const [students, setStudents] = useState([]);
    const [isLoading, setIsLoading] = useState(false);
    const [error, setError] = useState(null);
    const [metadata, setMetadata] = useState(null);
    const fileInputRef = useRef(null);

    const handleFileImport = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        setIsLoading(true);
        setError(null);
        setStudents([]);
        setMetadata(null);

        try {
            const fileName = file.name.toLowerCase();
            let result;

            if (fileName.endsWith('.xlsx') || fileName.endsWith('.xls')) {
                result = await parseExcelFile(file);
            } else if (fileName.endsWith('.csv')) {
                result = await parseCSVFile(file);
            } else {
                throw new Error('Please upload a .csv, .xls, or .xlsx file');
            }

            setStudents(result.students);
            setMetadata(result.metadata);

            if (result.students.length === 0) {
                setError('No valid student data found in the file');
            }
        } catch (err) {
            setError(err.message);
        } finally {
            setIsLoading(false);
        }

        event.target.value = '';
    };

    return (
        <div style={{ padding: '2rem', maxWidth: '800px', margin: '0 auto' }}>
            <h2 style={{ marginBottom: '1rem', color: '#1e40af' }}>
                ðŸ“Š Excel Import Test
            </h2>

            <div style={{
                border: '2px dashed #d1d5db',
                borderRadius: '0.5rem',
                padding: '2rem',
                textAlign: 'center',
                marginBottom: '1.5rem'
            }}>
                <input
                    ref={fileInputRef}
                    type="file"
                    accept=".csv,.xls,.xlsx"
                    onChange={handleFileImport}
                    style={{ display: 'none' }}
                />

                <button
                    onClick={() => fileInputRef.current?.click()}
                    disabled={isLoading}
                    style={{
                        display: 'flex',
                        alignItems: 'center',
                        gap: '0.5rem',
                        background: '#3b82f6',
                        color: 'white',
                        padding: '0.75rem 1.5rem',
                        borderRadius: '0.5rem',
                        border: 'none',
                        cursor: isLoading ? 'not-allowed' : 'pointer',
                        margin: '0 auto'
                    }}
                >
                    <Upload size={20} />
                    {isLoading ? 'Processing...' : 'Upload Excel/CSV File'}
                </button>

                <p style={{ color: '#6b7280', marginTop: '0.5rem', fontSize: '0.875rem' }}>
                    Supports .xls, .xlsx, and .csv files
                </p>
            </div>

            {error && (
                <div style={{
                    background: '#fef2f2',
                    border: '1px solid #fecaca',
                    borderRadius: '0.5rem',
                    padding: '1rem',
                    marginBottom: '1rem'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#dc2626' }}>
                        <AlertCircle size={16} />
                        <span style={{ fontWeight: '500' }}>Import Error</span>
                    </div>
                    <p style={{ color: '#7f1d1d', marginTop: '0.5rem', fontSize: '0.875rem' }}>
                        {error}
                    </p>
                </div>
            )}

            {metadata && (
                <div style={{
                    background: '#f0fdf4',
                    border: '1px solid #bbf7d0',
                    borderRadius: '0.5rem',
                    padding: '1rem',
                    marginBottom: '1rem'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#15803d' }}>
                        <CheckCircle size={16} />
                        <span style={{ fontWeight: '500' }}>Import Successful</span>
                    </div>
                    <div style={{ color: '#166534', marginTop: '0.5rem', fontSize: '0.875rem' }}>
                        <p>Parsed {metadata.parsed} students from {metadata.totalRows} rows</p>
                        {metadata.columns && (
                            <p>Detected columns: {metadata.columns.join(', ')}</p>
                        )}
                    </div>
                </div>
            )}

            {students.length > 0 && (
                <div style={{
                    background: 'white',
                    border: '1px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    overflow: 'hidden'
                }}>
                    <div style={{
                        background: '#f9fafb',
                        padding: '1rem',
                        borderBottom: '1px solid #e5e7eb'
                    }}>
                        <h3 style={{ margin: 0, color: '#374151' }}>
                            Imported Students ({students.length})
                        </h3>
                    </div>

                    <div style={{ maxHeight: '400px', overflowY: 'auto' }}>
                        <table style={{ width: '100%', borderCollapse: 'collapse' }}>
                            <thead>
                                <tr style={{ background: '#f3f4f6' }}>
                                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>ID</th>
                                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>Name</th>
                                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>Email</th>
                                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>Program</th>
                                    <th style={{ padding: '0.75rem', textAlign: 'left', borderBottom: '1px solid #e5e7eb' }}>Status</th>
                                </tr>
                            </thead>
                            <tbody>
                                {students.map((student, index) => (
                                    <tr key={student.id || index} style={{ borderBottom: '1px solid #e5e7eb' }}>
                                        <td style={{ padding: '0.75rem' }}>{student.id}</td>
                                        <td style={{ padding: '0.75rem', fontWeight: '500' }}>{student.name}</td>
                                        <td style={{ padding: '0.75rem' }}>{student.email}</td>
                                        <td style={{ padding: '0.75rem' }}>{student.program}</td>
                                        <td style={{ padding: '0.75rem' }}>
                                            <span style={{
                                                background: student.status === 'Enrolled' ? '#dcfce7' : '#f3f4f6',
                                                color: student.status === 'Enrolled' ? '#166534' : '#374151',
                                                padding: '0.25rem 0.5rem',
                                                borderRadius: '0.25rem',
                                                fontSize: '0.75rem'
                                            }}>
                                                {student.status}
                                            </span>
                                        </td>
                                    </tr>
                                ))}
                            </tbody>
                        </table>
                    </div>
                </div>
            )}
        </div>
    );
};

export default ExcelImportTest;
