      const updatedCLOs = [...formData.clos, newCLO];
      handleInputChange('clos', updatedCLOs);
    }
  };

  const removeCLO = (cloId) => {
    if (formData.clos.length > 1) {
      const updatedCLOs = formData.clos.filter(clo => clo.id !== cloId);
      handleInputChange('clos', updatedCLOs);
    }
  };

  const updateCLO = (cloId, text) => {
    const updatedCLOs = formData.clos.map(clo => 
      clo.id === cloId ? { ...clo, text } : clo
    );
    handleInputChange('clos', updatedCLOs);
  };

  // Use shared form data, fallback to defaults if not available
  const formData = assignmentPromptFormData || {
    assignmentTitle: '',
    assignmentNumber: '',
    weightPercentage: '',
    courseCode: '',
    programLevel: '',
    subjectArea: '',
    assignmentDescription: '',
    rationale: 'This assignment will evaluate the following Course Learning Outcomes:',
    clos: [
      { id: 1, text: '' },
      { id: 2, text: '' },
      { id: 3, text: '' },
      { id: 4, text: '' }
    ],
    directions: '',
    gradingMethod: 'rubric',
    gradingDetails: '',
    dueDate: '',
    submissionFolder: '',
    specialInstructions: ''
  };

  const generateAIPrompt = () => {
    // Generate CLO text for the prompt
    const closText = formData.clos
      .map((clo, index) => `- CLO${index + 1}: ${clo.text}`)
      .join('\n');

    const prompt = `Create HTML code for an assignment details page that matches EXACTLY the structure and format of the provided template. Use the following assignment information:

**Assignment Information:**
- Assignment Title: ${formData.assignmentTitle}
- Assignment Number: ${formData.assignmentNumber}
- Weight: ${formData.weightPercentage}%
- Course Code: ${formData.courseCode}
- Program Level: ${formData.programLevel}
- Subject Area: ${formData.subjectArea}
- Due Date: ${formData.dueDate || 'See your Instructional Plan for exact due dates.'}
- Submission Folder: ${formData.submissionFolder || 'Assignment X'}

**Assignment Content:**
- Description: ${formData.assignmentDescription}
- Rationale: ${formData.rationale}
- Course Learning Outcomes:
${closText}
- Directions: ${formData.directions}
- Grading Method: ${formData.gradingMethod}
- Grading Details: ${formData.gradingDetails}
- Special Instructions: ${formData.specialInstructions}

**CRITICAL REQUIREMENTS:**
1. Use EXACTLY this HTML structure and format:

\`\`\`html
<!DOCTYPE html>
<html lang="en">

<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0, shrink-to-fit=no">
    <title>Individual Assignment [NUMBER] ([WEIGHT]%)</title>
    <link type="text/css" rel="stylesheet" href="https://conestoga.desire2learn.com/content/enforced/1442597-Model-DSGN1140-100-IP-15/Course Files/module_00/../assets/css/main.css">
    <script type="text/javascript" src="https://conestoga.desire2learn.com/content/enforced/1442597-Model-DSGN1140-100-IP-15/Course Files/module_00/../assets/js/script.js"></script>
</head>

<body>
    <div class="container-fluid">
        <div class="row justify-content-center">
            <div class="col-sm-12 col-md-10">
                [CONTENT GOES HERE]
            </div>
        </div>
    </div>
</body>

</html>
\`\`\`

2. Replace all bracketed placeholders with the appropriate information provided above
3. Keep the exact CSS and JavaScript links unchanged
4. Follow the content structure: Due date → Submit to → Description → Rationale (with CLO list) → Directions → How Your Assignment Will be Graded
5. Use proper HTML formatting with &lt;strong&gt;, &lt;h2&gt;, &lt;h3&gt;, &lt;ul&gt;, &lt;li&gt; tags as shown in the template
6. For the CLO list, create a &lt;ul&gt; with &lt;li&gt; tags for each Course Learning Outcome provided
7. For grading section: if rubric is selected, use the rubric text; if not, use the custom grading details

Generate complete, ready-to-use HTML code that can be saved directly as an assignment page.`;

    setGeneratedPrompt(prompt);
    setShowPrompt(true);
    setShowHTML(false);
  };

  const generateAssignmentHTML = () => {
