// Complete GradingTemplate.js - Fully Consolidated with ALL Original Features + Late Policy Enhancement
// This version preserves EVERY function and feature from the original while adding late policy support

import React, { useState, useEffect, useCallback, useRef } from 'react';
import { useAssessment } from './SharedContext';
import {
  ChevronLeft, ChevronRight, Save, FileText, Download, Upload,
  Eye, EyeOff, Plus, Minus, Clock, AlertTriangle, CheckCircle,
  Settings, Edit3, Trash2, Copy, FileDown, FileUp, Calculator,
  X, RefreshCw, Info, Zap
} from 'lucide-react';

// Default rubric templates for various program types
const defaultRubricsByProgram = {
  degree: [
    {
      name: 'Critical Thinking & Analysis',
      description: 'Demonstrates ability to analyze information critically and draw logical conclusions.',
      maxPoints: 25,
      levels: {
        beginning: { description: 'Limited analysis with unclear reasoning and unsupported conclusions.', pointRange: '0-15' },
        developing: { description: 'Basic analysis with some logical reasoning but inconsistent support for conclusions.', pointRange: '16-19' },
        acceptable: { description: 'Good analysis with clear reasoning and adequate support for most conclusions.', pointRange: '20-22' },
        accomplished: { description: 'Strong analysis with logical reasoning and well-supported conclusions throughout.', pointRange: '23-24' },
        exceptional: { description: 'Outstanding analysis demonstrating sophisticated reasoning and compelling evidence.', pointRange: '25' }
      },
      feedbackLibrary: {
        strengths: [
          'Excellent mentorship integration',
          'Strong learning from feedback',
          'Good mentor relationship',
          'Effective knowledge application'
        ],
        improvements: [
          'Improve feedback integration',
          'Strengthen mentor relationship',
          'Enhance learning receptivity',
          'Apply feedback more effectively'
        ],
        general: [
          'Shows good mentorship response',
          'Learning from feedback is evident',
          'Mentor relationship is developing'
        ]
      },
    },
    {
      name: 'Research & Information Literacy',
      description: 'Effectively locates, evaluates, and uses information from credible sources.',
      maxPoints: 20,
      levels: {
        beginning: { description: 'Limited research with few or unreliable sources, poor information evaluation.', pointRange: '0-12' },
        developing: { description: 'Basic research skills with some credible sources, inconsistent evaluation.', pointRange: '13-15' },
        acceptable: { description: 'Good research skills with mostly credible sources and adequate evaluation.', pointRange: '16-17' },
        accomplished: { description: 'Strong research skills with credible, relevant sources and good evaluation.', pointRange: '18-19' },
        exceptional: { description: 'Excellent research demonstrating sophisticated information literacy skills.', pointRange: '20' }
      },
      feedbackLibrary: {
        strengths: [
          'Uses credible academic sources',
          'Demonstrates good research methodology',
          'Effective information evaluation',
          'Appropriate source integration'
        ],
        improvements: [
          'Expand range of source types',
          'Improve source evaluation criteria',
          'Strengthen research methodology',
          'Better integrate sources into argument'
        ],
        general: [
          'Shows developing research skills',
          'Good foundation in information literacy',
          'Research supports main arguments'
        ]
      },
    },
    {
      name: 'Communication & Presentation',
      description: 'Communicates ideas clearly and effectively in written and/or oral formats.',
      maxPoints: 25,
      levels: {
        beginning: { description: 'Poor communication with unclear ideas, significant errors, and weak organization.', pointRange: '0-15' },
        developing: { description: 'Basic communication with some clarity, minor errors, and adequate organization.', pointRange: '16-19' },
        acceptable: { description: 'Good communication with clear ideas, few errors, and good organization.', pointRange: '20-22' },
        accomplished: { description: 'Strong communication with very clear ideas, minimal errors, excellent organization.', pointRange: '23-24' },
        exceptional: { description: 'Outstanding communication demonstrating sophisticated expression and presentation.', pointRange: '25' }
      },
      feedbackLibrary: {
        strengths: [
          'Clear and effective communication',
          'Well-organized presentation of ideas',
          'Good use of language and terminology',
          'Engaging delivery style'
        ],
        improvements: [
          'Improve clarity of expression',
          'Strengthen organizational structure',
          'Reduce grammatical errors',
          'Enhance presentation skills'
        ],
        general: [
          'Shows good communication skills',
          'Ideas are generally well-presented',
          'Communication supports content effectively'
        ]
      },
    },
    {
      name: 'Professional Standards & Quality',
      description: 'Demonstrates professional standards in work quality, presentation, and attention to detail.',
      maxPoints: 30,
      levels: {
        beginning: { description: 'Work lacks professional standards with poor attention to detail and presentation.', pointRange: '0-18' },
        developing: { description: 'Work shows some professional standards with inconsistent attention to detail.', pointRange: '19-23' },
        acceptable: { description: 'Work meets professional standards with good attention to detail and presentation.', pointRange: '24-26' },
        accomplished: { description: 'Work exceeds professional standards with excellent attention to detail.', pointRange: '27-29' },
        exceptional: { description: 'Work demonstrates exceptional professional standards and meticulous attention to detail.', pointRange: '30' }
      },
      feedbackLibrary: {
        strengths: [
          'Demonstrates professional quality work',
          'Excellent attention to detail',
          'High standard of presentation',
          'Shows professional pride in work'
        ],
        improvements: [
          'Improve attention to detail',
          'Enhance professional presentation',
          'Meet higher quality standards',
          'Develop greater precision in work'
        ],
        general: [
          'Shows developing professional standards',
          'Work quality is generally good',
          'Professional approach is evident'
        ]
      }
    },
  ],
  diploma: [
    {
      name: 'Technical Knowledge & Application',
      description: 'Demonstrates understanding and practical application of technical concepts.',
      maxPoints: 30,
      levels: {
        beginning: { description: 'Limited technical knowledge with poor application of concepts.', pointRange: '0-18' },
        developing: { description: 'Basic technical knowledge with some successful application.', pointRange: '19-23' },
