// src/components/SharedContext.js
import React, { createContext, useContext, useState, useCallback } from 'react';

const AssessmentContext = createContext();

export const useAssessment = () => {
    const context = useContext(AssessmentContext);
    if (!context) {
        throw new Error('useAssessment must be used within an AssessmentProvider');
    }
    return context;
};

export const AssessmentProvider = ({ children }) => {
    // Core state
    const [activeTab, setActiveTab] = useState('ai-prompt-generator');
    const [sharedRubric, setSharedRubric] = useState(null);
    const [sharedCourseDetails, setSharedCourseDetails] = useState(null);
    const [gradingData, setGradingData] = useState({
        student: { name: '', id: '', email: '' },
        course: { code: '', name: '', instructor: '', term: '' },
        assignment: { name: '', dueDate: '', maxPoints: 100 },
        feedback: { general: '', strengths: '', improvements: '' },
        attachments: [],
        videoLinks: [],
        latePolicy: { level: 'none', penaltyApplied: false },
        rubricGrading: {},
        metadata: {
            gradedBy: '',
            gradedDate: '',
            aiAssisted: false,
            rubricIntegrated: false
        }
    });

    // Draft and Final Grade Management - ENHANCED
    const [drafts, setDrafts] = useState({});
    const [finalGrades, setFinalGrades] = useState({});

    // Class management state
    const [classList, setClassList] = useState(null);
    const [currentStudent, setCurrentStudent] = useState(null);

    // Grading session state
    const [gradingSession, setGradingSession] = useState({
        active: false,
        startTime: null,
        gradedStudents: [],
        totalStudents: 0,
        currentStudent: null,
        currentStudentIndex: 0
    });

    // Rubric form data state
    const [rubricFormData, setRubricFormData] = useState({
        assignmentInfo: {
            title: '',
            description: '',
            weight: 25,
            passingThreshold: 60,
            totalPoints: 100
        },
        rubricLevels: [
            { level: 'incomplete', name: 'Incomplete', description: 'No submission or unusable', color: '#95a5a6', multiplier: 0 },
            { level: 'unacceptable', name: 'Unacceptable', description: 'Below minimum standards', color: '#e74c3c', multiplier: 0.3 },
            { level: 'developing', name: 'Developing', description: 'Approaching standards', color: '#f39c12', multiplier: 0.55 },
            { level: 'acceptable', name: 'Acceptable (PASS)', description: 'Meets minimum standards', color: '#27ae60', multiplier: 0.7 },
            { level: 'emerging', name: 'Emerging', description: 'Above standard expectations', color: '#2980b9', multiplier: 0.82 },
            { level: 'accomplished', name: 'Accomplished', description: 'Strong professional quality', color: '#16a085', multiplier: 0.92 },
            { level: 'exceptional', name: 'Exceptional', description: 'Outstanding professional quality', color: '#8e44ad', multiplier: 1.0 }
        ],
        criteria: []
    });

    // Legacy compatibility state
    const [persistentFormData, setPersistentFormData] = useState({});

    // Draft and Final Grade Management Functions
    const saveDraft = useCallback((studentId, data) => {
        setDrafts(prev => ({
            ...prev,
            [studentId]: {
                ...data,
                savedAt: new Date().toISOString(),
                type: 'draft'
            }
        }));
        console.log(`ðŸ’¾ Draft saved for student ${studentId}`);
    }, []);

    const loadDraft = useCallback((studentId) => {
        return drafts[studentId] || null;
    }, [drafts]);

    const saveFinalGrade = useCallback((studentId, data) => {
        setFinalGrades(prev => ({
            ...prev,
            [studentId]: {
                ...data,
                savedAt: new Date().toISOString(),
                finalizedAt: new Date().toISOString(),
                type: 'final'
            }
        }));

        // Remove from drafts when finalized
        setDrafts(prev => {
            const newDrafts = { ...prev };
            delete newDrafts[studentId];
            return newDrafts;
        });

        console.log(`âœ… Final grade saved for student ${studentId}`);
    }, []);

    const loadFinalGrade = useCallback((studentId) => {
        return finalGrades[studentId] || null;
    }, [finalGrades]);

    const getGradeStatus = useCallback((studentId) => {
        if (finalGrades[studentId]) return 'final';
        if (drafts[studentId]) return 'draft';
        return 'not_started';
    }, [finalGrades, drafts]);

    const hasDraft = useCallback((studentId) => {
        return !!drafts[studentId];
    }, [drafts]);

    const finalizeGrade = useCallback((studentId) => {
        const draft = drafts[studentId];
        if (draft) {
            saveFinalGrade(studentId, draft);
            return true;
        }
        return false;
    }, [drafts, saveFinalGrade]);

    const unlockGrade = useCallback((studentId) => {
        const finalGrade = finalGrades[studentId];
        if (finalGrade) {
            // Move final grade back to draft
            setDrafts(prev => ({
                ...prev,
                [studentId]: {
                    ...finalGrade,
                    savedAt: new Date().toISOString(),
                    type: 'draft'
                }
            }));

            // Remove from final grades
            setFinalGrades(prev => {
                const newFinalGrades = { ...prev };
                delete newFinalGrades[studentId];
                return newFinalGrades;
            });

            console.log(`ðŸ”“ Grade unlocked for student ${studentId}`);
            return true;
        }
        return false;
    }, [finalGrades]);

    // Grading data update functions
    const updateStudentInfo = useCallback((studentInfo) => {
        setGradingData(prev => ({
            ...prev,
            student: { ...prev.student, ...studentInfo }
        }));
    }, []);

    const updateCourseInfo = useCallback((courseInfo) => {
        setGradingData(prev => ({
            ...prev,
            course: { ...prev.course, ...courseInfo }
        }));
    }, []);

    const updateAssignmentInfo = useCallback((assignmentInfo) => {
        setGradingData(prev => ({
            ...prev,
            assignment: { ...prev.assignment, ...assignmentInfo }
        }));
    }, []);

    const updateFeedbackInfo = useCallback((feedbackInfo) => {
        setGradingData(prev => ({
            ...prev,
            feedback: { ...prev.feedback, ...feedbackInfo }
        }));
    }, []);

    const updateAttachments = useCallback((attachments) => {
        setGradingData(prev => ({
            ...prev,
            attachments
        }));
    }, []);

    const updateVideoLinks = useCallback((videoLinks) => {
        setGradingData(prev => ({
            ...prev,
            videoLinks
        }));
    }, []);

    const updateLatePolicyInfo = useCallback((latePolicyInfo) => {
        setGradingData(prev => ({
            ...prev,
            latePolicy: { ...prev.latePolicy, ...latePolicyInfo }
        }));
    }, []);

    const updateRubricGrading = useCallback((rubricGrading) => {
        setGradingData(prev => ({
            ...prev,
            rubricGrading: { ...prev.rubricGrading, ...rubricGrading }
        }));
    }, []);

    const updateMetadata = useCallback((metadata) => {
        setGradingData(prev => ({
            ...prev,
            metadata: { ...prev.metadata, ...metadata }
        }));
    }, []);

    // Navigation helpers for grading sessions
    const nextStudentInSession = useCallback((saveType = 'draft') => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        const students = classList.students;

        if (currentIndex < students.length - 1) {
            const nextIndex = currentIndex + 1;
            const nextStudent = students[nextIndex];

            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: nextIndex,
                currentStudent: nextStudent,
                gradedStudents: [...prev.gradedStudents, currentStudent.id]
            }));

            setCurrentStudent(nextStudent);
            return true;
        }

        // End of session
        setGradingSession(prev => ({
            ...prev,
            active: false,
            gradedStudents: [...prev.gradedStudents, currentStudent.id]
        }));
        return false;
    }, [gradingSession, classList, currentStudent]);

    const previousStudentInSession = useCallback(() => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        if (currentIndex > 0) {
            const prevIndex = currentIndex - 1;
            const prevStudent = classList.students[prevIndex];

            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: prevIndex,
                currentStudent: prevStudent
            }));

            setCurrentStudent(prevStudent);
            return true;
        }
        return false;
    }, [gradingSession, classList]);

    // **FIXED: Enhanced initializeGradingSession function**
    const initializeGradingSession = useCallback((classListData) => {
        // 1. Guard against missing or empty class list
        if (!classListData || !classListData.students?.length) {
            console.warn('âš ï¸ Cannot initialize grading session: No students found');
            return false;
        }

        // 2. Extract the students array and the imported course metadata
        const { students, courseMetadata } = classListData;

        // 3. Pick the first student to start the session
        const firstStudent = students[0];

        // 4. Build and set the grading session state
        const session = {
            active: true,
            startTime: new Date().toISOString(),
            gradedStudents: [],
            totalStudents: students.length,
            currentStudent: firstStudent,
            currentStudentIndex: 0
        };
        setGradingSession(session);
        setCurrentStudent(firstStudent);

        // 5. Prefill the grading form: student + course details
        setGradingData(prev => ({
            ...prev,
            student: {
                name: firstStudent.name,
                id: firstStudent.id,
                email: firstStudent.email
            },
            course: {
                code: courseMetadata?.courseCode ?? prev.course.code,
                name: courseMetadata?.courseName ?? prev.course.name,
                instructor: courseMetadata?.instructor ?? prev.course.instructor,
                term: courseMetadata?.term ?? prev.course.term,
            },
            assignment: sharedCourseDetails?.assignment ?? prev.assignment
        }));

        console.log(`ðŸš€ Grading session started for ${students.length} students`);
        return true;
    }, [sharedCourseDetails]);

    // Update grading session
    const updateGradingSession = useCallback((updates) => {
        setGradingSession(prev => ({
            ...prev,
            ...updates
        }));
    }, []);

    // Rubric management functions
    const transferRubricToGrading = useCallback(() => {
        if (rubricFormData) {
            setSharedRubric(rubricFormData);
            setActiveTab('grading-tool');
        }
    }, [rubricFormData]);

    const transferRubricToGradingWithDetails = useCallback((details) => {
        if (rubricFormData) {
            setSharedRubric(rubricFormData);
            setSharedCourseDetails(details);
            setActiveTab('grading-tool');
        }
    }, [rubricFormData]);

    // Clear functions
    const clearSharedRubric = useCallback(() => {
        setSharedRubric(null);
    }, []);

    const clearGradingFormData = useCallback(() => {
        setGradingData({
            student: { name: '', id: '', email: '' },
            course: { code: '', name: '', instructor: '', term: '' },
            assignment: { name: '', dueDate: '', maxPoints: 100 },
            feedback: { general: '', strengths: '', improvements: '' },
            attachments: [],
            videoLinks: [],
            latePolicy: { level: 'none', penaltyApplied: false },
            rubricGrading: {},
            metadata: {
                gradedBy: '',
                gradedDate: '',
                aiAssisted: false,
                rubricIntegrated: false
            }
        });
    }, []);

    const clearRubricFormData = useCallback(() => {
        setRubricFormData({
            assignmentInfo: {
                title: '',
                description: '',
                weight: 25,
                passingThreshold: 60,
                totalPoints: 100
            },
            rubricLevels: [
                { level: 'incomplete', name: 'Incomplete', description: 'No submission or unusable', color: '#95a5a6', multiplier: 0 },
                { level: 'unacceptable', name: 'Unacceptable', description: 'Below minimum standards', color: '#e74c3c', multiplier: 0.3 },
                { level: 'developing', name: 'Developing', description: 'Approaching standards', color: '#f39c12', multiplier: 0.55 },
                { level: 'acceptable', name: 'Acceptable (PASS)', description: 'Meets minimum standards', color: '#27ae60', multiplier: 0.7 },
                { level: 'emerging', name: 'Emerging', description: 'Above standard expectations', color: '#2980b9', multiplier: 0.82 },
                { level: 'accomplished', name: 'Accomplished', description: 'Strong professional quality', color: '#16a085', multiplier: 0.92 },
                { level: 'exceptional', name: 'Exceptional', description: 'Outstanding professional quality', color: '#8e44ad', multiplier: 1.0 }
            ],
            criteria: []
        });
    }, []);

    const clearAllData = useCallback(() => {
        clearGradingFormData();
        clearRubricFormData();
        clearSharedRubric();
        setSharedCourseDetails(null);
        setCurrentStudent(null);
        setGradingSession({
            active: false,
            startTime: null,
            gradedStudents: [],
            totalStudents: 0,
            currentStudent: null,
            currentStudentIndex: 0
        });
    }, [clearGradingFormData, clearRubricFormData, clearSharedRubric]);

    // Session management
    const exportSession = useCallback(() => {
        const sessionData = {
            activeTab,
            sharedRubric,
            sharedCourseDetails,
            gradingData,
            classList,
            currentStudent,
            gradingSession,
            rubricFormData,
            drafts,
            finalGrades,
            exportedAt: new Date().toISOString()
        };
        return sessionData;
    }, [activeTab, sharedRubric, sharedCourseDetails, gradingData, classList, currentStudent, gradingSession, rubricFormData, drafts, finalGrades]);

    const importSession = useCallback((sessionData) => {
        try {
            if (sessionData.activeTab) setActiveTab(sessionData.activeTab);
            if (sessionData.sharedRubric) setSharedRubric(sessionData.sharedRubric);
            if (sessionData.sharedCourseDetails) setSharedCourseDetails(sessionData.sharedCourseDetails);
            if (sessionData.gradingData) setGradingData(sessionData.gradingData);
            if (sessionData.classList) setClassList(sessionData.classList);
            if (sessionData.currentStudent) setCurrentStudent(sessionData.currentStudent);
            if (sessionData.gradingSession) setGradingSession(sessionData.gradingSession);
            if (sessionData.rubricFormData) setRubricFormData(sessionData.rubricFormData);
            if (sessionData.drafts) setDrafts(sessionData.drafts);
            if (sessionData.finalGrades) setFinalGrades(sessionData.finalGrades);
            return true;
        } catch (error) {
            console.error('Failed to import session:', error);
            return false;
        }
    }, []);

    // Legacy compatibility
    const updatePersistentFormData = useCallback((data) => {
        setPersistentFormData(prev => ({ ...prev, ...data }));
    }, []);

    // **FIXED: Complete value object with all functions**
    const value = {
        // Core state
        activeTab,
        setActiveTab,
        sharedRubric,
        setSharedRubric,
        sharedCourseDetails,
        setSharedCourseDetails,
        gradingData,
        setGradingData,

        // Grading data update functions
        updateStudentInfo,
        updateCourseInfo,
        updateAssignmentInfo,
        updateFeedbackInfo,
        updateAttachments,
        updateVideoLinks,
        updateLatePolicyInfo,
        updateRubricGrading,
        updateMetadata,

        // Draft and Final Grade Management - ENHANCED
        drafts,
        finalGrades,
        saveDraft,
        loadDraft,
        saveFinalGrade,
        loadFinalGrade,
        getGradeStatus,
        hasDraft,
        finalizeGrade,
        unlockGrade,

        // Class management
        classList,
        setClassList,
        currentStudent,
        setCurrentStudent,

        // Grading session
        gradingSession,
        setGradingSession,
        nextStudentInSession,
        previousStudentInSession,
        updateGradingSession,
        initializeGradingSession,

        // Rubric form data
        rubricFormData,
        setRubricFormData,

        // Utility functions
        transferRubricToGrading,
        transferRubricToGradingWithDetails,
        clearSharedRubric,
        clearGradingFormData,
        clearRubricFormData,
        clearAllData,

        // Session management
        exportSession,
        importSession,

        // Legacy compatibility
        persistentFormData,
        updatePersistentFormData
    };

    return (
        <AssessmentContext.Provider value={value}>
            {children}
        </AssessmentContext.Provider>
    );
};
