          videoLinks: [],
          latePolicy: { level: 'none', penaltyApplied: false },
          metadata: {
            gradedBy: '',
            gradedDate: '',
            aiAssisted: false,
            rubricIntegrated: false
          },
          rubricGrading: {}
        });
      }
    }
  };

  // SAVING HANDLERS
  const handleSaveDraft = () => {
    if (!currentStudent?.id) {
      alert('No student selected for saving draft');
