import React, { useState, useRef, useEffect } from 'react';
import { Plus, X, Upload, Download, Save, FileText, RotateCcw, ChevronDown, ChevronUp, Maximize2, ArrowRight, Minimize2, Edit3 } from 'lucide-react';
import { useAssessment } from './SharedContext';

/**
 * ===================================================================================
 * MARKDOWN PASTE UTILITY
 * ===================================================================================
 * This function converts plain text (with markdown-like lists) into clean HTML.
 * It's used as a fallback when the clipboard doesn't contain rich HTML content.
 *
 * @param {string} text - The plain text from the clipboard.
 * @returns {string} - A clean HTML string.
 */
const pasteAsMarkdown = (text) => {
    if (!text) return '';

    const lines = text.split('\n');
    let html = '';
    let inList = false;
    let listType = null; // 'ol' or 'ul'

    const closeList = () => {
        if (inList) {
            html += `</${listType}>`;
            inList = false;
            listType = null;
        }
    };

    lines.forEach(line => {
        const trimmedLine = line.trim();
        const isNumberedList = /^\d+\.\s/.test(trimmedLine);
        const isBulletedList = /^(\*|-)\s/.test(trimmedLine);

        if (isNumberedList || isBulletedList) {
            const currentListType = isNumberedList ? 'ol' : 'ul';
            if (!inList || currentListType !== listType) {
                closeList();
                html += `<${currentListType}>`;
                inList = true;
                listType = currentListType;
            }
            html += `<li>${trimmedLine.replace(/^(\d+\.|\*|-)\s/, '')}</li>`;
        } else {
            closeList();
            if (trimmedLine) {
                html += `<p>${trimmedLine}</p>`;
            }
        }
    });

    closeList();
    return html;
};


const RubricCreator = () => {
    const {
        sharedRubric,
        setSharedRubric,
        transferRubricToGradingWithDetails,
        activeTab
    } = useAssessment();

    const defaultLevels = [
        { level: 'incomplete', name: 'Incomplete', description: 'No submission or unusable', color: '#95a5a6', multiplier: 0 },
        { level: 'unacceptable', name: 'Unacceptable', description: 'Below minimum standards', color: '#e74c3c', multiplier: 0.3 },
        { level: 'developing', name: 'Developing', description: 'Approaching standards', color: '#f39c12', multiplier: 0.55 },
        { level: 'acceptable', name: 'Acceptable (PASS)', description: 'Meets minimum standards', color: '#27ae60', multiplier: 0.7 },
        { level: 'emerging', name: 'Emerging', description: 'Above standard expectations', color: '#2980b9', multiplier: 0.82 },
        { level: 'accomplished', name: 'Accomplished', description: 'Strong professional quality', color: '#16a085', multiplier: 0.92 },
        { level: 'exceptional', name: 'Exceptional', description: 'Outstanding professional quality', color: '#8e44ad', multiplier: 1.0 }
    ];

    const [rubricData, setRubricData] = useState(() => {
        return sharedRubric || {
            assignmentInfo: { title: '', description: '', weight: 25, passingThreshold: 60, totalPoints: 100 },
            rubricLevels: [...defaultLevels],
            criteria: [{ id: 'criterion-1', name: '', description: '', maxPoints: 20, weight: 20, levels: {}, feedbackLibrary: { strengths: [], improvements: [], general: [] } }]
        };
    });

    const [pointingSystem, setPointingSystem] = useState('multiplier');
    const [reversedOrder, setReversedOrder] = useState(false);
    const [showGuide, setShowGuide] = useState(false);
    const [expandedFeedback, setExpandedFeedback] = useState({});
    const [inlineEditor, setInlineEditor] = useState({ show: false, content: '', field: null, onSave: null, criterionId: null, level: null, type: null });
    const [autoSaveTimeout, setAutoSaveTimeout] = useState(null);
    const richTextContentRef = useRef('');
    const editorRef = useRef(null);
    const importInputRef = useRef(null);

    const containsHtml = (content) => /<[^>]*>/.test(content);
    const stripHtml = (html) => {
        const tempDiv = document.createElement('div');
        tempDiv.innerHTML = html;
        return tempDiv.textContent || tempDiv.innerText || '';
    };

    /**
     * ===================================================================================
     * NEW, ROBUST HTML SANITIZER
     * ===================================================================================
     * This function is adapted from the `editor.js` logic. It safely cleans
     * pasted HTML by parsing it and allowing only a specific list of tags and attributes.
     * This preserves rich text formatting while removing unwanted styles and scripts.
     *
     * @param {string} html - The potentially dirty HTML string from the clipboard.
     * @returns {string} - Sanitized HTML ready for insertion.
     */
    const sanitizeHtml = (html) => {
        // Define tags and attributes allowed from pasted content, matching the Rich Text Editor's capabilities.
        const ALLOWED_TAGS = [
            'h1', 'h2', 'h3', 'p', 'blockquote', 'pre', 'b', 'strong', 'i', 'em', 'u', 'strike', 's',
            'ul', 'ol', 'li', 'a', 'br', 'hr', 'sup', 'sub', 'div', 'span', 'font'
        ];
        const ALLOWED_ATTRS = ['href', 'title', 'style', 'color', 'size', 'face'];

        const doc = new DOMParser().parseFromString(html, 'text/html');
        const frag = document.createDocumentFragment();

        function cleanNode(node) {
            if (node.nodeType === Node.TEXT_NODE) {
                // Keep text nodes
                return document.createTextNode(node.textContent);
            }

            if (node.nodeType === Node.ELEMENT_NODE) {
                const tag = node.tagName.toLowerCase();

                if (ALLOWED_TAGS.includes(tag)) {
                    const el = document.createElement(tag);

                    // Copy allowed attributes from the original node to the new element
                    for (const attr of node.attributes) {
                        if (ALLOWED_ATTRS.includes(attr.name.toLowerCase())) {
                            el.setAttribute(attr.name, attr.value);
                        }
                    }

                    // Recursively clean and append child nodes
                    node.childNodes.forEach(child => {
                        const cleanedChild = cleanNode(child);
                        if (cleanedChild) el.appendChild(cleanedChild);
                    });
                    return el;

                } else {
                    // If the tag is not allowed, flatten it by processing its children directly
                    const container = document.createDocumentFragment();
                    node.childNodes.forEach(child => {
                        const cleanedChild = cleanNode(child);
                        if (cleanedChild) container.appendChild(cleanedChild);
                    });
                    return container;
                }
            }
            return null; // Ignore other node types (comments, etc.)
        }

        Array.from(doc.body.childNodes).forEach(n => {
            const cleaned = cleanNode(n);
            if (cleaned) frag.appendChild(cleaned);
        });

        const wrapper = document.createElement('div');
        wrapper.appendChild(frag);
        return wrapper.innerHTML;
    };

    const saveCursorPosition = () => {
        const selection = window.getSelection();
        if (selection.rangeCount === 0) return null;
        return selection.getRangeAt(0);
    };

    const restoreCursorPosition = (range) => {
        if (!range) return;
        const selection = window.getSelection();
        selection.removeAllRanges();
        selection.addRange(range);
    };

    useEffect(() => {
        if (autoSaveTimeout) clearTimeout(autoSaveTimeout);
        const id = setTimeout(() => setSharedRubric(rubricData), 1000);
        setAutoSaveTimeout(id);
        return () => clearTimeout(id);
    }, [rubricData, setSharedRubric]);

    useEffect(() => {
        if (sharedRubric && activeTab === 'rubric-creator') {
            setRubricData(sharedRubric);
        }
    }, [sharedRubric, activeTab]);

    const toggleFeedbackExpansion = (id) => setExpandedFeedback(p => ({ ...p, [id]: !p[id] }));

    const openInlineEditor = (content, field, onSave, criterionId = null, level = null, type = 'assignment') => {
        richTextContentRef.current = content || '';
        setInlineEditor({ show: true, content: content || '', field, onSave, criterionId, level, type });
        setTimeout(() => {
            if (editorRef.current) {
                editorRef.current.innerHTML = content || '';
                editorRef.current.focus();
                const range = document.createRange();
                const selection = window.getSelection();
                range.selectNodeContents(editorRef.current);
                range.collapse(false);
                selection.removeAllRanges();
                selection.addRange(range);
            }
        }, 100);
    };

    const handleRichTextCommand = (command, value = null) => {
        if (editorRef.current) {
            editorRef.current.focus();
            document.execCommand(command, false, value);
            richTextContentRef.current = editorRef.current.innerHTML;
        }
    };

    const handleRichTextChange = () => {
        if (editorRef.current) richTextContentRef.current = editorRef.current.innerHTML;
    };

    /**
     * ===================================================================================
     * NEW, IMPROVED PASTE HANDLER
     * ===================================================================================
     * This function now correctly handles pasted content by prioritizing HTML and
     * cleaning it with the robust `sanitizeHtml` function.
     */
    const handlePaste = (e) => {
        e.preventDefault();
        const clipboardData = e.clipboardData || window.clipboardData;
        let pasteData = '';

        // Prioritize HTML content if available, as it contains rich formatting.
        if (clipboardData.types.includes('text/html')) {
            const dirtyHtml = clipboardData.getData('text/html');
            // Use the new robust sanitize function to clean the HTML.
            pasteData = sanitizeHtml(dirtyHtml);
        } else {
            // Fallback for plain text: use the markdown converter for lists.
            const plainText = clipboardData.getData('text/plain');
            pasteData = pasteAsMarkdown(plainText);
        }

        // Insert the final cleaned HTML into the editor.
        document.execCommand('insertHTML', false, pasteData);
    };

    const handleKeyDown = (e) => {
        if (!editorRef.current) return;
        if (e.key === 'Tab') {
            e.preventDefault();
            document.execCommand('insertHTML', false, '&nbsp;&nbsp;&nbsp;&nbsp;');
        }
        if (e.ctrlKey || e.metaKey) {
            switch (e.key) {
                case 'b': e.preventDefault(); handleRichTextCommand('bold'); break;
                case 'i': e.preventDefault(); handleRichTextCommand('italic'); break;
                case 'u': e.preventDefault(); handleRichTextCommand('underline'); break;
                default: break;
            }
        }
        setTimeout(() => handleRichTextChange(), 0);
    };

    const closeInlineEditor = (save = false) => {
        if (save && inlineEditor.onSave) {
            // The ref has the most up-to-date HTML content.
            inlineEditor.onSave(richTextContentRef.current);
        }
        setInlineEditor({ show: false, content: '', field: null, onSave: null, criterionId: null, level: null, type: null });
        richTextContentRef.current = '';
    };

    const calculateTotalPoints = () => rubricData.criteria.reduce((total, c) => total + c.maxPoints, 0);

    const updateAssignmentInfo = (field, value) => {
        setRubricData(prev => {
            const updated = { ...prev, assignmentInfo: { ...prev.assignmentInfo, [field]: value } };
            if (field !== 'totalPoints') updated.assignmentInfo.totalPoints = calculateTotalPoints();
            return updated;
        });
    };

    const addCriterion = () => {
        const newCriterion = { id: `criterion-${Date.now()}`, name: '', description: '', maxPoints: 20, weight: 20, levels: {}, feedbackLibrary: { strengths: [], improvements: [], general: [] } };
        setRubricData(prev => ({ ...prev, criteria: [...prev.criteria, newCriterion] }));
    };

    const removeCriterion = (id) => setRubricData(prev => ({ ...prev, criteria: prev.criteria.filter(c => c.id !== id) }));
    const updateCriterion = (id, field, value) => setRubricData(prev => ({ ...prev, criteria: prev.criteria.map(c => c.id === id ? { ...c, [field]: value } : c) }));
    const updateCriterionLevel = (id, level, field, value) => {
        setRubricData(prev => ({
            ...prev,
            criteria: prev.criteria.map(c => c.id === id ? { ...c, levels: { ...c.levels, [level]: { ...c.levels[level], [field]: value } } } : c)
        }));
    };

    const addFeedbackItem = (id, cat, item) => {
        if (!item?.trim()) return;
        setRubricData(prev => ({
            ...prev,
            criteria: prev.criteria.map(c => c.id === id ? { ...c, feedbackLibrary: { ...c.feedbackLibrary, [cat]: [...c.feedbackLibrary[cat], item.trim()] } } : c)
        }));
    };

    const removeFeedbackItem = (id, cat, index) => {
        setRubricData(prev => ({
            ...prev,
            criteria: prev.criteria.map(c => c.id === id ? { ...c, feedbackLibrary: { ...c.feedbackLibrary, [cat]: c.feedbackLibrary[cat].filter((_, i) => i !== index) } } : c)
        }));
    };

    const calculatePointRange = (criterion, level) => {
        const rubricLevel = rubricData.rubricLevels.find(l => l.level === level);
        if (!rubricLevel) return '0';
        return pointingSystem === 'multiplier' ? (level === 'incomplete' ? '0' : Math.round(criterion.maxPoints * rubricLevel.multiplier).toString()) : (criterion.levels[level]?.pointRange || '0');
    };

    const getDisplayLevels = () => reversedOrder ? [...rubricData.rubricLevels].reverse() : rubricData.rubricLevels;

    const prepareRubricForExport = () => {
        const exportData = { ...rubricData, assignmentInfo: { ...rubricData.assignmentInfo, totalPoints: calculateTotalPoints() } };
        exportData.criteria = exportData.criteria.map(c => ({ ...c, levels: Object.fromEntries(rubricData.rubricLevels.map(l => [l.level, { pointRange: calculatePointRange(c, l.level), description: c.levels[l.level]?.description || '' }])) }));
        return exportData;
    };

    const downloadJson = (data, fileName) => {
        const blob = new Blob([JSON.stringify(data, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = fileName;
        link.click();
        URL.revokeObjectURL(url);
    };

    const saveRubric = () => downloadJson(rubricData, `rubric_${rubricData.assignmentInfo.title.replace(/\s+/g, '_') || 'draft'}_${new Date().toISOString().split('T')[0]}.json`);
    const exportForGrading = () => downloadJson(prepareRubricForExport(), `rubric_${rubricData.assignmentInfo.title.replace(/\s+/g, '_') || 'export'}_final.json`);
    const transferToGrading = () => transferRubricToGradingWithDetails(prepareRubricForExport());

    const importRubric = (e) => {
        const file = e.target.files[0];
        if (file) {
            const reader = new FileReader();
            reader.onload = (ev) => {
                try {
                    const imported = JSON.parse(ev.target.result);
                    if (imported.assignmentInfo && imported.criteria && imported.rubricLevels) setRubricData(imported);
                    else alert('Invalid rubric format.');
                } catch (err) {
                    alert('Error loading rubric: ' + err.message);
                }
            };
            reader.readAsText(file);
        }
        e.target.value = '';
    };

    const resetRubric = () => {
        if (window.confirm('Reset to a blank rubric? All current work will be lost.')) {
            setRubricData({
                assignmentInfo: { title: '', description: '', weight: 25, passingThreshold: 60, totalPoints: 100 },
                rubricLevels: [...defaultLevels],
                criteria: [{ id: 'criterion-1', name: '', description: '', maxPoints: 20, weight: 20, levels: {}, feedbackLibrary: { strengths: [], improvements: [], general: [] } }]
            });
        }
    };

    // ... The rest of the component's render logic, including JSX, remains the same.
    // The ContentDisplay, RichTextToolbar, and all table rendering logic are unchanged.
    // I am omitting the large JSX block for brevity as no changes were made to it.
    // The key changes are the new `sanitizeHtml` function and the updated `handlePaste` function shown above.

    const exportToHTML = () => {
        const displayLevels = getDisplayLevels();
        const totalPoints = calculateTotalPoints();

        const htmlContent = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>${rubricData.assignmentInfo.title || 'Assessment Rubric'} - Rubric</title>
    <style>
        body { font-family: 'Arial', sans-serif; max-width: 1200px; margin: 0 auto; padding: 20px; line-height: 1.4; }
        .header { text-align: center; margin-bottom: 30px; padding-bottom: 20px; border-bottom: 3px solid #1e40af; }
        .header h1 { color: #1e40af; margin-bottom: 10px; }
        .assignment-info { background: #f8fafc; padding: 20px; border-radius: 8px; margin-bottom: 25px; border: 1px solid #e2e8f0; }
        .rubric-table { width: 100%; border-collapse: collapse; margin-bottom: 30px; font-size: 0.85rem; box-shadow: 0 4px 6px rgba(0,0,0,0.1); }
        .rubric-table th, .rubric-table td { border: 1px solid #ddd; padding: 12px; text-align: left; vertical-align: top; }
        .rubric-table th { background: #1e40af; color: white; text-align: center; font-weight: 600; }
        .criterion-name { font-weight: bold; background: #f8fafc; padding: 15px; }
        .point-range { text-align: center; font-weight: bold; color: #1e40af; font-size: 1.1em; }
        .level-description { font-size: 0.9rem; line-height: 1.4; }
        .overall-score { background: #eff6ff; padding: 25px; border-radius: 8px; margin-top: 30px; border: 2px solid #bfdbfe; }
        .footer { margin-top: 40px; text-align: center; font-size: 0.9rem; color: #666; border-top: 1px solid #e2e8f0; padding-top: 20px; }
        .level-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(140px, 1fr)); gap: 15px; margin-top: 20px; }
        .level-card { text-align: center; padding: 15px; border-radius: 8px; border: 2px solid; }
        @media print { body { margin: 0; padding: 15px; } .level-grid { break-inside: avoid; } }
    </style>
</head>
<body>
    <div class="header">
        <h1>${rubricData.assignmentInfo.title || 'Assessment Rubric'}</h1>
        <p style="color: #64748b; font-size: 1.1em;">${stripHtml(rubricData.assignmentInfo.description) || 'No description provided'}</p>
    </div>
    
    <div class="assignment-info">
        <div style="display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 15px;">
            <p><strong>Assignment Weight:</strong> ${rubricData.assignmentInfo.weight}% of Final Grade</p>
            <p><strong>Passing Threshold:</strong> ${rubricData.assignmentInfo.passingThreshold}%</p>
            <p><strong>Total Points:</strong> ${totalPoints}</p>
            <p><strong>Number of Criteria:</strong> ${rubricData.criteria.length}</p>
        </div>
    </div>
    
    <table class="rubric-table">
        <thead>
            <tr>
                <th style="width: 20%">Criterion</th>
                ${displayLevels.map(level => `<th style="width: ${80 / displayLevels.length}%; background-color: ${level.color};">${level.name}</th>`).join('')}
            </tr>
        </thead>
        <tbody>
            ${rubricData.criteria.map(criterion => `
                <tr>
                    <td class="criterion-name">
                        <strong style="color: #1e40af; font-size: 1.1em;">${criterion.name || 'Unnamed Criterion'}</strong>
                        <div style="font-weight: normal; color: #475569; margin-top: 8px; font-style: italic;">
                            ${(criterion.description) || 'No description provided'}
                        </div>
                        <div style="background: #1e40af; color: white; padding: 5px 10px; border-radius: 4px; margin-top: 10px; text-align: center; font-weight: bold;">
                            ${criterion.maxPoints} points
                        </div>
                    </td>
                    ${displayLevels.map(level => `
                        <td style="background-color: ${level.color}08;">
                            <div class="point-range" style="color: ${level.color}; margin-bottom: 10px;">
                                ${calculatePointRange(criterion, level.level)} pts
                            </div>
                            <div class="level-description">
                                ${(criterion.levels[level.level]?.description) || '<em>Not defined</em>'}
                            </div>
                        </td>
                    `).join('')}
                </tr>
            `).join('')}
        </tbody>
    </table>
    
    <div class="overall-score">
        <h3 style="color: #1e40af; margin-bottom: 15px;">Overall Achievement Levels</h3>
        <p style="color: #475569; margin-bottom: 20px;">Each submission receives an overall achievement level based on total rubric score.</p>
        <div class="level-grid">
            ${displayLevels.map(level => `
                <div class="level-card" style="border-color: ${level.color}; background-color: ${level.color}15;">
                    <div style="font-weight: bold; color: ${level.color}; margin-bottom: 5px;">${level.name}</div>
                    <div style="font-size: 1.2em; font-weight: bold; margin: 8px 0; color: ${level.color};">
                        ${Math.round(totalPoints * level.multiplier)}+ pts
                    </div>
                    <div style="font-size: 0.8em; color: #64748b;">${(level.multiplier * 100).toFixed(0)}% or higher</div>
                </div>
            `).join('')}
        </div>
    </div>
    
    <div class="footer">
        <p><strong>Rubric Generated:</strong> ${new Date().toLocaleDateString('en-US', {
            weekday: 'long',
            year: 'numeric',
            month: 'long',
            day: 'numeric',
            hour: '2-digit',
            minute: '2-digit'
        })}</p>
        <p>Total Possible Points: <strong>${totalPoints}</strong> | Criteria: <strong>${rubricData.criteria.length}</strong> | Levels: <strong>${rubricData.rubricLevels.length}</strong></p>
    </div>
</body>
</html>`;

        const htmlBlob = new Blob([htmlContent], { type: 'text/html' });
        const url = URL.createObjectURL(htmlBlob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `rubric_${rubricData.assignmentInfo.title.replace(/\s+/g, '_') || 'display'}_${new Date().toISOString().split('T')[0]}.html`;
        link.click();
        URL.revokeObjectURL(url);
    };

    const ContentDisplay = ({ content, onEdit, placeholder, isTextarea = false, className = "" }) => {
        const hasHtml = containsHtml(content);

        if (hasHtml && !isTextarea) {
            return (
                <div className={`relative group ${className}`}>
                    <div
                        className="p-2 border rounded text-xs bg-white min-h-[80px] max-h-[300px] overflow-y-auto"
                        style={{
                            wordWrap: 'break-word',
                            overflowWrap: 'break-word',
                            whiteSpace: 'pre-wrap',
                            maxWidth: '100%'
                        }}
                        dangerouslySetInnerHTML={{ __html: content }}
                    />
                    <button
                        type="button"
                        onClick={onEdit}
                        className="absolute top-1 right-1 opacity-70 group-hover:opacity-100 transition-opacity bg-blue-500 text-white rounded p-1 hover:bg-blue-600 cursor-pointer"
                        title="Open rich text editor"
                    >
                        <Edit3 size={12} />
                    </button>
                </div>
            );
        }

        if (isTextarea) {
            return (
                <div className="relative">
                    <textarea
                        value={stripHtml(content)}
                        onChange={(e) => onEdit(e.target.value)}
                        className={`w-full p-3 border rounded-lg resize-both hover:border-blue-400 transition-all duration-200 pr-12 ${className}`}
                        rows="3"
                        placeholder={placeholder}
                        title="Type here for quick editing, or click the expand button for rich text editor"
                        style={{
                            minHeight: '80px',
                            maxHeight: '200px',
                            resize: 'both',
                            wordWrap: 'break-word',
                            overflowWrap: 'break-word'
                        }}
                    />
                    <button
                        type="button"
                        onClick={() => onEdit(null, true)}
                        className="absolute top-2 right-2 z-10 opacity-70 hover:opacity-100 transition-opacity bg-blue-500 text-white rounded p-1 hover:bg-blue-600 cursor-pointer"
                        title="Open rich text editor"
                    >
                        <Maximize2 size={14} />
                    </button>
                </div>
            );
        }

        return (
            <div className="relative">
                <textarea
                    value={stripHtml(content)}
                    onChange={(e) => onEdit(e.target.value)}
                    className={`w-full p-2 border rounded text-xs resize-both hover:border-blue-400 transition-all duration-200 pr-8 ${className}`}
                    rows="2"
                    placeholder={placeholder}
                    title="Type here for quick editing, or click the expand button for rich text editor"
                    style={{
                        minHeight: '48px',
                        maxHeight: '200px',
                        resize: 'both',
                        wordWrap: 'break-word',
                        overflowWrap: 'break-word'
                    }}
                />
                <button
                    type="button"
                    onClick={() => onEdit(null, true)}
                    className="absolute top-1 right-1 z-10 opacity-70 hover:opacity-100 transition-opacity bg-blue-500 text-white rounded p-1 hover:bg-blue-600 cursor-pointer"
                    title="Open rich text editor"
                >
                    <Maximize2 size={12} />
                </button>
            </div>
        );
    };

    const RichTextToolbar = () => (
        <div className="border-b bg-gray-50 p-3">
            <div className="flex flex-wrap gap-1 mb-2">
                <div className="flex border-r border-gray-300 pr-2 mr-2">
                    <select
                        onChange={(e) => { if (e.target.value) { handleRichTextCommand('formatBlock', e.target.value); e.target.value = ''; } }}
                        className="p-1 text-xs border rounded"
                        title="Text Format"
                    >
                        <option value="">Format</option>
                        <option value="h1">Heading 1</option>
                        <option value="h2">Heading 2</option>
                        <option value="h3">Heading 3</option>
                        <option value="p">Paragraph</option>
                        <option value="blockquote">Quote</option>
                        <option value="pre">Code Block</option>
                    </select>
                </div>

                <div className="flex border-r border-gray-300 pr-2 mr-2">
                    <button type="button" onClick={() => handleRichTextCommand('bold')} className="p-2 hover:bg-gray-200 rounded" title="Bold (Ctrl+B)"><span className="font-bold">B</span></button>
                    <button type="button" onClick={() => handleRichTextCommand('italic')} className="p-2 hover:bg-gray-200 rounded" title="Italic (Ctrl+I)"><span className="italic">I</span></button>
                    <button type="button" onClick={() => handleRichTextCommand('underline')} className="p-2 hover:bg-gray-200 rounded" title="Underline (Ctrl+U)"><span className="underline">U</span></button>
                    <button type="button" onClick={() => handleRichTextCommand('strikeThrough')} className="p-2 hover:bg-gray-200 rounded" title="Strikethrough"><span className="line-through">S</span></button>
                </div>

                <div className="flex border-r border-gray-300 pr-2 mr-2">
                    <input type="color" onChange={(e) => handleRichTextCommand('foreColor', e.target.value)} className="w-8 h-8 border rounded cursor-pointer" title="Text Color" />
                    <input type="color" onChange={(e) => handleRichTextCommand('hiliteColor', e.target.value)} className="w-8 h-8 border rounded cursor-pointer ml-1" title="Highlight Color" />
                </div>

                <div className="flex border-r border-gray-300 pr-2 mr-2">
                    <button type="button" onClick={() => handleRichTextCommand('insertUnorderedList')} className="p-2 hover:bg-gray-200 rounded" title="Bullet List"> List</button>
                    <button type="button" onClick={() => handleRichTextCommand('insertOrderedList')} className="p-2 hover:bg-gray-200 rounded" title="Numbered List">1. List</button>
                </div>

                <div className="flex">
                    <button type="button" onClick={() => handleRichTextCommand('removeFormat')} className="p-2 hover:bg-gray-200 rounded" title="Clear Formatting">Clear</button>
                </div>
            </div>
        </div>
    );

    return (
        <div className="min-h-screen bg-gray-50 p-6">
            <style>{`
                .rubric-table-container table { table-layout: fixed; width: 100%; }
                .rubric-table-container td { max-width: 0; word-wrap: break-word; overflow-wrap: break-word; white-space: pre-wrap; vertical-align: top; }
                .rich-text-editor li { margin: 0.2em 0; line-height: 1.4; }
                .rich-text-editor li p { margin: 0; }
                .rich-text-editor ul, .rich-text-editor ol { margin: 0.5em 0; padding-left: 1.5em; line-height: 1.5; }
            `}</style>
            <div className="max-w-7xl mx-auto bg-white rounded-lg shadow-lg">
                <div className="bg-blue-900 text-white p-6 rounded-t-lg">
                    <div className="flex justify-between items-center">
                        <div>
                            <h1 className="text-2xl font-bold">Professional Rubric Builder</h1>
                            <p className="text-blue-200">Create comprehensive assessment rubrics with detailed criteria and feedback</p>
                        </div>
                        <div className="flex gap-2 flex-wrap">
                            <button onClick={resetRubric} className="bg-gray-700 hover:bg-gray-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><RotateCcw size={14} /> Reset</button>
                            <button onClick={() => importInputRef.current?.click()} className="bg-purple-700 hover:bg-purple-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><Upload size={14} /> Import</button>
                            <button onClick={transferToGrading} className="bg-blue-700 hover:bg-blue-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><ArrowRight size={14} /> Use for Grading</button>
                            <button onClick={saveRubric} className="bg-teal-700 hover:bg-teal-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><Save size={14} /> Save Draft</button>
                            <button onClick={exportForGrading} className="bg-green-700 hover:bg-green-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><Download size={14} /> Export JSON</button>
                            <button onClick={exportToHTML} className="bg-orange-700 hover:bg-orange-600 px-3 py-2 rounded flex items-center gap-1 text-sm"><FileText size={14} /> Export HTML</button>
                        </div>
                    </div>
                </div>
                <div className="p-6">
                    {/* Assignment Info Section */}
                    <div className="assignment-info bg-blue-50 border border-blue-200 p-6 rounded-lg mb-6">
                        <h3 className="text-lg font-semibold text-blue-800 mb-4">Assignment Information</h3>
                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Assignment Title *</label>
                                <input type="text" value={rubricData.assignmentInfo.title} onChange={(e) => updateAssignmentInfo('title', e.target.value)} className="w-full p-3 border rounded-lg" />
                            </div>
                            <div>
                                <label className="block text-sm font-medium text-gray-700 mb-1">Weight (% of Final Grade)</label>
                                <input type="number" value={rubricData.assignmentInfo.weight} onChange={(e) => updateAssignmentInfo('weight', parseInt(e.target.value) || 0)} className="w-full p-3 border rounded-lg" />
                            </div>
                            <div className="md:col-span-2">
                                <label className="block text-sm font-medium text-gray-700 mb-1">Description</label>
                                <ContentDisplay
                                    content={rubricData.assignmentInfo.description}
                                    onEdit={(value, openRichEditor) => {
                                        if (openRichEditor) openInlineEditor(rubricData.assignmentInfo.description, 'Assignment Description', (newContent) => updateAssignmentInfo('description', newContent), null, null, 'assignment');
                                        else updateAssignmentInfo('description', value);
                                    }}
                                    isTextarea={true}
                                />
                            </div>
                        </div>
                    </div>

                    {/* Inline Rich Text Editor Modal */}
                    {inlineEditor.show && (
                        <div className="fixed inset-0 bg-black bg-opacity-50 z-50 flex items-center justify-center p-4">
                            <div className="bg-white rounded-lg shadow-2xl w-full max-w-3xl flex flex-col">
                                <div className="flex justify-between items-center p-4 border-b">
                                    <h4 className="text-lg font-semibold">{inlineEditor.field}</h4>
                                    <div>
                                        <button onClick={() => closeInlineEditor(true)} className="bg-blue-600 text-white px-4 py-2 rounded mr-2">Save</button>
                                        <button onClick={() => closeInlineEditor(false)} className="bg-gray-600 text-white px-4 py-2 rounded">Cancel</button>
                                    </div>
                                </div>
                                <RichTextToolbar />
                                <div className="p-4 flex-grow">
                                    <div
                                        ref={editorRef}
                                        contentEditable={true}
                                        onInput={handleRichTextChange}
                                        onKeyDown={handleKeyDown}
                                        onPaste={handlePaste}
                                        className="w-full h-full p-4 border rounded-lg focus:outline-none focus:ring-2 focus:ring-blue-500 rich-text-editor"
                                        style={{ minHeight: '300px' }}
                                        suppressContentEditableWarning={true}
                                    />
                                </div>
                            </div>
                        </div>
                    )}

                    {/* Rubric Table */}
                    <div className="bg-white border rounded-lg overflow-hidden mb-6 rubric-table-container">
                        <div className="bg-gray-50 p-4 border-b flex justify-between items-center">
                            <h3 className="text-lg font-semibold text-gray-800">Assessment Criteria Matrix</h3>
                            <button onClick={addCriterion} className="bg-blue-600 hover:bg-blue-700 text-white px-4 py-2 rounded flex items-center gap-2"><Plus size={16} /> Add Criterion</button>
                        </div>
                        <div className="overflow-x-auto">
                            <table className="w-full border-collapse">
                                <thead>
                                    <tr className="bg-gray-100">
                                        <th className="border p-3 text-left font-semibold">Criterion</th>
                                        {getDisplayLevels().map(level => (
                                            <th key={level.level} className="border p-3 text-center font-semibold" style={{ backgroundColor: level.color + '20', color: level.color }}>{level.name}</th>
                                        ))}
                                        <th className="border p-3 text-center font-semibold">Actions</th>
                                    </tr>
                                </thead>
                                <tbody>
                                    {rubricData.criteria.map((criterion, index) => (
                                        <tr key={criterion.id}>
                                            <td className="border p-3 bg-gray-50">
                                                <input type="text" value={criterion.name} onChange={(e) => updateCriterion(criterion.id, 'name', e.target.value)} className="w-full p-2 border rounded mb-2" placeholder={`Criterion ${index + 1}`} />
                                                <ContentDisplay
                                                    content={criterion.description}
                                                    onEdit={(value, openRichEditor) => {
                                                        if (openRichEditor) openInlineEditor(criterion.description, `Criterion Description`, (newContent) => updateCriterion(criterion.id, 'description', newContent), criterion.id, null, 'criterion');
                                                        else updateCriterion(criterion.id, 'description', value);
                                                    }}
                                                />
                                                <input type="number" value={criterion.maxPoints} onChange={(e) => updateCriterion(criterion.id, 'maxPoints', parseInt(e.target.value) || 0)} className="w-full p-1 border rounded text-sm mt-2" />
                                            </td>
                                            {getDisplayLevels().map(level => (
                                                <td key={level.level} className="border p-3">
                                                    <div className="font-bold text-center text-sm" style={{ color: level.color }}>{calculatePointRange(criterion, level.level)} pts</div>
                                                    <ContentDisplay
                                                        content={criterion.levels[level.level]?.description || ''}
                                                        onEdit={(value, openRichEditor) => {
                                                            if (openRichEditor) openInlineEditor(criterion.levels[level.level]?.description, `${level.name} Description`, (newContent) => updateCriterionLevel(criterion.id, level.level, 'description', newContent), criterion.id, level.level, 'level');
                                                            else updateCriterionLevel(criterion.id, level.level, 'description', value);
                                                        }}
                                                    />
                                                </td>
                                            ))}
                                            <td className="border p-3 text-center">
                                                <button onClick={() => removeCriterion(criterion.id)} className="text-red-600 hover:text-red-800" disabled={rubricData.criteria.length <= 1}><X size={16} /></button>
                                            </td>
                                        </tr>
                                    ))}
                                </tbody>
                            </table>
                        </div>
                    </div>
                </div>
                <input ref={importInputRef} type="file" accept=".json" onChange={importRubric} className="hidden" />
            </div>
        </div>
    );
};

export default RubricCreator;
