        }
        
        .rubric-table th {
            background: linear-gradient(135deg, #1e40af 0%, #3b82f6 100%);
            color: white;
            padding: 15px 12px;
            font-weight: 600;
            text-align: left;
            font-size: 14px;
        }
        
        .rubric-table td {
            padding: 12px;
            border-bottom: 1px solid #e2e8f0;
            vertical-align: top;
            font-size: 13px;
        }
        
        .criterion-cell {
            background: #f8fafc;
            border-right: 1px solid #e2e8f0;
            width: 20%;
        }
        
        .criterion-name {
            font-weight: 600;
            color: #1e40af;
            margin-bottom: 5px;
        }
        
        .criterion-desc {
            font-size: 12px;
            color: #64748b;
            font-style: italic;
        }
        
        .points-cell, .earned-cell {
            text-align: center;
            font-weight: 600;
            width: 10%;
        }
        
        .level-cell {
            text-align: center;
            font-weight: 600;
            width: 15%;
        }
        
        .level-achieved {
            border-radius: 4px;
            padding: 8px 4px;
        }
        
        .comments-cell {
            width: 45%;
        }
        
        .level-description {
            background: #f1f5f9;
            border-left: 4px solid #3b82f6;
            padding: 10px;
            margin-bottom: 10px;
            border-radius: 4px;
        }
        
        .level-description strong {
            color: #1e40af;
        }
        
        .level-description p {
            margin: 5px 0;
            line-height: 1.4;
        }
        
        .additional-comments {
            background: #fffbeb;
            border: 1px solid #fbbf24;
            border-radius: 4px;
            padding: 10px;
            margin-top: 10px;
        }
        
        .additional-comments strong {
            color: #92400e;
        }
        
        .additional-comments p {
            margin: 5px 0;
            line-height: 1.4;
        }
        
        .rubric-summary {
            background: linear-gradient(135deg, #eff6ff 0%, #dbeafe 100%);
            border: 2px solid #3b82f6;
            border-radius: 8px;
            padding: 20px;
            margin-top: 25px;
        }
        
        .summary-grid {
            display: grid;
            grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
            gap: 15px;
        }
        
        .summary-item {
            font-size: 14px;
        }
        
        .passing {
            color: #16a34a;
            font-weight: 600;
        }
        
        .needs-improvement {
            color: #dc2626;
            font-weight: 600;
        }
        
        .penalty {
            color: #ea580c;
        }
        
        .late-policy-section {
            background: #fef2f2;
            border: 1px solid #fca5a5;
            border-radius: 8px;
            padding: 20px;
            margin: 20px 0;
        }
        
        .feedback-section {
            background: #f9fafb;
            border-radius: 8px;
            padding: 20px;
            margin: 20px 0;
            border-left: 4px solid #3b82f6;
        }
        
        .feedback-section h3 {
            color: #1e40af;
            margin-top: 0;
        }
        
        .attachments {
            margin: 25px 0;
        }
        
        .attachment-item {
            display: inline-block;
            margin: 10px;
            padding: 15px;
            background: white;
            border: 1px solid #e2e8f0;
            border-radius: 8px;
            text-align: center;
            max-width: 200px;
            vertical-align: top;
        }
        
        .attachment-image {
            max-width: 150px;
            max-height: 150px;
            object-fit: contain;
            border-radius: 4px;
            margin-bottom: 10px;
        }
        
        .file-icon {
            font-size: 48px;
            margin-bottom: 10px;
        }
        
        .attachment-name {
            font-weight: 500;
            font-size: 12px;
            word-break: break-word;
        }
        
        .attachment-size {
            font-size: 11px;
            color: #64748b;
        }
        
        .video-links {
            margin: 25px 0;
        }
        
        .video-link-item {
            background: white;
            border: 1px solid #e2e8f0;
            border-radius: 8px;
            padding: 15px;
            margin-bottom: 10px;
            display: flex;
            align-items: center;
            gap: 15px;
        }
        
        .video-icon {
            font-size: 24px;
        }
        
        .video-title {
            font-weight: 600;
            margin-bottom: 5px;
        }
        
        .video-url {
            font-size: 12px;
            color: #3b82f6;
            word-break: break-all;
        }
        
        .footer {
            text-align: center;
            margin-top: 40px;
            padding-top: 20px;
            border-top: 1px solid #e2e8f0;
            color: #64748b;
            font-size: 12px;
        }
        
        @keyframes rotate {
            to {
                transform: rotate(360deg);
            }
        }
        
        @media print {
            body {
                background: white;
            }
            .header, .score-summary {
                break-inside: avoid;
            }
            .rubric-table {
                break-inside: avoid;
            }
            .attachment-item {
                break-inside: avoid;
            }
        }
    </style>
</head>
<body>
    <div class="header">
        <div class="header-content">
            <h1>ðŸ“‹ Grade Report</h1>
            <div class="header-info">
                <div>
                    <p><strong>Student:</strong> ${gradingData.student.name}</p>
                    <p><strong>Student ID:</strong> ${gradingData.student.id}</p>
                    <p><strong>Email:</strong> ${gradingData.student.email}</p>
                </div>
                <div>
                    <p><strong>Course:</strong> ${gradingData.course.code} - ${gradingData.course.name}</p>
                    <p><strong>Instructor:</strong> ${gradingData.course.instructor}</p>
                    <p><strong>Term:</strong> ${gradingData.course.term}</p>
                </div>
            </div>
        </div>
    </div>

    <div class="score-summary">
        <div class="score-content">
            <h2 style="margin: 0 0 10px 0; color: #15803d;">ðŸ“Š Final Score</h2>
            <div class="final-score">
                ${totalScore.toFixed(1)} / ${maxPoints}
            </div>
            <div class="score-percentage">
                ${percentage}%
            </div>
            <p style="margin: 10px 0; color: #166534;">
                ${loadedRubric ? `Assessment: ${loadedRubric.assignmentInfo.title}` : `Module: ${currentModule?.courseType} v${currentModule?.version || '1.0'}`}
                ${penaltyApplied ? ` | Late Policy: ${latePolicyLevels[gradingData.latePolicy.level].name}` : ''}
            </p>
        </div>
    </div>

    ${penaltyApplied ? `
    <div class="late-policy-section">
        <h3 style="color: #dc2626; margin-top: 0;">ðŸ“… Late Submission Policy Applied</h3>
        <p><strong>Policy Status:</strong> ${latePolicyLevels[gradingData.latePolicy.level].name}</p>
        <p>${latePolicyLevels[gradingData.latePolicy.level].description}</p>
        <p><strong>Calculation:</strong> Raw Score ${Math.round(rawScore * 10) / 10}/${maxPoints} â†’ Final Score ${Math.round(totalScore * 10) / 10}/${maxPoints}</p>
    </div>
    ` : ''}

    ${rubricTableHTML}
    
    ${Object.entries(gradingData.feedback).filter(([key, value]) => value).map(([key, value]) => `
        <div class="feedback-section">
            <h3>${key.charAt(0).toUpperCase() + key.slice(1)} Feedback</h3>
            <p>${value.replace(/\n/g, '<br>')}</p>
        </div>
    `).join('')}

    ${attachmentsHTML ? `
    <div class="attachments">
        <h3 class="section-title">ðŸ“Ž File Attachments</h3>
        ${attachmentsHTML}
    </div>
    ` : ''}

    ${videoLinksHTML ? `
    <div class="video-links">
        <h3 class="section-title">ðŸŽ¥ Video Review Links</h3>
        ${videoLinksHTML}
    </div>
    ` : ''}
    
    <div class="footer">
        <p><strong>Grade Report Generated</strong></p>
        <p>${currentDate} | ${new Date().toLocaleTimeString()}</p>
        <p>Assessment Platform v2.0 | Professional Grading System</p>
    </div>
</body>
</html>`;

    // Create a new window for PDF printing
    const printWindow = window.open('', '_blank', 'width=800,height=600');
    printWindow.document.write(pdfContent);
    printWindow.document.close();

    // Wait for content to load then trigger print
    printWindow.onload = function () {
      setTimeout(() => {
        printWindow.print();
      }, 500);
    };
  };

  return (
    <div style={{ minHeight: '100vh', background: '#f9fafb', padding: '1.5rem' }}>
      <div style={{ maxWidth: '80rem', margin: '0 auto' }}>
        {/* Header */}
        <div style={{
          background: 'linear-gradient(135deg, #1e3a8a 0%, #1e40af 100%)',
          color: 'white',
          padding: '1.5rem',
          borderRadius: '0.5rem 0.5rem 0 0',
          boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)'
        }}>
          <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <div>
              <h1 style={{ fontSize: '1.875rem', fontWeight: '700', margin: '0 0 0.5rem 0' }}>
                ðŸ“‹ Professional Grading Interface
              </h1>
              <p style={{ color: '#bfdbfe', margin: 0 }}>
                {loadedRubric ? 'Comprehensive rubric-based assessment' : 'Streamlined grading with flexible assessment options'}
              </p>
            </div>
            // STEP 4: Replace the existing export button section with this updated version
            <div style={{ display: 'flex', gap: '0.75rem' }}>
              <button
                onClick={exportToHTML}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  background: '#16a34a',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '0.875rem'
                }}
              >
                <FileDown size={16} />
                Export HTML
              </button>
              <button
                onClick={exportToPDF}
                style={{
                  display: 'flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  background: '#dc2626',
                  color: 'white',
                  padding: '0.5rem 1rem',
                  borderRadius: '0.5rem',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '0.875rem'
                }}
              >
                <FileText size={16} />
                Export PDF
              </button>
            </div>
          </div>
        </div>

        <div style={{ background: 'white', borderRadius: '0 0 0.5rem 0.5rem', boxShadow: '0 4px 6px -1px rgb(0 0 0 / 0.1)' }}>
          <div style={{ padding: '1.5rem' }}>
            {/* Auto-save Indicator */}
            {sharedGradingData && (
              <div style={{
                background: 'linear-gradient(135deg, #f0fdf4 0%, #dcfce7 100%)',
                border: '1px solid #bbf7d0',
                borderRadius: '0.5rem',
                padding: '0.75rem',
                marginBottom: '1.5rem'
              }}>
                <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem', color: '#15803d' }}>
                  <div style={{ width: '8px', height: '8px', background: '#16a34a', borderRadius: '50%' }}></div>
                  <span style={{ fontSize: '0.875rem', fontWeight: '500' }}>Auto-saved - Work preserved across tabs</span>
                </div>
              </div>
            )}

            {/* Course Information */}
            <div style={{ marginBottom: '2rem' }}>
              <h2 style={{ fontSize: '1.25rem', fontWeight: '600', marginBottom: '1rem', color: '#374151' }}>
                Course Information
              </h2>
              <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
                <input
                  type="text"
                  placeholder="Course Code (e.g., ART101)"
                  value={gradingData.course.code}
                  onChange={(e) => {
                    const newValue = e.target.value;
                    setGradingDataLocal(prevData => ({
                      ...prevData,
                      course: { ...prevData.course, code: newValue }
                    }));
                    updateCourseInfo({ code: newValue });
                  }}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '1px solid #d1d5db',
                    borderRadius: '0.5rem',
                    fontSize: '0.875rem'
                  }}
                />
                <input
                  type="text"
                  placeholder="Course Name"
                  value={gradingData.course.name}
                  onChange={(e) => {
                    const newValue = e.target.value;
                    setGradingDataLocal(prevData => ({
                      ...prevData,
                      course: { ...prevData.course, name: newValue }
                    }));
                    updateCourseInfo({ name: newValue });
                  }}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
