import React, { useState, useEffect, useRef } from 'react';
import { useAssessment } from './SharedContext';

// Sample rubric data for fallback
const sampleRubric = {
  courseInfo: {
    courseCode: 'SAMPLE 101',
    courseName: 'Sample Course',
    instructor: 'Sample Instructor',
    term: 'Fall 2024'
  },
  assignmentInfo: {
    name: 'Sample Assignment',
    dueDate: '2024-12-15',
    totalPoints: 100
  },
  criteria: [
    {
      id: 'criteria-1',
      name: 'Content Quality',
      description: 'Demonstrates understanding of key concepts and applies them effectively.',
      weight: 40,
      levels: {
        exceptional: { description: 'Exceptional demonstration of mastery with innovative applications.', pointRange: '36-40' },
        accomplished: { description: 'Strong demonstration of understanding with effective applications.', pointRange: '32-35' },
        emerging: { description: 'Good understanding with some effective applications.', pointRange: '28-31' },
        acceptable: { description: 'Basic understanding with limited applications.', pointRange: '24-27' },
        inadequate: { description: 'Minimal understanding with little to no effective applications.', pointRange: '0-23' }
      },
      feedbackLibrary: {
        strengths: [
          'Excellent grasp of key concepts',
          'Creative application of ideas',
          'Strong analytical thinking',
          'Clear understanding demonstrated'
        ],
        improvements: [
          'Review fundamental concepts',
          'Practice applying concepts to new situations',
          'Seek additional examples',
          'Connect ideas more clearly'
        ],
        general: [
          'Shows good progress in understanding',
          'Consider exploring additional perspectives',
          'Strong foundation for continued learning'
        ]
      }
    }
  ]
};

const GradingTemplate = () => {
  // Get shared context - ENHANCED with new functions
  const {
    sharedRubric,
    sharedCourseDetails,
    setSharedCourseDetails,
    gradingData: sharedGradingData,
    setGradingData: setSharedGradingData,
    updateStudentInfo,
    updateCourseInfo,
    updateAssignmentInfo,
    clearGradingFormData,
    classList,
    setClassList,
    setCurrentStudent,
    gradingSession,
    setGradingSession,
    nextStudentInSession,
    previousStudentInSession,
    initializeGradingSession,
    setActiveTab,
    currentStudent,
    saveDraft,
    loadDraft,
    // NEW: Add the draft/final grade functions
    saveFinalGrade,
    loadFinalGrade,
    finalGrades,
    getGradeStatus
  } = useAssessment();

  // Local state for this template
  const [localGradingData, setLocalGradingData] = useState({
    student: { name: '', id: '', email: '' },
    course: { code: '', name: '', instructor: '', term: '' },
    assignment: { name: '', dueDate: '', maxPoints: 100 },
    feedback: { general: '', strengths: '', improvements: '' },
    attachments: [],
    videoLinks: [],
    latePolicy: { level: 'none', penaltyApplied: false },
    rubricGrading: {},
    metadata: {
      gradedBy: '',
      gradedDate: '',
      aiAssisted: false,
      rubricIntegrated: false
    }
  });

  const [rubricGrading, setRubricGrading] = useState({});
  const [loadedRubric, setLoadedRubric] = useState(null);
  const fileInputRef = useRef(null);
  const videoFileInputRef = useRef(null);

  // Load rubric from shared context
  useEffect(() => {
    if (sharedRubric) {
      setLoadedRubric(sharedRubric);

      // Initialize rubric grading state
      const initialGrading = {};
      sharedRubric.criteria.forEach(criterion => {
        initialGrading[criterion.id] = {
          criterionId: criterion.id,
          selectedLevel: null,
          customComments: ''
        };
      });
      setRubricGrading(initialGrading);

      // Update local grading data with rubric info
      setLocalGradingData(prev => ({
        ...prev,
        course: {
          code: sharedRubric.courseInfo?.courseCode || '',
          name: sharedRubric.courseInfo?.courseName || '',
          instructor: sharedRubric.courseInfo?.instructor || '',
          term: sharedRubric.courseInfo?.term || ''
        },
        assignment: {
          name: sharedRubric.assignmentInfo?.name || '',
          dueDate: sharedRubric.assignmentInfo?.dueDate || '',
          maxPoints: sharedRubric.assignmentInfo?.totalPoints || 100
        }
      }));
    } else {
      // Use sample rubric if none provided
      setLoadedRubric(sampleRubric);
      const initialGrading = {};
      sampleRubric.criteria.forEach(criterion => {
        initialGrading[criterion.id] = {
          criterionId: criterion.id,
          selectedLevel: null,
          customComments: ''
        };
      });
      setRubricGrading(initialGrading);
    }
  }, [sharedRubric]);

  // ENHANCED: Load saved data when student changes
  useEffect(() => {
    if (currentStudent?.id) {
      console.log('🔍 Loading data for student:', currentStudent.name);

      // Get the grade status for this student
      const gradeStatus = getGradeStatus(currentStudent.id);
      console.log('📊 Grade status:', gradeStatus);

      let savedData = null;

      if (gradeStatus === 'final') {
        // Load final grade data
        savedData = loadFinalGrade ? loadFinalGrade(currentStudent.id) : null;
        console.log('📋 Loading final grade data:', savedData ? 'Found' : 'Not found');
      } else if (gradeStatus === 'draft') {
        // Load draft data
        savedData = loadDraft(currentStudent.id);
        console.log('📝 Loading draft data:', savedData ? 'Found' : 'Not found');
      }

      if (savedData) {
        console.log('✅ Successfully loaded saved grade data');
        setLocalGradingData(savedData);
        if (savedData.rubricGrading) {
          setRubricGrading(savedData.rubricGrading);
        }
      } else {
        console.log('🆕 No saved data found, initializing fresh form');
        setLocalGradingData(prev => ({
          ...prev,
          student: {
            name: currentStudent.name,
            id: currentStudent.id,
            email: currentStudent.email
          },
          feedback: { general: '', strengths: '', improvements: '' },
          attachments: [],
          videoLinks: [],
          latePolicy: { level: 'none', penaltyApplied: false }
        }));

        // Reset rubric grading state
        if (loadedRubric) {
          const initialGrading = {};
          loadedRubric.criteria.forEach(criterion => {
            initialGrading[criterion.id] = {
              criterionId: criterion.id,
              selectedLevel: null,
              customComments: ''
            };
          });
          setRubricGrading(initialGrading);
        }
      }
    }
  }, [currentStudent, getGradeStatus, loadDraft, loadFinalGrade]);

  // Sync local data with shared context
  useEffect(() => {
    setSharedGradingData(localGradingData);
  }, [localGradingData, setSharedGradingData]);

  // Update rubric grading in local data
  useEffect(() => {
    setLocalGradingData(prev => ({
      ...prev,
      rubricGrading: rubricGrading
    }));
  }, [rubricGrading]);

  // ENHANCED: New student navigation functions for draft/final system
  const handleNextStudentAsDraft = () => {
    // Save current student's work as draft
    if (currentStudent?.id) {
      console.log('💾 Saving as draft for student:', currentStudent.name);
      saveDraft(currentStudent.id, localGradingData);
    }

    const success = nextStudentInSession('draft');
    if (!success) {
      alert('Grading session completed! All students have been graded.');
      setActiveTab('class-manager');
    } else {
      resetGradingForm();
      console.log('✅ Draft saved, moved to next student');
    }
  };

  const handleNextStudentAsFinal = () => {
    // Save current student's work as final grade
    if (currentStudent?.id) {
      console.log('✅ Finalizing grade for student:', currentStudent.name);
      saveFinalGrade(currentStudent.id, localGradingData);
    }

    const success = nextStudentInSession('final');
    if (!success) {
      alert('Grading session completed! All students have been graded.');
      setActiveTab('class-manager');
    } else {
      resetGradingForm();
      console.log('✅ Final grade saved, moved to next student');
    }
  };

  const handlePreviousStudent = () => {
    // Auto-save current student's work before moving
    if (currentStudent?.id) {
      saveDraft(currentStudent.id, localGradingData);
    }

    const success = previousStudentInSession();
    if (!success) {
      console.log('Already at first student or no active session');
    } else {
      resetGradingForm();
    }
  };

  const resetGradingForm = () => {
    // Reset only the grading-specific data
    setLocalGradingData(prev => ({
      ...prev,
      feedback: { general: '', strengths: '', improvements: '' },
      attachments: [],
      videoLinks: [],
      latePolicy: { level: 'none', penaltyApplied: false }
    }));

    // Reset rubric grading state
    if (loadedRubric) {
      const initialGrading = {};
      loadedRubric.criteria.forEach(criterion => {
        initialGrading[criterion.id] = {
          criterionId: criterion.id,
          selectedLevel: null,
          customComments: ''
        };
      });
      setRubricGrading(initialGrading);
    }
  };

  // Get current student info for navigation display
  const getCurrentStudentInfo = () => {
    if (!classList || !gradingSession?.active || !gradingSession.currentStudent) {
      return null;
    }

    const currentIndex = gradingSession.currentStudentIndex;
    const totalStudents = classList.students.length;
    const student = gradingSession.currentStudent;

    return {
      student,
      position: currentIndex + 1,
      total: totalStudents,
      isFirst: currentIndex === 0,
      isLast: currentIndex === totalStudents - 1
    };
  };

  const currentStudentInfo = getCurrentStudentInfo();

  // Handle level selection for rubric criteria
  const handleLevelSelect = (criterionId, level, pointRange) => {
    setRubricGrading(prev => ({
      ...prev,
      [criterionId]: {
        ...prev[criterionId],
        selectedLevel: level,
        pointRange: pointRange
      }
    }));
  };

  // Handle custom comments
  const handleCustomComments = (criterionId, comments) => {
    setRubricGrading(prev => ({
      ...prev,
      [criterionId]: {
        ...prev[criterionId],
        customComments: comments
      }
    }));
  };

  // Add quick feedback
  const addQuickFeedback = (criterionId, feedback) => {
    const currentComments = rubricGrading[criterionId]?.customComments || '';
    const newComments = currentComments ? `${currentComments}\n${feedback}` : feedback;
    handleCustomComments(criterionId, newComments);
  };

  // Calculate total score
  const calculateTotalScore = () => {
    if (!loadedRubric) return { totalScore: 0, maxPoints: 100, penaltyApplied: false };

    let totalScore = 0;
    let maxPoints = 0;

    loadedRubric.criteria.forEach(criterion => {
      const grading = rubricGrading[criterion.id];
      maxPoints += criterion.weight;

      if (grading?.selectedLevel && grading?.pointRange) {
        // Parse point range (e.g., "36-40" or "10")
        const pointStr = grading.pointRange;
        const pointValue = pointStr.includes('-')
          ? parseInt(pointStr.split('-')[1]) // Take the high end of range
          : parseInt(pointStr);

        if (!isNaN(pointValue)) {
          totalScore += pointValue;
        }
      }
    });

    // Apply late penalty if applicable
    const penaltyApplied = localGradingData.latePolicy?.penaltyApplied || false;
    if (penaltyApplied) {
      const penaltyPercent = {
        'minor': 0.1,
        'moderate': 0.2,
        'significant': 0.3
      }[localGradingData.latePolicy.level] || 0;

      totalScore = Math.max(0, totalScore - (totalScore * penaltyPercent));
    }

    return { totalScore, maxPoints, penaltyApplied };
  };

  // File attachment handlers
  const handleFileUpload = (event) => {
    const files = Array.from(event.target.files);
    files.forEach(file => {
      const reader = new FileReader();
      reader.onload = (e) => {
        const newAttachment = {
          id: Date.now() + Math.random(),
          name: file.name,
          size: file.size,
          type: file.type,
          base64Data: e.target.result
        };

        setLocalGradingData(prev => ({
          ...prev,
          attachments: [...prev.attachments, newAttachment]
        }));
      };
      reader.readAsDataURL(file);
    });
  };

  const removeAttachment = (attachmentId) => {
    setLocalGradingData(prev => ({
      ...prev,
      attachments: prev.attachments.filter(att => att.id !== attachmentId)
    }));
  };

  // Video link handlers
  const addVideoLink = () => {
    const title = prompt('Enter video title:');
    const url = prompt('Enter video URL:');

    if (title && url) {
      const newVideoLink = {
        id: Date.now(),
        title,
        url
      };

      setLocalGradingData(prev => ({
        ...prev,
        videoLinks: [...prev.videoLinks, newVideoLink]
      }));
    }
  };

  const removeVideoLink = (linkId) => {
    setLocalGradingData(prev => ({
      ...prev,
      videoLinks: prev.videoLinks.filter(link => link.id !== linkId)
    }));
  };

  // Export to PDF functionality
  const exportToPDF = () => {
    const scoreCalculation = calculateTotalScore();
    const totalScore = scoreCalculation.totalScore.toFixed(1);
    const maxPoints = loadedRubric ? loadedRubric.assignmentInfo.totalPoints : localGradingData.assignment.maxPoints;
    const percentage = ((totalScore / maxPoints) * 100).toFixed(1);
    const penaltyApplied = scoreCalculation.penaltyApplied;
    const currentDate = new Date().toLocaleDateString('en-US', {
      weekday: 'long',
      year: 'numeric',
      month: 'long',
      day: 'numeric'
    });

    // Build attachments HTML
    const attachmentsHTML = localGradingData.attachments.map((att, index) => {
      if (att.base64Data) {
        return `
          <div class="attachment-item">
            <img src="${att.base64Data}" alt="${att.name}" class="attachment-image" />
            <div class="attachment-info">
              <div class="attachment-name">${att.name}</div>
              <div class="attachment-size">${(att.size / 1024).toFixed(1)} KB</div>
            </div>
          </div>
        `;
      } else {
        return `
          <div class="attachment-item">
            <div class="file-icon">📄</div>
            <div class="attachment-info">
              <div class="attachment-name">${att.name}</div>
              <div class="attachment-size">${(att.size / 1024).toFixed(1)} KB</div>
            </div>
          </div>
        `;
      }
    }).join('');

    // Build video links HTML
    const videoLinksHTML = localGradingData.videoLinks.map(link => `
      <div class="video-link-item">
        <span class="video-icon">🎥</span>
        <div class="video-info">
          <div class="video-title">${link.title}</div>
          <div class="video-url">${link.url}</div>
        </div>
      </div>
    `).join('');

    // Enhanced rubric table for PDF
    const rubricTableHTML = loadedRubric ? loadedRubric.criteria.map(criterion => {
      const grading = rubricGrading[criterion.id] || {};
      const selectedLevel = grading.selectedLevel;
      const customComments = grading.customComments || '';

      const levelsHTML = Object.entries(criterion.levels).map(([levelKey, levelInfo]) => {
        const isSelected = selectedLevel === levelKey;
        return `
          <tr class="${isSelected ? 'selected-level' : ''}">
            <td class="level-name">${levelKey}</td>
            <td class="level-description">${levelInfo.description}</td>
            <td class="level-points">${levelInfo.pointRange}</td>
            <td class="level-selected">${isSelected ? '✓' : ''}</td>
          </tr>
        `;
      }).join('');

      return `
        <div class="criterion-section">
          <h3 class="criterion-title">${criterion.name} (${criterion.weight} points)</h3>
          <p class="criterion-description">${criterion.description}</p>
          <table class="levels-table">
            <thead>
              <tr>
                <th>Level</th>
                <th>Description</th>
                <th>Points</th>
                <th>Selected</th>
              </tr>
            </thead>
            <tbody>
              ${levelsHTML}
            </tbody>
          </table>
          ${customComments ? `<div class="custom-comments"><strong>Comments:</strong> ${customComments}</div>` : ''}
        </div>
      `;
    }).join('') : '';

    const htmlContent = `
<!DOCTYPE html>
<html>
<head>
    <meta charset="UTF-8">
    <title>Grade Report - ${localGradingData.student.name}</title>
    <style>
        body { font-family: Arial, sans-serif; line-height: 1.6; margin: 20px; color: #333; }
        .header { text-align: center; border-bottom: 3px solid #2563eb; padding-bottom: 20px; margin-bottom: 30px; }
        .title { color: #2563eb; font-size: 28px; font-weight: bold; margin: 0; }
        .subtitle { color: #666; font-size: 14px; margin: 5px 0; }
        .score-summary { background: linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%); border: 2px solid #0284c7; border-radius: 12px; padding: 20px; margin: 20px 0; text-align: center; }
        .total-score { font-size: 36px; font-weight: bold; color: #0284c7; margin: 10px 0; }
        .percentage { font-size: 18px; color: #0369a1; }
        .penalty-notice { background: #fef3c7; border: 1px solid #f59e0b; border-radius: 6px; padding: 10px; margin: 10px 0; color: #92400e; }
        .info-grid { display: grid; grid-template-columns: 1fr 1fr; gap: 20px; margin: 20px 0; }
        .info-card { background: #f8fafc; border: 1px solid #e2e8f0; border-radius: 8px; padding: 15px; }
        .info-title { font-weight: bold; color: #1e40af; margin-bottom: 10px; border-bottom: 1px solid #e2e8f0; padding-bottom: 5px; }
        .criterion-section { margin: 30px 0; border: 1px solid #e5e7eb; border-radius: 8px; padding: 20px; }
        .criterion-title { color: #1f2937; font-size: 18px; margin: 0 0 10px 0; }
        .criterion-description { color: #666; margin: 0 0 15px 0; font-style: italic; }
        .levels-table { width: 100%; border-collapse: collapse; margin: 15px 0; }
        .levels-table th, .levels-table td { border: 1px solid #d1d5db; padding: 8px; text-align: left; }
        .levels-table th { background: #f3f4f6; font-weight: bold; }
        .selected-level { background: #dcfce7 !important; font-weight: bold; }
        .custom-comments { background: #fffbeb; border: 1px solid #fbbf24; border-radius: 6px; padding: 10px; margin: 10px 0; }
        .feedback-section { background: #f0fdf4; border: 1px solid #22c55e; border-radius: 8px; padding: 20px; margin: 20px 0; }
        .attachments-section, .videos-section { margin: 20px 0; padding: 15px; border: 1px solid #e5e7eb; border-radius: 8px; background: #fafafa; }
        .attachment-item, .video-link-item { display: flex; align-items: center; margin: 10px 0; padding: 10px; background: white; border-radius: 6px; border: 1px solid #e5e7eb; }
        .attachment-image { max-width: 100px; max-height: 60px; margin-right: 10px; border-radius: 4px; }
        .file-icon, .video-icon { font-size: 20px; margin-right: 10px; }
        .attachment-info, .video-info { flex-grow: 1; }
        .attachment-name, .video-title { font-weight: bold; color: #374151; }
        .attachment-size, .video-url { color: #6b7280; font-size: 12px; }
        .footer { margin-top: 40px; padding-top: 20px; border-top: 2px solid #e5e7eb; text-align: center; color: #666; font-size: 12px; }
        @media print { .no-print { display: none; } }
    </style>
</head>
<body>
    <div class="header">
        <h1 class="title">📋 Grade Report</h1>
        <div class="subtitle">Generated on ${currentDate}</div>
    </div>

    <div class="score-summary">
        <div class="total-score">${totalScore} / ${maxPoints} points</div>
        <div class="percentage">${percentage}%</div>
        ${penaltyApplied ? '<div class="penalty-notice">⚠️ Late penalty applied</div>' : ''}
    </div>

    <div class="info-grid">
        <div class="info-card">
            <div class="info-title">👨‍🎓 Student Information</div>
            <div><strong>Name:</strong> ${localGradingData.student.name}</div>
            <div><strong>ID:</strong> ${localGradingData.student.id}</div>
            <div><strong>Email:</strong> ${localGradingData.student.email}</div>
        </div>
        <div class="info-card">
            <div class="info-title">📚 Course Information</div>
            <div><strong>Course:</strong> ${localGradingData.course.code} - ${localGradingData.course.name}</div>
            <div><strong>Instructor:</strong> ${localGradingData.course.instructor}</div>
            <div><strong>Term:</strong> ${localGradingData.course.term}</div>
        </div>
        <div class="info-card">
            <div class="info-title">📝 Assignment Details</div>
            <div><strong>Assignment:</strong> ${localGradingData.assignment.name}</div>
            <div><strong>Due Date:</strong> ${localGradingData.assignment.dueDate}</div>
            <div><strong>Max Points:</strong> ${localGradingData.assignment.maxPoints}</div>
        </div>
        <div class="info-card">
            <div class="info-title">⏰ Late Policy</div>
            <div><strong>Level:</strong> ${localGradingData.latePolicy.level}</div>
            <div><strong>Penalty Applied:</strong> ${localGradingData.latePolicy.penaltyApplied ? 'Yes' : 'No'}</div>
        </div>
    </div>

    <div class="rubric-section">
        <h2 style="color: #1f2937; border-bottom: 2px solid #e5e7eb; padding-bottom: 10px;">📊 Rubric Assessment</h2>
        ${rubricTableHTML}
    </div>

    ${localGradingData.feedback.general || localGradingData.feedback.strengths || localGradingData.feedback.improvements ? `
    <div class="feedback-section">
        <h2 style="color: #15803d; margin-top: 0;">💬 Instructor Feedback</h2>
        ${localGradingData.feedback.general ? `<div><strong>General Comments:</strong><br>${localGradingData.feedback.general}</div>` : ''}
        ${localGradingData.feedback.strengths ? `<div style="margin-top: 15px;"><strong>Strengths:</strong><br>${localGradingData.feedback.strengths}</div>` : ''}
        ${localGradingData.feedback.improvements ? `<div style="margin-top: 15px;"><strong>Areas for Improvement:</strong><br>${localGradingData.feedback.improvements}</div>` : ''}
    </div>
    ` : ''}

    ${localGradingData.attachments.length > 0 ? `
    <div class="attachments-section">
        <h3 style="margin-top: 0; color: #374151;">📎 Attachments</h3>
        ${attachmentsHTML}
    </div>
    ` : ''}

    ${localGradingData.videoLinks.length > 0 ? `
    <div class="videos-section">
        <h3 style="margin-top: 0; color: #374151;">🎥 Video Links</h3>
        ${videoLinksHTML}
    </div>
    ` : ''}

    <div class="footer">
        This grade report was generated using the Enhanced Rubric Grading System.<br>
        For questions about this assessment, please contact your instructor.
    </div>
</body>
</html>
    `;

    // Create and download PDF
    const newWindow = window.open('', '_blank');
    newWindow.document.write(htmlContent);
    newWindow.document.close();

    setTimeout(() => {
      newWindow.print();
    }, 250);
  };

  if (!loadedRubric) {
    return (
      <div style={{
        padding: '2rem',
        textAlign: 'center',
        background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)',
        minHeight: '400px',
        display: 'flex',
        flexDirection: 'column',
        justifyContent: 'center',
        alignItems: 'center'
      }}>
        <div style={{ fontSize: '4rem', marginBottom: '1rem' }}>📋</div>
        <h2 style={{ color: '#0369a1', marginBottom: '1rem' }}>No Rubric Loaded</h2>
        <p style={{ color: '#0284c7', fontSize: '1.1rem', marginBottom: '2rem' }}>
          Please create or load a rubric from the Rubric Creator to begin grading.
        </p>
        <button
          onClick={() => setActiveTab('rubric-creator')}
          style={{
            background: 'linear-gradient(135deg, #3b82f6 0%, #1d4ed8 100%)',
            color: 'white',
            padding: '0.75rem 2rem',
            borderRadius: '0.5rem',
            border: 'none',
            fontSize: '1rem',
            fontWeight: '600',
            cursor: 'pointer',
            boxShadow: '0 4px 6px rgba(59, 130, 246, 0.3)'
          }}
        >
          Go to Rubric Creator
        </button>
      </div>
    );
  }

  return (
    <div style={{
      maxWidth: '1200px',
      margin: '0 auto',
      padding: '2rem',
      background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
      minHeight: '100vh'
    }}>
      {/* Header */}
      <div style={{
        background: 'linear-gradient(135deg, #1e40af 0%, #3730a3 100%)',
        color: 'white',
        padding: '2rem',
        borderRadius: '1rem',
        marginBottom: '2rem',
        textAlign: 'center'
      }}>
        <h1 style={{ margin: '0 0 0.5rem 0', fontSize: '2.5rem' }}>📝 Grading Template</h1>
        <p style={{ margin: 0, fontSize: '1.1rem', opacity: 0.9 }}>
          {loadedRubric.courseInfo.courseCode} - {loadedRubric.assignmentInfo.name}
        </p>
      </div>

      {/* Current Student Info Display */}
      {currentStudentInfo && (
        <div style={{
          background: 'linear-gradient(135deg, #ecfdf5 0%, #d1fae5 100%)',
          border: '2px solid #10b981',
          borderRadius: '0.75rem',
          padding: '1rem',
          marginBottom: '2rem',
          textAlign: 'center'
        }}>
          <h3 style={{ margin: '0 0 0.5rem 0', color: '#047857' }}>
            👨‍🎓 Currently Grading: {currentStudentInfo.student.name}
          </h3>
          <p style={{ margin: 0, color: '#065f46' }}>
            Student {currentStudentInfo.position} of {currentStudentInfo.total} • ID: {currentStudentInfo.student.id}
          </p>
        </div>
      )}

      {/* Grading Form */}
      <div style={{
        background: 'white',
        borderRadius: '1rem',
        padding: '2rem',
        boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
        marginBottom: '2rem'
      }}>
        {/* Student Information */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>👨‍🎓 Student Information</h3>
          <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                Student Name
              </label>
              <input
                type="text"
                value={localGradingData.student.name}
                onChange={(e) => setLocalGradingData(prev => ({
                  ...prev,
                  student: { ...prev.student, name: e.target.value }
                }))}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                Student ID
              </label>
              <input
                type="text"
                value={localGradingData.student.id}
                onChange={(e) => setLocalGradingData(prev => ({
                  ...prev,
                  student: { ...prev.student, id: e.target.value }
                }))}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem'
                }}
              />
            </div>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                Email
              </label>
              <input
                type="email"
                value={localGradingData.student.email}
                onChange={(e) => setLocalGradingData(prev => ({
                  ...prev,
                  student: { ...prev.student, email: e.target.value }
                }))}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem'
                }}
              />
            </div>
          </div>
        </div>

        {/* Rubric Grading Section */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>📊 Rubric Assessment</h3>

          {loadedRubric.criteria.map((criterion, index) => {
            const currentGrading = rubricGrading[criterion.id] || {};

            return (
              <div key={criterion.id} style={{
                border: '2px solid #e5e7eb',
                borderRadius: '0.75rem',
                padding: '1.5rem',
                marginBottom: '1.5rem',
                background: '#fafafa'
              }}>
                <h4 style={{
                  color: '#1f2937',
                  fontSize: '1.25rem',
                  marginBottom: '0.5rem',
                  display: 'flex',
                  justifyContent: 'space-between',
                  alignItems: 'center'
                }}>
                  {criterion.name}
                  <span style={{
                    background: '#3b82f6',
                    color: 'white',
                    padding: '0.25rem 0.75rem',
                    borderRadius: '1rem',
                    fontSize: '0.875rem',
                    fontWeight: '600'
                  }}>
                    {criterion.weight} pts
                  </span>
                </h4>
                <p style={{ color: '#6b7280', marginBottom: '1rem', fontStyle: 'italic' }}>
                  {criterion.description}
                </p>

                {/* Performance Levels */}
                <div style={{ display: 'grid', gap: '0.75rem', marginBottom: '1rem' }}>
                  {Object.entries(criterion.levels).map(([levelKey, levelInfo]) => {
                    const isSelected = currentGrading.selectedLevel === levelKey;

                    return (
                      <div
                        key={levelKey}
                        onClick={() => handleLevelSelect(criterion.id, levelKey, levelInfo.pointRange)}
                        style={{
                          border: `2px solid ${isSelected ? '#10b981' : '#d1d5db'}`,
                          borderRadius: '0.5rem',
                          padding: '1rem',
                          cursor: 'pointer',
                          background: isSelected ? '#ecfdf5' : 'white',
                          transition: 'all 0.2s ease'
                        }}
                      >
                        <div style={{
                          display: 'flex',
                          justifyContent: 'space-between',
                          alignItems: 'center',
                          marginBottom: '0.5rem'
                        }}>
                          <span style={{
                            fontWeight: '600',
                            color: isSelected ? '#047857' : '#374151',
                            textTransform: 'capitalize'
                          }}>
                            {levelKey}
                          </span>
                          <span style={{
                            background: isSelected ? '#10b981' : '#9ca3af',
                            color: 'white',
                            padding: '0.25rem 0.5rem',
                            borderRadius: '0.25rem',
                            fontSize: '0.75rem',
                            fontWeight: '600'
                          }}>
                            {levelInfo.pointRange} pts
                          </span>
                        </div>
                        <p style={{
                          margin: 0,
                          color: isSelected ? '#065f46' : '#6b7280',
                          fontSize: '0.875rem'
                        }}>
                          {levelInfo.description}
                        </p>
                      </div>
                    );
                  })}
                </div>

                {/* Custom Comments */}
                <div style={{ marginBottom: '1rem' }}>
                  <label style={{
                    display: 'block',
                    marginBottom: '0.5rem',
                    fontWeight: '600',
                    color: '#374151'
                  }}>
                    Custom Comments
                  </label>
                  <textarea
                    value={currentGrading.customComments || ''}
                    onChange={(e) => handleCustomComments(criterion.id, e.target.value)}
                    rows={3}
                    style={{
                      width: '100%',
                      padding: '0.75rem',
                      border: '2px solid #e5e7eb',
                      borderRadius: '0.5rem',
                      fontSize: '0.875rem',
                      resize: 'vertical'
                    }}
                    placeholder="Add specific feedback for this criterion..."
                  />
                </div>

                {/* Quick Feedback Buttons */}
                {criterion.feedbackLibrary && (
                  <div style={{ marginTop: '1rem' }}>
                    <h5 style={{
                      color: '#6b7280',
                      fontSize: '0.875rem',
                      marginBottom: '0.5rem',
                      fontWeight: '600'
                    }}>
                      Quick Feedback:
                    </h5>

                    {Object.entries(criterion.feedbackLibrary).map(([category, feedbacks]) => (
                      <div key={category} style={{ marginBottom: '0.5rem' }}>
                        <span style={{
                          fontSize: '0.75rem',
                          fontWeight: '600',
                          color: '#9ca3af',
                          textTransform: 'capitalize'
                        }}>
                          {category}:
                        </span>
                        <div style={{ display: 'flex', flexWrap: 'wrap', gap: '0.25rem', marginTop: '0.25rem' }}>
                          {feedbacks.map((feedback, idx) => (
                            <button
                              key={idx}
                              onClick={() => addQuickFeedback(criterion.id, feedback)}
                              style={{
                                fontSize: '0.75rem',
                                padding: '0.25rem 0.5rem',
                                margin: '0.125rem',
                                background: '#e5e7eb',
                                color: '#374151',
                                border: 'none',
                                borderRadius: '0.25rem',
                                cursor: 'pointer'
                              }}
                            >
                              + {feedback}
                            </button>
                          ))}
                        </div>
                      </div>
                    ))}
                  </div>
                )}
              </div>
            );
          })}
        </div>

        {/* NEW: Enhanced Complete and Grade Next Section - PLACED AFTER RUBRIC */}
        {currentStudentInfo && (
          <div style={{
            background: 'linear-gradient(135deg, #ecfdf5 0%, #d1fae5 100%)',
            border: '2px solid #10b981',
            borderRadius: '0.75rem',
            padding: '1.5rem',
            marginBottom: '2rem',
            textAlign: 'center'
          }}>
            <h3 style={{ fontSize: '1.25rem', fontWeight: '600', color: '#047857', marginBottom: '1rem' }}>
              Ready to move to the next student?
            </h3>
            <p style={{ color: '#065f46', marginBottom: '1.5rem', fontSize: '0.875rem' }}>
              Choose how to save the current grading before proceeding.
            </p>

            <div style={{ display: 'flex', gap: '1rem', justifyContent: 'center', flexWrap: 'wrap' }}>
              {/* Save as Draft Button */}
              <button
                onClick={handleNextStudentAsDraft}
                style={{
                  display: 'inline-flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  background: 'linear-gradient(135deg, #fbbf24 0%, #f59e0b 100%)',
                  color: 'white',
                  padding: '0.75rem 1.5rem',
                  borderRadius: '0.5rem',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '0.875rem',
                  fontWeight: '600',
                  boxShadow: '0 2px 8px rgba(251, 191, 36, 0.4)',
                  transition: 'all 0.2s ease'
                }}
                onMouseOver={(e) => {
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 4px 12px rgba(251, 191, 36, 0.5)';
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = '0 2px 8px rgba(251, 191, 36, 0.4)';
                }}
              >
                <span style={{ fontSize: '1.1rem' }}>📝</span>
                Save & Grade Next Student
              </button>

              {/* Finalize & Grade Next Button */}
              <button
                onClick={handleNextStudentAsFinal}
                style={{
                  display: 'inline-flex',
                  alignItems: 'center',
                  gap: '0.5rem',
                  background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
                  color: 'white',
                  padding: '0.75rem 1.5rem',
                  borderRadius: '0.5rem',
                  border: 'none',
                  cursor: 'pointer',
                  fontSize: '0.875rem',
                  fontWeight: '600',
                  boxShadow: '0 2px 8px rgba(16, 185, 129, 0.4)',
                  transition: 'all 0.2s ease'
                }}
                onMouseOver={(e) => {
                  e.target.style.transform = 'translateY(-2px)';
                  e.target.style.boxShadow = '0 4px 12px rgba(16, 185, 129, 0.5)';
                }}
                onMouseOut={(e) => {
                  e.target.style.transform = 'translateY(0)';
                  e.target.style.boxShadow = '0 2px 8px rgba(16, 185, 129, 0.4)';
                }}
              >
                <span style={{ fontSize: '1.1rem' }}>✅</span>
                Finalize & Grade Next Student
              </button>
            </div>

            <div style={{
              marginTop: '1rem',
              padding: '0.75rem',
              background: 'rgba(255, 255, 255, 0.6)',
              borderRadius: '0.375rem',
              fontSize: '0.75rem',
              color: '#065f46'
            }}>
              <strong>Draft:</strong> Save progress, can edit later • <strong>Finalize:</strong> Complete grading, ready for student/LMS
            </div>

            {currentStudentInfo.isLast && (
              <div style={{
                marginTop: '1rem',
                padding: '0.75rem',
                background: '#fef3c7',
                borderRadius: '0.375rem',
                fontSize: '0.875rem',
                color: '#92400e',
                fontWeight: '500'
              }}>
                🎉 This is the last student in your grading session!
              </div>
            )}
          </div>
        )}

        {/* Score Summary */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>📊 Score Summary</h3>
          {(() => {
            const scoreCalc = calculateTotalScore();
            const percentage = ((scoreCalc.totalScore / scoreCalc.maxPoints) * 100).toFixed(1);

            return (
              <div style={{
                background: 'linear-gradient(135deg, #f0f9ff 0%, #e0f2fe 100%)',
                border: '2px solid #0284c7',
                borderRadius: '0.75rem',
                padding: '1.5rem',
                textAlign: 'center'
              }}>
                <div style={{ fontSize: '2rem', fontWeight: 'bold', color: '#0284c7', marginBottom: '0.5rem' }}>
                  {scoreCalc.totalScore.toFixed(1)} / {scoreCalc.maxPoints} points
                </div>
                <div style={{ fontSize: '1.25rem', color: '#0369a1', marginBottom: '1rem' }}>
                  {percentage}%
                </div>
                {scoreCalc.penaltyApplied && (
                  <div style={{
                    background: '#fef3c7',
                    border: '1px solid #f59e0b',
                    borderRadius: '0.375rem',
                    padding: '0.5rem',
                    color: '#92400e',
                    fontSize: '0.875rem'
                  }}>
                    ⚠️ Late penalty applied
                  </div>
                )}
              </div>
            );
          })()}
        </div>

        {/* Late Policy */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>⏰ Late Policy</h3>
          <div style={{ display: 'flex', gap: '1rem', alignItems: 'center', flexWrap: 'wrap' }}>
            <select
              value={localGradingData.latePolicy.level}
              onChange={(e) => setLocalGradingData(prev => ({
                ...prev,
                latePolicy: { ...prev.latePolicy, level: e.target.value }
              }))}
              style={{
                padding: '0.75rem',
                border: '2px solid #e5e7eb',
                borderRadius: '0.5rem',
                fontSize: '1rem',
                minWidth: '150px'
              }}
            >
              <option value="none">No Late Penalty</option>
              <option value="minor">Minor (10%)</option>
              <option value="moderate">Moderate (20%)</option>
              <option value="significant">Significant (30%)</option>
            </select>

            <label style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
              <input
                type="checkbox"
                checked={localGradingData.latePolicy.penaltyApplied}
                onChange={(e) => setLocalGradingData(prev => ({
                  ...prev,
                  latePolicy: { ...prev.latePolicy, penaltyApplied: e.target.checked }
                }))}
                style={{ transform: 'scale(1.2)' }}
              />
              <span style={{ fontWeight: '600', color: '#374151' }}>Apply Penalty</span>
            </label>
          </div>
        </div>

        {/* General Feedback */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>💬 Instructor Feedback</h3>

          <div style={{ display: 'grid', gap: '1rem' }}>
            <div>
              <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                General Comments
              </label>
              <textarea
                value={localGradingData.feedback.general}
                onChange={(e) => setLocalGradingData(prev => ({
                  ...prev,
                  feedback: { ...prev.feedback, general: e.target.value }
                }))}
                rows={4}
                style={{
                  width: '100%',
                  padding: '0.75rem',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  fontSize: '1rem',
                  resize: 'vertical'
                }}
                placeholder="Provide overall feedback about the student's work..."
              />
            </div>

            <div style={{ display: 'grid', gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))', gap: '1rem' }}>
              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                  Strengths
                </label>
                <textarea
                  value={localGradingData.feedback.strengths}
                  onChange={(e) => setLocalGradingData(prev => ({
                    ...prev,
                    feedback: { ...prev.feedback, strengths: e.target.value }
                  }))}
                  rows={3}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    resize: 'vertical'
                  }}
                  placeholder="What did the student do well?"
                />
              </div>

              <div>
                <label style={{ display: 'block', marginBottom: '0.5rem', fontWeight: '600', color: '#374151' }}>
                  Areas for Improvement
                </label>
                <textarea
                  value={localGradingData.feedback.improvements}
                  onChange={(e) => setLocalGradingData(prev => ({
                    ...prev,
                    feedback: { ...prev.feedback, improvements: e.target.value }
                  }))}
                  rows={3}
                  style={{
                    width: '100%',
                    padding: '0.75rem',
                    border: '2px solid #e5e7eb',
                    borderRadius: '0.5rem',
                    fontSize: '1rem',
                    resize: 'vertical'
                  }}
                  placeholder="What can be improved next time?"
                />
              </div>
            </div>
          </div>
        </div>

        {/* File Attachments */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>📎 File Attachments</h3>

          <div style={{ marginBottom: '1rem' }}>
            <input
              type="file"
              ref={fileInputRef}
              onChange={handleFileUpload}
              multiple
              style={{ display: 'none' }}
            />
            <button
              onClick={() => fileInputRef.current?.click()}
              style={{
                background: 'linear-gradient(135deg, #6366f1 0%, #4f46e5 100%)',
                color: 'white',
                padding: '0.75rem 1.5rem',
                borderRadius: '0.5rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '1rem',
                fontWeight: '600'
              }}
            >
              📎 Add Files
            </button>
          </div>

          {localGradingData.attachments.length > 0 && (
            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
              gap: '1rem'
            }}>
              {localGradingData.attachments.map((attachment) => (
                <div key={attachment.id} style={{
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  padding: '1rem',
                  background: '#fafafa',
                  position: 'relative'
                }}>
                  {attachment.base64Data && attachment.type.startsWith('image/') ? (
                    <img
                      src={attachment.base64Data}
                      alt={attachment.name}
                      style={{
                        width: '100%',
                        maxHeight: '150px',
                        objectFit: 'cover',
                        borderRadius: '0.25rem',
                        marginBottom: '0.5rem'
                      }}
                    />
                  ) : (
                    <div style={{
                      display: 'flex',
                      alignItems: 'center',
                      justifyContent: 'center',
                      height: '100px',
                      background: '#e5e7eb',
                      borderRadius: '0.25rem',
                      marginBottom: '0.5rem',
                      fontSize: '2rem'
                    }}>
                      📄
                    </div>
                  )}

                  <div style={{ fontSize: '0.875rem', fontWeight: '600', marginBottom: '0.25rem' }}>
                    {attachment.name}
                  </div>
                  <div style={{ fontSize: '0.75rem', color: '#6b7280', marginBottom: '0.5rem' }}>
                    {(attachment.size / 1024).toFixed(1)} KB
                  </div>

                  <button
                    onClick={() => removeAttachment(attachment.id)}
                    style={{
                      position: 'absolute',
                      top: '0.5rem',
                      right: '0.5rem',
                      background: '#ef4444',
                      color: 'white',
                      border: 'none',
                      borderRadius: '50%',
                      width: '24px',
                      height: '24px',
                      cursor: 'pointer',
                      fontSize: '0.75rem'
                    }}
                  >
                    ×
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Video Links */}
        <div style={{ marginBottom: '2rem' }}>
          <h3 style={{ color: '#1f2937', fontSize: '1.5rem', marginBottom: '1rem' }}>🎥 Video Links</h3>

          <div style={{ marginBottom: '1rem' }}>
            <button
              onClick={addVideoLink}
              style={{
                background: 'linear-gradient(135deg, #f59e0b 0%, #d97706 100%)',
                color: 'white',
                padding: '0.75rem 1.5rem',
                borderRadius: '0.5rem',
                border: 'none',
                cursor: 'pointer',
                fontSize: '1rem',
                fontWeight: '600'
              }}
            >
              🎥 Add Video Link
            </button>
          </div>

          {localGradingData.videoLinks.length > 0 && (
            <div style={{ display: 'flex', flexDirection: 'column', gap: '0.5rem' }}>
              {localGradingData.videoLinks.map((link) => (
                <div key={link.id} style={{
                  display: 'flex',
                  alignItems: 'center',
                  justifyContent: 'space-between',
                  border: '2px solid #e5e7eb',
                  borderRadius: '0.5rem',
                  padding: '1rem',
                  background: '#fafafa'
                }}>
                  <div>
                    <div style={{ fontWeight: '600', marginBottom: '0.25rem' }}>{link.title}</div>
                    <div style={{ fontSize: '0.875rem', color: '#6b7280' }}>{link.url}</div>
                  </div>
                  <button
                    onClick={() => removeVideoLink(link.id)}
                    style={{
                      background: '#ef4444',
                      color: 'white',
                      border: 'none',
                      borderRadius: '0.25rem',
                      padding: '0.5rem',
                      cursor: 'pointer'
                    }}
                  >
                    Remove
                  </button>
                </div>
              ))}
            </div>
          )}
        </div>

        {/* Export Controls */}
        <div style={{
          background: 'linear-gradient(135deg, #f3f4f6 0%, #e5e7eb 100%)',
          borderRadius: '0.75rem',
          padding: '1.5rem',
          textAlign: 'center'
        }}>
          <h3 style={{ color: '#374151', marginBottom: '1rem' }}>📄 Export Options</h3>
          <button
            onClick={exportToPDF}
            style={{
              background: 'linear-gradient(135deg, #dc2626 0%, #b91c1c 100%)',
              color: 'white',
              padding: '0.75rem 2rem',
              borderRadius: '0.5rem',
              border: 'none',
              cursor: 'pointer',
              fontSize: '1rem',
              fontWeight: '600',
              boxShadow: '0 4px 6px rgba(220, 38, 38, 0.3)'
            }}
          >
            📄 Export Grade Report (PDF)
          </button>
        </div>
      </div>
    </div>
  );
};

export default GradingTemplate;
