// Complete GradingPolicyManager.jsx - Fully Consolidated Component
// Brand new comprehensive policy management dashboard with full functionality

import React, { useState, useEffect } from 'react';
import { useAssessment } from './SharedContext';
import {
    Settings, Plus, Edit3, Trash2, Copy, Download, Upload,
    Save, X, Eye, EyeOff, Calculator, AlertTriangle, CheckCircle,
    Clock, Target, BarChart3, FileDown, FileUp, Info, Zap, RefreshCw
} from 'lucide-react';

// Policy templates for quick creation
const POLICY_TEMPLATES = {
    graceful: {
        id: 'template_graceful',
        name: 'Graceful Policy',
        description: 'Lenient late policy with minimal penalties for understanding circumstances',
        levels: {
            none: {
                name: 'On Time',
                multiplier: 1.0,
                description: 'Assignment submitted on or before due date',
                color: '#16a34a',
                timeframe: 'On or before due date'
            },
            within72: {
                name: 'Within 3 Days',
                multiplier: 0.9,
                description: '10% penalty for submissions within 3 days of due date',
                color: '#f59e0b',
                timeframe: 'Up to 3 days late'
            },
            within7days: {
                name: 'Within 1 Week',
                multiplier: 0.75,
                description: '25% penalty for submissions within 1 week of due date',
                color: '#ea580c',
                timeframe: 'Up to 1 week late'
            },
            beyond7days: {
                name: 'Beyond 1 Week',
                multiplier: 0.5,
                description: '50% penalty for submissions beyond 1 week',
                color: '#dc2626',
                timeframe: 'More than 1 week late'
            }
        },
        isTemplate: true,
        isCustom: false
    },
    strict: {
        id: 'template_strict',
        name: 'Strict Policy',
