sourcesContent:
import React, { useState, useRef, useEffect, useCallback } from 'react';
import {
  Download, Upload, FileText, Video, Plus, X, Save, FileDown, Bot,
  ChevronDown, ChevronUp, ArrowLeft, ArrowRight, Users, PlayCircle,
  CheckCircle, Clock, SkipForward, SkipBack, Pause, RotateCcw
} from 'lucide-react';
import { useAssessment } from './SharedContext';

// Late policy levels configuration
const latePolicyLevels = {
  none: { multiplier: 1.0, description: 'No late penalty applied' },
  minor: { multiplier: 0.95, description: '5% penalty for minor lateness' },
  moderate: { multiplier: 0.85, description: '15% penalty for moderate lateness' },
  major: { multiplier: 0.7, description: '30% penalty for major lateness' },
  severe: { multiplier: 0.5, description: '50% penalty for severe lateness' }
};

// Sample comprehensive rubric from the PDF
const sampleRubric = {
  assignmentInfo: {
    title: "Digital Portfolio Project - Final Showcase",
    description: "Students will create a comprehensive digital portfolio showcasing their best work from the semester, including 5-7 completed projects with professional presentation, artist statements, and technical documentation. The portfolio must demonstrate growth, technical proficiency, and creative vision suitable for industry presentation.",
    weight: 30,
    passingThreshold: 65,
    totalPoints: 120
  },
  rubricLevels: [
    { level: 'incomplete', name: 'Incomplete', description: 'No submission or unusable', color: '#95a5a6', multiplier: 0 },
    { level: 'unacceptable', name: 'Unacceptable', description: 'Below minimum standards', color: '#e74c3c', multiplier: 0.3 },
    { level: 'developing', name: 'Developing', description: 'Approaching standards', color: '#f39c12', multiplier: 0.55 },
    { level: 'acceptable', name: 'Acceptable (PASS)', description: 'Meets minimum standards', color: '#27ae60', multiplier: 0.7 },
    { level: 'emerging', name: 'Emerging', description: 'Above standard expectations', color: '#2980b9', multiplier: 0.82 },
    { level: 'accomplished', name: 'Accomplished', description: 'Strong professional quality', color: '#16a085', multiplier: 0.92 },
    { level: 'exceptional', name: 'Exceptional', description: 'Outstanding professional quality', color: '#8e44ad', multiplier: 1.0 }
  ],
  criteria: [
    {
      id: 'technical-proficiency',
      name: 'Technical Proficiency & Craft',
      description: 'Demonstration of technical skills, software mastery, and attention to detail in execution',
      maxPoints: 35,
      weight: 29,
      levels: {
        incomplete: { description: 'No technical work submitted or files corrupted/unusable.', pointRange: '0' },
        unacceptable: { description: 'Severe technical issues: poor resolution, improper file formats, broken links, or significant software handling problems that interfere with viewing work.', pointRange: '8-12' },
        developing: { description: 'Basic technical competency shown but with noticeable issues: inconsistent quality, some resolution problems, or workflow inefficiencies. Software use shows beginner-level understanding.', pointRange: '16-21' },
        acceptable: { description: 'Solid technical foundation with appropriate file formats, good resolution, and functional navigation. Demonstrates competent use of course software with minimal technical distractions.', pointRange: '22-26' },
        emerging: { description: 'Strong technical execution with sophisticated understanding of software capabilities, proper file management, and polished presentation. Shows advanced workflow knowledge.', pointRange: '27-29' },
        accomplished: { description: 'Excellent technical mastery demonstrating advanced software skills, innovative use of tools, and professional-grade execution that enhances the overall portfolio presentation.', pointRange: '30-32' },
        exceptional: { description: 'Outstanding technical excellence showing mastery that exceeds course expectations, innovative problem-solving, and techniques that demonstrate readiness for professional practice.', pointRange: '33-35' }
      },
      feedbackLibrary: {
        strengths: [
          'Excellent technical execution',
          'Advanced software proficiency demonstrated',
          'Professional file management and organization',
          'Innovative use of tools and techniques',
          'High attention to technical detail'
        ],
        improvements: [
          'Improve file resolution and formats',
          'Work on software efficiency and workflow',
          'Address technical consistency issues',
          'Enhance navigation and functionality',
          'Practice advanced software techniques'
        ],
        general: [
          'Technical skills are developing appropriately',
          'Shows good understanding of course software',
          'Professional technical standards maintained'
        ]
      }
    },
    {
      id: 'creative-vision',
      name: 'Creative Vision & Conceptual Development',
      description: 'Evidence of original thinking, creative problem-solving, and conceptual depth in project development',
      maxPoints: 30,
      weight: 25,
      levels: {
        incomplete: { description: 'No evidence of creative development or conceptual thinking.', pointRange: '0' },
        unacceptable: { description: 'Limited creativity with derivative or superficial concepts. Little evidence of original thinking or creative problem-solving.', pointRange: '7-10' },
        developing: { description: 'Some creative elements present but concepts lack depth or originality. Beginning to show personal voice but needs further development.', pointRange: '14-18' },
        acceptable: { description: 'Good creative foundation with evidence of original thinking and personal artistic voice. Concepts are well-developed and show creative problem-solving.', pointRange: '19-22' },
        emerging: { description: 'Strong creative vision with sophisticated conceptual development. Shows unique perspective and innovative approaches to creative challenges.', pointRange: '23-25' },
        accomplished: { description: 'Excellent creative mastery with compelling original concepts that demonstrate advanced creative thinking and artistic maturity.', pointRange: '26-28' },
        exceptional: { description: 'Outstanding creative excellence with innovative concepts that push boundaries and demonstrate exceptional artistic vision and creative leadership.', pointRange: '29-30' }
      },
      feedbackLibrary: {
        strengths: [
          'Strong creative vision evident',
          'Innovative conceptual approach',
          'Unique artistic perspective shown',
          'Excellent creative problem-solving',
          'Compelling original concepts'
        ],
        improvements: [
          'Develop concepts with greater depth',
          'Push creative boundaries further',
          'Strengthen personal artistic voice',
          'Explore more innovative approaches',
          'Enhance conceptual sophistication'
        ],
        general: [
          'Creative development is progressing well',
          'Shows good creative foundation',
          'Artistic voice is emerging appropriately'
        ]
      }
    },
    {
      id: 'presentation-communication',
      name: 'Presentation & Communication',
      description: 'Quality of portfolio organization, visual presentation, and ability to communicate artistic intent effectively',
      maxPoints: 25,
      weight: 21,
      levels: {
        incomplete: { description: 'No coherent presentation or communication of work.', pointRange: '0' },
        unacceptable: { description: 'Poor organization with unclear presentation. Lacks effective communication of artistic intent or project development.', pointRange: '6-8' },
        developing: { description: 'Basic organization present but presentation lacks polish. Some communication of intent but needs clearer articulation.', pointRange: '11-15' },
        acceptable: { description: 'Well-organized presentation with clear communication of artistic concepts. Professional appearance suitable for academic review.', pointRange: '16-19' },
        emerging: { description: 'Strong presentation skills with sophisticated organization and clear, compelling communication of creative process and intent.', pointRange: '20-21' },
        accomplished: { description: 'Excellent presentation demonstrating advanced communication skills and professional-quality organization that enhances the work significantly.', pointRange: '22-23' },
        exceptional: { description: 'Outstanding presentation excellence that demonstrates exceptional communication skills and creates a compelling, professional portfolio experience.', pointRange: '24-25' }
      },
      feedbackLibrary: {
        strengths: [
          'Excellent portfolio organization',
          'Clear communication of artistic intent',
          'Professional presentation quality',
          'Compelling visual narrative',
          'Strong presentation skills'
        ],
        improvements: [
          'Improve portfolio organization',
          'Clarify communication of concepts',
          'Enhance visual presentation',
          'Strengthen narrative structure',
          'Polish presentation elements'
        ],
        general: [
          'Presentation skills are developing',
          'Good communication foundation',
          'Professional standards maintained'
        ]
      }
    },
    {
      id: 'professional-readiness',
      name: 'Professional Readiness & Industry Standards',
      description: 'Demonstration of work quality and presentation standards appropriate for professional portfolio review',
      maxPoints: 30,
      weight: 25,
      levels: {
        incomplete: { description: 'Work does not meet basic professional standards.', pointRange: '0' },
        unacceptable: { description: 'Significant gaps in professional presentation. Work quality below industry standards for portfolio review.', pointRange: '7-10' },
        developing: { description: 'Approaching professional standards but needs improvement in quality and presentation for industry readiness.', pointRange: '14-18' },
        acceptable: { description: 'Meets professional standards with portfolio quality appropriate for industry review and academic requirements.', pointRange: '19-22' },
        emerging: { description: 'Strong professional quality that exceeds basic standards and demonstrates readiness for industry presentation.', pointRange: '23-25' },
        accomplished: { description: 'Excellent professional preparation with portfolio demonstrating high industry standards and career readiness.', pointRange: '26-28' },
