// src/components/ClassListManager.js
import React, { useState, useRef } from 'react';
import { parseExcelFile, validateStudentData } from '../utils/excelParser';
import { useAssessment } from './SharedContext';
import {
    Upload, Users, FileSpreadsheet, CheckCircle, AlertCircle,
    Download, RefreshCw, BookOpen, Clock, BarChart3, FileText,
    PlayCircle, Pause
} from 'lucide-react';

const ClassListManager = () => {
    // Pull everything from context (aliasing our session navigation helpers)
    const {
        /* Tab navigation */
        activeTab,
        setActiveTab,

        /* Rubric / grading data */
        sharedRubric,
        gradingData,
        setGradingData,

        /* Update helpers */
        updateStudentInfo,
        updateCourseInfo,
        updateAssignmentInfo,
        clearGradingFormData,

        /* Class‐list state */
        classList,
        setClassList,

        /* Current student convenience */
        currentStudent,
        setCurrentStudent,

        /* Batch‐grading session */
        gradingSession,
        setGradingSession,
        initializeGradingSession,
        nextStudentInSession: nextStudent,
        previousStudentInSession: previousStudent,

        /* Draft helpers */
        hasDraft,
        loadDraft,
    } = useAssessment();

    // Local UI state
    const [loading, setLoading] = useState(false);
    const [importResult, setImportResult] = useState(null);
    const fileInputRef = useRef(null);

    // ── 1) Excel import ────────────────────────────────────────────────
    const handleExcelImport = async (file) => {
        if (!file) return;
        setLoading(true);
        setImportResult(null);

        try {
            const result = await parseExcelFile(file);
            if (result.success) {
                const validation = validateStudentData(result.students);
                const classData = {
                    ...result,
                    validation,
                    fileName: file.name,
                    fileSize: file.size,
                    importTime: new Date().toISOString(),
                    gradingProgress: result.students.map(student => ({
                        studentId: student.id,
                        studentName: student.name,
                        status: 'pending',
                        grade: null,
                        lastModified: null,
                        timeSpent: 0,
                    })),
                };

                setClassList(classData);
                setImportResult(result);

                // auto-populate course info
                if (result.courseMetadata) {
                    updateCourseInfo({
                        code: result.courseMetadata.courseCode || '',
                        name: result.courseMetadata.courseName || '',
                        instructor: result.courseMetadata.professors || '',
                        term: result.courseMetadata.term || '',
                    });
                }
            } else {
                setImportResult(result);
            }
        } catch (error) {
            setImportResult({
                success: false,
                error: error.message,
                students: [],
            });
        }

        setLoading(false);
    };

    // ── 2) Start batch session ─────────────────────────────────────────
    const startGradingSession = () => {
        if (!classList || !classList.students.length) return;
        const success = initializeGradingSession(classList);
        if (success) {
            setActiveTab('grading-tool');
        }
    };

    // ── 3) Load a single student into the grading form ────────────────
    const loadStudentForGrading = (student) => {
        updateStudentInfo({
            name: student.name,
            id: student.id,
            email: student.email,
        });
        if (sharedRubric) {
            updateAssignmentInfo({
                name: sharedRubric.assignmentInfo?.title || '',
                maxPoints: sharedRubric.assignmentInfo?.totalPoints || 100,
            });
        }
        setActiveTab('grading-tool');
    };

    // ── 4) Grading progress calculator ─────────────────────────────────
    const getGradingProgress = () => {
        if (!classList) return { completed: 0, total: 0, percentage: 0 };

        // match both 'completed_draft' and 'completed_final'
        const completed = classList.gradingProgress
            .filter(p => p.status?.startsWith('completed'))
            .length;

        const total = classList.students.length;
        const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;

        return { completed, total, percentage };
    };

    // ── 5) Export CSV ───────────────────────────────────────────────────
    const exportClassGrades = () => {
        if (!classList) return;
        const csvRows = [
            ['Student ID', 'Student Name', 'Email', 'Program', 'Status', 'Grade', 'Last Modified'],
            ...classList.students.map((student, idx) => ([
                student.id,
                student.name,
                student.email,
                student.program,
                classList.gradingProgress[idx]?.status || 'pending',
                classList.gradingProgress[idx]?.grade || 'Not Graded',
                classList.gradingProgress[idx]?.lastModified || 'Never',
            ]))
        ];
        const csvContent = csvRows.map(r => r.join(',')).join('\n');
        const blob = new Blob([csvContent], { type: 'text/csv' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `${classList.courseMetadata?.courseCode || 'class'}_grades_${new Date().toISOString().slice(0, 10)}.csv`;
        link.click();
        URL.revokeObjectURL(url);
    };

    const progress = getGradingProgress();

    // ── 6) Render ───────────────────────────────────────────────────────
    return (
        <div className="min-h-screen bg-gray-50 p-6">
            <div className="max-w-7xl mx-auto">
                {/* Header */}
                <div className="bg-gradient-to-r from-indigo-600 to-purple-600 text-white p-6 rounded-t-lg shadow-lg">
                    <div className="flex justify-between items-center">
                        <div>
                            <h1 className="text-3xl font-bold mb-2">Class List Manager</h1>
                            <p className="text-indigo-100">
                                Import, manage, and grade entire classes efficiently
                            </p>
                        </div>
                        <div className="flex items-center gap-4">
                            {classList && (
                                <div className="text-right">
                                    <div className="text-2xl font-bold">
                                        {classList.students.length}
                                    </div>
                                    <div className="text-sm text-indigo-200">
                                        Students
                                    </div>
                                </div>
                            )}
                            <Users size={48} className="text-indigo-200" />
                        </div>
                    </div>
                </div>

                <div className="bg-white rounded-b-lg shadow-lg">
                    {/* Import Section */}
                    {!classList && (
                        <div className="p-8">
                            <div className="text-center mb-8">
                                <FileSpreadsheet
                                    size={64}
                                    className="mx-auto mb-4 text-gray-400"
                                />
                                <h2 className="text-2xl font-bold text-gray-800 mb-2">
                                    Import Class List
                                </h2>
                                <p className="text-gray-600">
                                    Upload your institutional Excel file to get started
                                </p>
                            </div>
                            <div className="max-w-lg mx-auto">
                                <div
                                    className="border-2 border-dashed border-gray-300 rounded-lg p-8 text-center hover:border-indigo-400 transition-colors cursor-pointer"
                                    onClick={() => fileInputRef.current?.click()}
                                >
                                    <Upload
                                        size={48}
                                        className="mx-auto mb-4 text-gray-400"
                                    />
                                    <p className="text-lg font-medium mb-2">
                                        {loading
                                            ? 'Processing...'
                                            : 'Drop Excel file here or click to browse'}
                                    </p>
                                    <p className="text-sm text-gray-500">
                                        Supports .xls and .xlsx files
                                    </p>
                                    <input
                                        ref={fileInputRef}
                                        type="file"
                                        accept=".xls,.xlsx"
                                        onChange={e =>
                                            handleExcelImport(e.target.files[0])
                                        }
                                        className="hidden"
                                        disabled={loading}
                                    />
                                </div>
                                {importResult && !importResult.success && (
                                    <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                                        <div className="flex items-center gap-2 text-red-800">
                                            <AlertCircle size={20} />
                                            <span className="font-medium">Import Failed</span>
                                        </div>
                                        <p className="mt-1 text-red-700">
                                            {importResult.error}
                                        </p>
                                    </div>
                                )}
                            </div>
                        </div>
                    )}

                    {/* Management Dashboard */}
                    {classList && (
                        <div className="p-6">
                            {/* Overview Cards */}
                            <div className="grid grid-cols-1 lg:grid-cols-3 gap-6 mb-8">
                                {/* Course Info */}
                                <div className="bg-gradient-to-r from-blue-50 to-indigo-50 border border-blue-200 rounded-lg p-6">
                                    <div className="flex items-center gap-3 mb-4">
                                        <BookOpen className="text-blue-600" size={24} />
                                        <h3 className="text-lg font-semibold text-blue-800">
                                            Course Information
                                        </h3>
                                    </div>
                                    <div className="space-y-2 text-sm">
                                        <div>
                                            <strong>Code:</strong>{' '}
                                            {classList.courseMetadata?.courseCode || 'N/A'}
                                        </div>
                                        <div>
                                            <strong>Name:</strong>{' '}
                                            {classList.courseMetadata?.courseName || 'N/A'}
                                        </div>
                                        <div>
                                            <strong>Section:</strong>{' '}
                                            {classList.courseMetadata?.section || 'N/A'}
                                        </div>
                                        <div>
                                            <strong>Campus:</strong>{' '}
                                            {classList.courseMetadata?.campus || 'N/A'}
                                        </div>
                                        <div>
                                            <strong>Term:</strong>{' '}
                                            {classList.courseMetadata?.term || 'N/A'}
                                        </div>
                                    </div>
                                </div>
                                {/* Progress */}
                                <div className="bg-gradient-to-r from-green-50 to-emerald-50 border border-green-200 rounded-lg p-6">
                                    <div className="flex items-center gap-3 mb-4">
                                        <BarChart3 className="text-green-600" size={24} />
                                        <h3 className="text-lg font-semibold text-green-800">
                                            Grading Progress
                                        </h3>
                                    </div>
                                    <div className="space-y-3">
                                        <div className="flex justify-between items-center">
                                            <span className="text-sm font-medium">
                                                Completed
                                            </span>
                                            <span className="text-lg font-bold text-green-600">
                                                {progress.completed}/{progress.total}
                                            </span>
                                        </div>
                                        <div className="w-full bg-gray-200 rounded-full h-3">
                                            <div
                                                className="bg-green-500 h-3 rounded-full transition-all duration-500"
                                                style={{
                                                    width: `${progress.percentage}%`,
                                                }}
                                            />
                                        </div>
                                        <div className="text-center text-sm text-green-600 font-medium">
                                            {progress.percentage}% Complete
                                        </div>
                                    </div>
                                </div>
                                {/* Session Status */}
                                <div className="bg-gradient-to-r from-purple-50 to-violet-50 border border-purple-200 rounded-lg p-6">
                                    <div className="flex items-center gap-3 mb-4">
                                        <Clock className="text-purple-600" size={24} />
                                        <h3 className="text-lg font-semibold text-purple-800">
                                            Session Status
                                        </h3>
                                    </div>
                                    <div className="space-y-3">
                                        <div className="flex items-center gap-2">
                                            {gradingSession.active ? (
                                                <>
                                                    <div className="w-3 h-3 bg-green-500 rounded-full animate-pulse" />
                                                    <span className="text-sm font-medium text-green-600">
                                                        Active Session
                                                    </span>
                                                </>
                                            ) : (
                                                <>
                                                    <div className="w-3 h-3 bg-gray-400 rounded-full" />
                                                    <span className="text-sm font-medium text-gray-600">
                                                        No Active Session
                                                    </span>
                                                </>
                                            )}
                                        </div>
                                        {gradingSession.active && gradingSession.currentStudent && (
                                            <div className="text-sm">
                                                <div>
                                                    <strong>Current:</strong>{' '}
                                                    {gradingSession.currentStudent.name}
                                                </div>
                                                <div>
                                                    <strong>ID:</strong>{' '}
                                                    {gradingSession.currentStudent.id}
                                                </div>
                                            </div>
                                        )}
                                    </div>
                                </div>
                            </div>

                            {/* Action Buttons */}
                            <div className="flex flex-wrap gap-4 mb-8">
                                {!gradingSession.active ? (
                                    <button
                                        onClick={startGradingSession}
                                        disabled={!sharedRubric}
                                        className="
                                            inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium shadow-md
                                            bg-green-600 hover:bg-green-700 text-white
                                            transition-colors duration-150
                                            disabled:bg-gray-400 disabled:text-gray-200 disabled:shadow-none disabled:cursor-not-allowed
                                        "
                                    >
                                        <PlayCircle size={20} />
                                        Start Batch Grading
                                    </button>
                                ) : (
                                    <>
                                        <button
                                            onClick={() =>
                                                setGradingSession(prev => ({
                                                    ...prev,
                                                    active: false,
                                                }))
                                            }
                                            className="
                                                inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium shadow-md
                                                bg-orange-600 hover:bg-orange-700 text-white
                                                transition-colors duration-150
                                            "
                                        >
                                            <Pause size={20} />
                                            Pause Session
                                        </button>
                                        <button
                                            onClick={previousStudent}
                                            disabled={gradingSession.currentStudentIndex === 0}
                                            className="
                                                inline-flex items-center gap-2 px-4 py-3 rounded-lg font-medium shadow-md
                                                bg-blue-500 hover:bg-blue-700 text-white
                                                transition-colors duration-150
                                                disabled:bg-gray-400 disabled:text-gray-200 disabled:shadow-none disabled:cursor-not-allowed
                                            "
                                        >
                                            &larr; Previous
                                        </button>
                                        <button
                                            onClick={nextStudent}
                                            disabled={
                                                gradingSession.currentStudentIndex >=
                                                classList.students.length - 1
                                            }
                                            className="
                                                inline-flex items-center gap-2 px-4 py-3 rounded-lg font-medium shadow-md
                                                bg-blue-500 hover:bg-blue-700 text-white
                                                transition-colors duration-150
                                                disabled:bg-gray-400 disabled:text-gray-200 disabled:shadow-none disabled:cursor-not-allowed
                                            "
                                        >
                                            Next &rarr;
                                        </button>
                                    </>
                                )}
                                <button
                                    onClick={exportClassGrades}
                                    disabled={!classList}
                                    className="
                                        inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium shadow-md
                                        bg-blue-500 hover:bg-blue-700 text-white
                                        transition-colors duration-150
                                        disabled:bg-gray-400 disabled:text-gray-200 disabled:shadow-none disabled:cursor-not-allowed
                                    "
                                >
                                    <Download size={20} />
                                    Export Grades
                                </button>
                                <button
                                    onClick={() => {
                                        setClassList(null);
                                        setImportResult(null);
                                        setGradingSession({
                                            active: false,
                                            startTime: null,
                                            gradedStudents: [],
                                            totalStudents: 0,
                                            currentStudent: null,
                                            currentStudentIndex: 0,
                                        });
                                        setCurrentStudent(null);
                                    }}
                                    disabled={!classList}
                                    className="
                                        inline-flex items-center gap-2 px-6 py-3 rounded-lg font-medium shadow-md
                                        bg-gray-600 hover:bg-gray-700 text-white
                                        transition-colors duration-150
                                        disabled:bg-gray-400 disabled:text-gray-200 disabled:shadow-none disabled:cursor-not-allowed
                                    "
                                >
                                    <RefreshCw size={20} />
                                    New Import
                                </button>
                            </div>

                            {/* Student List Table */}
                            <div className="bg-white border border-gray-200 rounded-lg overflow-hidden">
                                <div className="bg-gray-50 px-6 py-4 border-b border-gray-200">
                                    <h3 className="text-lg font-semibold text-gray-800 flex items-center gap-2">
                                        <Users size={20} />
                                        Student List ({classList.students.length})
                                    </h3>
                                </div>
                                <div className="overflow-x-auto">
                                    <table className="w-full">
                                        <thead className="bg-gray-50">
                                            <tr>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    #
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Status
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Student ID
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Name
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Email
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Program
                                                </th>
                                                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                                    Actions
                                                </th>
                                            </tr>
                                        </thead>
                                        <tbody className="bg-white divide-y divide-gray-200">
                                            {classList.students.map((student, index) => {
                                                // 1️⃣ Grab this student’s progress entry
                                                const progress = classList.gradingProgress[index];
                                                // 2️⃣ Check if this is the “current” student in the session
                                                const isCurrentStudent =
                                                    gradingSession.active &&
                                                    index === gradingSession.currentStudentIndex;
                                                // 3️⃣ ← **THIS** is what you were missing:
                                                //    define whether we have a draft for this student
                                                const studentHasDraft = hasDraft(student.id);

                                                return (
                                                    <tr
                                                        key={student.id}
                                                        className={`${isCurrentStudent
                                                                ? 'bg-blue-50 border-l-4 border-blue-500'
                                                                : 'hover:bg-gray-50'
                                                            } transition-colors`}
                                                    >
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                            {index + 1}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap">
                                                            <div className="flex items-center gap-2">
                                                                {progress?.status?.startsWith('completed') ? (
                                                                    <>
                                                                        <CheckCircle size={16} className="text-green-500" />
                                                                        <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full">
                                                                            Completed
                                                                        </span>
                                                                    </>
                                                                ) : progress?.status === 'in-progress' ? (
                                                                    <>
                                                                        <Clock size={16} className="text-yellow-500" />
                                                                        <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full">
                                                                            In Progress
                                                                        </span>
                                                                    </>
                                                                ) : studentHasDraft ? (
                                                                    <>
                                                                        <FileText size={16} className="text-blue-500" />
                                                                        <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full">
                                                                            Draft Saved
                                                                        </span>
                                                                    </>
                                                                ) : (
                                                                    <>
                                                                        <div className="w-4 h-4 border-2 border-gray-300 rounded-full" />
                                                                        <span className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                                                                            Pending
                                                                        </span>
                                                                    </>
                                                                )}
                                                                {isCurrentStudent && (
                                                                    <span className="text-xs bg-blue-100 text-blue-800 px-2 py-1 rounded-full font-medium">
                                                                        Current
                                                                    </span>
                                                                )}
                                                            </div>
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                                            {student.id}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                            {student.name}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                            {student.email}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                            {student.program}
                                                        </td>
                                                        <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                            {/*
    If this student’s status starts with “completed_” (i.e. was finalized),
    show a disabled “Completed” button in green.
  */}
                                                            {progress?.status?.startsWith('completed') ? (
                                                                <button
                                                                    disabled
                                                                    className="
        px-3 py-1 rounded text-sm font-medium
        bg-green-200 text-green-800 cursor-default
      "
                                                                >
                                                                    Completed
                                                                </button>

                                                            ) : studentHasDraft ? (
                                                                /* If there’s a draft, show “Continue Draft” */
                                                                <button
                                                                    onClick={() => {
                                                                        setCurrentStudent(student);
                                                                        const draft = loadDraft(student.id);
                                                                        if (draft) setGradingData(draft);
                                                                        setActiveTab('grading-tool');
                                                                    }}
                                                                    className="
        px-3 py-1 rounded text-sm font-medium
        text-white bg-blue-500 hover:bg-blue-700
        focus:ring-2 focus:ring-blue-500
        transition-colors duration-150
      "
                                                                >
                                                                    Continue Draft
                                                                </button>

                                                            ) : (
                                                                /* Otherwise, show the regular “Grade” button */
                                                                <button
                                                                    onClick={() => {
                                                                        setCurrentStudent(student);
                                                                        clearGradingFormData();
                                                                        setActiveTab('grading-tool');
                                                                    }}
                                                                    className="
        px-3 py-1 rounded text-sm font-medium
        text-white bg-indigo-600 hover:bg-indigo-500
        focus:ring-2 focus:ring-indigo-500
        transition-colors duration-150
      "
                                                                >
                                                                    Grade
                                                                </button>
                                                            )}
                                                        </td>

                                                    </tr>
                                                );
                                            })}
                                        </tbody>

                                    </table>
                                </div>
                            </div>

                            {/* Import Summary */}
                            <div className="mt-8 bg-gray-50 rounded-lg p-6">
                                <h3 className="text-lg font-semibold text-gray-800 mb-4">
                                    Import Summary
                                </h3>
                                <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                    <div>
                                        <span className="text-gray-600">File:</span>
                                        <div className="font-medium">
                                            {classList.fileName}
                                        </div>
                                    </div>
                                    <div>
                                        <span className="text-gray-600">Import Date:</span>
                                        <div className="font-medium">
                                            {new Date(
                                                classList.importTime
                                            ).toLocaleDateString()}
                                        </div>
                                    </div>
                                    <div>
                                        <span className="text-gray-600">Data Quality:</span>
                                        <div className="font-medium">
                                            {classList.validation?.validationScore || 0}%
                                        </div>
                                    </div>
                                    <div>
                                        <span className="text-gray-600">Total Students:</span>
                                        <div className="font-medium">
                                            {classList.students.length}
                                        </div>
                                    </div>
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ClassListManager;
