// src/components/ClassListManager.js
import React, { useState, useRef } from 'react';
import { parseExcelFile, validateStudentData } from '../utils/excelParser';
import { useAssessment } from './SharedContext';
import {
    Upload, Users, GraduationCap, FileSpreadsheet, CheckCircle, AlertCircle,
    ArrowRight, Edit3, Download, Trash2, RefreshCw, BookOpen, UserCheck,
    Clock, BarChart3, Target, Award, FileText, PlayCircle, Pause, Unlock
} from 'lucide-react';

const ClassListManager = () => {
    // Pull classList, currentStudent & helpers from context - UPDATED with new functions
    const {
        activeTab,
        setActiveTab,
        sharedRubric,
        gradingData,
        setGradingData,
        updateStudentInfo,
        updateCourseInfo,
        updateAssignmentInfo,
        clearGradingFormData,
        classList,
        setClassList,
        setCurrentStudent,
        gradingSession,
        setGradingSession,
        initializeGradingSession,
        hasDraft,
        loadDraft,
        // NEW: Add the draft/final grade functions
        getGradeStatus,
        saveFinalGrade,
        finalGrades,
        drafts,
        setDrafts,
        setFinalGrades
    } = useAssessment();

    const [currentStudentIndex, setCurrentStudentIndex] = useState(0);
    const [batchOperations, setBatchOperations] = useState({
        selectedStudents: [],
        exportQueue: [],
        isProcessing: false
    });
    const [loading, setLoading] = useState(false);
    const [importResult, setImportResult] = useState(null);

    const fileInputRef = useRef(null);

    // NEW: Function to get status display component
    const getStatusDisplay = (progress, studentId) => {
        const gradeStatus = getGradeStatus(studentId);

        if (gradeStatus === 'final') {
            return (
                <div className="flex items-center gap-2">
                    <CheckCircle className="w-4 h-4 text-green-600" />
                    <span className="text-green-800 font-medium text-sm">Final</span>
                </div>
            );
        } else if (gradeStatus === 'draft') {
            return (
                <div className="flex items-center gap-2">
                    <Clock className="w-4 h-4 text-yellow-600" />
                    <span className="text-yellow-800 font-medium text-sm">Draft</span>
                </div>
            );
        } else if (progress?.status === 'in-progress') {
            return (
                <div className="flex items-center gap-2">
                    <div className="w-4 h-4 border-2 border-blue-600 border-t-transparent rounded-full animate-spin"></div>
                    <span className="text-blue-800 font-medium text-sm">In Progress</span>
                </div>
            );
        } else {
            return (
                <div className="flex items-center gap-2">
                    <div className="w-4 h-4 bg-gray-300 rounded-full"></div>
                    <span className="text-gray-600 text-sm">Not Started</span>
                </div>
            );
        }
    };

    // NEW: Function to finalize a draft grade
    const finalizeGrade = (studentId) => {
        const draftData = drafts[studentId];
        if (draftData) {
            saveFinalGrade(studentId, draftData);

            // Update class list progress
            const studentIndex = classList.students.findIndex(s => s.id === studentId);
            if (studentIndex >= 0) {
                const updatedProgress = [...classList.gradingProgress];
                updatedProgress[studentIndex] = {
                    ...updatedProgress[studentIndex],
                    status: 'completed_final',
                    lastModified: new Date().toISOString(),
                    gradeType: 'final'
                };
                setClassList(prev => ({
                    ...prev,
                    gradingProgress: updatedProgress
                }));
            }

            // Show success message
            alert(`Grade for ${classList.students.find(s => s.id === studentId)?.name} has been finalized.`);
        }
    };

    // NEW: Function to unlock a final grade (convert back to draft)
    const unlockGrade = (studentId) => {
        const confirmed = confirm(`Are you sure you want to unlock the final grade for ${classList.students.find(s => s.id === studentId)?.name}? This will convert it back to a draft.`);
        if (!confirmed) return;

        const finalData = finalGrades[studentId];
        if (finalData) {
            // Move from final back to draft
            setDrafts(prev => ({ ...prev, [studentId]: finalData }));
            setFinalGrades(prev => {
                const updated = { ...prev };
                delete updated[studentId];
                return updated;
            });

            // Update class list progress
            const studentIndex = classList.students.findIndex(s => s.id === studentId);
            if (studentIndex >= 0) {
                const updatedProgress = [...classList.gradingProgress];
                updatedProgress[studentIndex] = {
                    ...updatedProgress[studentIndex],
                    status: 'completed_draft',
                    lastModified: new Date().toISOString(),
                    gradeType: 'draft'
                };
                setClassList(prev => ({
                    ...prev,
                    gradingProgress: updatedProgress
                }));
            }

            alert(`Grade for ${classList.students.find(s => s.id === studentId)?.name} has been unlocked and converted to draft.`);
        }
    };

    // Handle Excel file import
    const handleExcelImport = async (file) => {
        if (!file) return;
        setLoading(true);
        setImportResult(null);

        try {
            const result = await parseExcelFile(file);
            if (result.success) {
                const validation = validateStudentData(result.students);
                const classData = {
                    ...result,
                    validation,
                    fileName: file.name,
                    fileSize: file.size,
                    importTime: new Date().toISOString(),
                    gradingProgress: result.students.map(student => ({
                        studentId: student.id,
                        studentName: student.name,
                        status: 'pending', // pending, in-progress, completed
                        grade: null,
                        lastModified: null,
                        timeSpent: 0
                    }))
                };

                setClassList(classData);
                setImportResult(result);

                // Auto-populate course information from Excel
                if (result.courseMetadata) {
                    updateCourseInfo({
                        code: result.courseMetadata.courseCode || '',
                        name: result.courseMetadata.courseName || '',
                        instructor: result.courseMetadata.professors || '',
                        term: result.courseMetadata.term || ''
                    });
                }
            } else {
                setImportResult(result);
            }
        } catch (error) {
            setImportResult({
                success: false,
                error: error.message,
                students: []
            });
        }
        setLoading(false);
    };

    // Start batch grading session
    const startGradingSession = () => {
        if (!classList || !classList.students.length) return;

        // Use the new initialization function
        const success = initializeGradingSession(classList);
        if (success) {
            // Switch to grading tool
            setActiveTab('grading-tool');
        }
    };

    // Load student information into grading form
    const loadStudentForGrading = (student) => {
        updateStudentInfo({
            name: student.name,
            id: student.id,
            email: student.email
        });

        // Update assignment info if we have shared rubric
        if (sharedRubric) {
            updateAssignmentInfo({
                name: sharedRubric.assignmentInfo?.title || '',
                maxPoints: sharedRubric.assignmentInfo?.totalPoints || 100
            });
        }

        // Switch to grading tool
        setActiveTab('grading-tool');
    };

    // Navigate to next student
    const nextStudent = () => {
        if (!classList || currentStudentIndex >= classList.students.length - 1) return;

        // Mark current student as completed
        const updatedProgress = [...classList.gradingProgress];
        updatedProgress[currentStudentIndex] = {
            ...updatedProgress[currentStudentIndex],
            status: 'completed',
            lastModified: new Date().toISOString()
        };

        setClassList(prev => ({
            ...prev,
            gradingProgress: updatedProgress
        }));

        // Move to next student
        const nextIndex = currentStudentIndex + 1;
        setCurrentStudentIndex(nextIndex);

        if (nextIndex < classList.students.length) {
            loadStudentForGrading(classList.students[nextIndex]);

            // Update grading session if active
            if (gradingSession.active) {
                setGradingSession(prev => ({
                    ...prev,
                    currentStudentIndex: nextIndex,
                    currentStudent: classList.students[nextIndex]
                }));
                setCurrentStudent(classList.students[nextIndex]);
            }
        }
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 p-4">
            <div className="max-w-7xl mx-auto">
                <div className="bg-white rounded-2xl shadow-xl overflow-hidden">
                    {/* Header */}
                    <div className="bg-gradient-to-r from-indigo-600 to-blue-600 text-white p-8">
                        <div className="flex items-center justify-between">
                            <div>
                                <h1 className="text-3xl font-bold mb-2">Class List Manager</h1>
                                <p className="text-blue-100">Import and manage student class lists for batch grading</p>
                            </div>
                            <Users className="w-16 h-16 text-blue-200" />
                        </div>
                    </div>

                    {/* Content */}
                    <div className="p-8">
                        {!classList ? (
                            /* Import Section */
                            <div className="text-center max-w-2xl mx-auto">
                                <div className="mb-8">
                                    <FileSpreadsheet className="w-24 h-24 text-indigo-500 mx-auto mb-4" />
                                    <h2 className="text-2xl font-bold text-gray-800 mb-4">Import Class List</h2>
                                    <p className="text-gray-600 mb-8">
                                        Upload an Excel file containing your student roster. Supported formats include .xlsx and .xls files
                                        with columns for student ID, name, email, and program.
                                    </p>
                                </div>

                                <div className="bg-gray-50 border-2 border-dashed border-gray-300 rounded-xl p-8 transition-colors hover:border-indigo-400 hover:bg-indigo-50">
                                    <input
                                        ref={fileInputRef}
                                        type="file"
                                        accept=".xlsx,.xls"
                                        onChange={(e) => handleExcelImport(e.target.files[0])}
                                        className="hidden"
                                    />
                                    <button
                                        onClick={() => fileInputRef.current?.click()}
                                        disabled={loading}
                                        className="inline-flex items-center gap-3 bg-indigo-600 text-white px-8 py-4 rounded-lg font-semibold text-lg hover:bg-indigo-700 disabled:opacity-50 disabled:cursor-not-allowed transition-colors"
                                    >
                                        {loading ? (
                                            <>
                                                <RefreshCw className="w-6 h-6 animate-spin" />
                                                Processing...
                                            </>
                                        ) : (
                                            <>
                                                <Upload className="w-6 h-6" />
                                                Upload Excel File
                                            </>
                                        )}
                                    </button>
                                    <p className="text-sm text-gray-500 mt-4">
                                        Drag and drop your Excel file here or click to browse
                                    </p>
                                </div>

                                {/* Import Results */}
                                {importResult && (
                                    <div className="mt-8">
                                        {importResult.success ? (
                                            <div className="bg-green-50 border border-green-200 rounded-lg p-6">
                                                <div className="flex items-center gap-3 mb-4">
                                                    <CheckCircle className="w-8 h-8 text-green-600" />
                                                    <div>
                                                        <h3 className="text-lg font-semibold text-green-800">Import Successful!</h3>
                                                        <p className="text-green-600">
                                                            Successfully imported {importResult.students.length} students
                                                        </p>
                                                    </div>
                                                </div>
                                                <button
                                                    onClick={() => setImportResult(null)}
                                                    className="bg-green-600 text-white px-6 py-2 rounded-lg hover:bg-green-700 transition-colors"
                                                >
                                                    Continue to Class Management
                                                </button>
                                            </div>
                                        ) : (
                                            <div className="bg-red-50 border border-red-200 rounded-lg p-6">
                                                <div className="flex items-center gap-3 mb-4">
                                                    <AlertCircle className="w-8 h-8 text-red-600" />
                                                    <div>
                                                        <h3 className="text-lg font-semibold text-red-800">Import Failed</h3>
                                                        <p className="text-red-600">{importResult.error}</p>
                                                    </div>
                                                </div>
                                                <button
                                                    onClick={() => setImportResult(null)}
                                                    className="bg-red-600 text-white px-6 py-2 rounded-lg hover:bg-red-700 transition-colors"
                                                >
                                                    Try Again
                                                </button>
                                            </div>
                                        )}
                                    </div>
                                )}
                            </div>
                        ) : (
                            /* Class Management Section */
                            <div>
                                {/* NEW: Summary Cards */}
                                <div className="mb-6 grid grid-cols-1 md:grid-cols-4 gap-4">
                                    <div className="bg-gray-50 rounded-lg p-4">
                                        <div className="flex items-center">
                                            <div className="w-8 h-8 bg-gray-300 rounded-full mr-3"></div>
                                            <div>
                                                <p className="text-sm font-medium text-gray-900">Not Started</p>
                                                <p className="text-2xl font-bold text-gray-700">
                                                    {classList.students.filter(s => getGradeStatus(s.id) === 'not_started').length}
                                                </p>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="bg-yellow-50 rounded-lg p-4">
                                        <div className="flex items-center">
                                            <Clock className="w-8 h-8 text-yellow-600 mr-3" />
                                            <div>
                                                <p className="text-sm font-medium text-yellow-900">Drafts</p>
                                                <p className="text-2xl font-bold text-yellow-700">
                                                    {classList.students.filter(s => getGradeStatus(s.id) === 'draft').length}
                                                </p>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="bg-green-50 rounded-lg p-4">
                                        <div className="flex items-center">
                                            <CheckCircle className="w-8 h-8 text-green-600 mr-3" />
                                            <div>
                                                <p className="text-sm font-medium text-green-900">Finalized</p>
                                                <p className="text-2xl font-bold text-green-700">
                                                    {classList.students.filter(s => getGradeStatus(s.id) === 'final').length}
                                                </p>
                                            </div>
                                        </div>
                                    </div>

                                    <div className="bg-blue-50 rounded-lg p-4">
                                        <div className="flex items-center">
                                            <Users className="w-8 h-8 text-blue-600 mr-3" />
                                            <div>
                                                <p className="text-sm font-medium text-blue-900">Total Students</p>
                                                <p className="text-2xl font-bold text-blue-700">
                                                    {classList.students.length}
                                                </p>
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* NEW: Bulk Actions */}
                                <div className="mb-6 bg-white border border-gray-200 rounded-lg p-4">
                                    <div className="flex items-center justify-between">
                                        <h3 className="text-lg font-medium text-gray-900">Bulk Actions</h3>
                                        <div className="flex gap-2">
                                            <button
                                                onClick={() => {
                                                    const draftStudentIds = classList.students
                                                        .filter(s => getGradeStatus(s.id) === 'draft')
                                                        .map(s => s.id);

                                                    if (draftStudentIds.length === 0) {
                                                        alert('No draft grades to finalize.');
                                                        return;
                                                    }

                                                    const confirmed = confirm(`Are you sure you want to finalize ${draftStudentIds.length} draft grades?`);
                                                    if (confirmed) {
                                                        draftStudentIds.forEach(id => finalizeGrade(id));
                                                    }
                                                }}
                                                className="inline-flex items-center px-3 py-2 border border-transparent text-sm leading-4 font-medium rounded-md text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:opacity-50 disabled:cursor-not-allowed"
                                                disabled={classList.students.filter(s => getGradeStatus(s.id) === 'draft').length === 0}
                                            >
                                                <CheckCircle size={16} className="mr-1" />
                                                Finalize All Drafts
                                            </button>

                                            <button
                                                onClick={() => {
                                                    const completed = classList.students.filter(s =>
                                                        getGradeStatus(s.id) === 'final' || getGradeStatus(s.id) === 'draft'
                                                    ).length;
                                                    const total = classList.students.length;
                                                    const percentage = Math.round((completed / total) * 100);

                                                    alert(`Grading Progress: ${completed}/${total} students (${percentage}%)\n\nDrafts: ${classList.students.filter(s => getGradeStatus(s.id) === 'draft').length}\nFinalized: ${classList.students.filter(s => getGradeStatus(s.id) === 'final').length}\nNot Started: ${classList.students.filter(s => getGradeStatus(s.id) === 'not_started').length}`);
                                                }}
                                                className="inline-flex items-center px-3 py-2 border border-gray-300 text-sm leading-4 font-medium rounded-md text-gray-700 bg-white hover:bg-gray-50 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-indigo-500"
                                            >
                                                <AlertCircle size={16} className="mr-1" />
                                                View Progress Report
                                            </button>
                                        </div>
                                    </div>
                                </div>

                                {/* Class Actions Header */}
                                <div className="flex items-center justify-between mb-6">
                                    <div>
                                        <h2 className="text-2xl font-bold text-gray-800">
                                            {classList.fileName}
                                        </h2>
                                        <p className="text-gray-600">
                                            {classList.students.length} students • Imported {new Date(classList.importTime).toLocaleDateString()}
                                        </p>
                                    </div>
                                    <div className="flex gap-3">
                                        {sharedRubric && (
                                            <div className="flex items-center gap-2 bg-green-50 text-green-700 px-3 py-2 rounded-lg">
                                                <BookOpen className="w-4 h-4" />
                                                <span className="text-sm font-medium">Rubric Loaded</span>
                                            </div>
                                        )}
                                        <button
                                            onClick={startGradingSession}
                                            className="flex items-center gap-2 bg-green-600 text-white px-6 py-3 rounded-lg font-semibold hover:bg-green-700 transition-colors"
                                        >
                                            <PlayCircle className="w-5 h-5" />
                                            Start Batch Grading
                                        </button>
                                        <button
                                            onClick={() => {
                                                setClassList(null);
                                                setImportResult(null);
                                            }}
                                            className="flex items-center gap-2 bg-gray-500 text-white px-4 py-3 rounded-lg hover:bg-gray-600 transition-colors"
                                        >
                                            <Upload className="w-5 h-5" />
                                            Import New List
                                        </button>
                                    </div>
                                </div>

                                {/* Active Session Indicator */}
                                {gradingSession.active && (
                                    <div className="mb-6 bg-blue-50 border border-blue-200 rounded-lg p-4">
                                        <div className="flex items-center justify-between">
                                            <div className="flex items-center gap-3">
                                                <div className="w-3 h-3 bg-blue-500 rounded-full animate-pulse"></div>
                                                <div>
                                                    <h3 className="font-semibold text-blue-900">Active Grading Session</h3>
                                                    <p className="text-blue-700 text-sm">
                                                        Currently grading: {gradingSession.currentStudent?.name}
                                                        ({gradingSession.currentStudentIndex + 1} of {gradingSession.totalStudents})
                                                    </p>
                                                </div>
                                            </div>
                                            <button
                                                onClick={() => setActiveTab('grading-tool')}
                                                className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                                            >
                                                Return to Grading
                                            </button>
                                        </div>
                                    </div>
                                )}

                                {/* Student Table */}
                                <div className="bg-white rounded-lg border border-gray-200 overflow-hidden">
                                    <div className="overflow-x-auto">
                                        <table className="w-full">
                                            <thead className="bg-gray-50">
                                                <tr>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">#</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Status</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Student ID</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Name</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Email</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Program</th>
                                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Actions</th>
                                                </tr>
                                            </thead>
                                            <tbody className="bg-white divide-y divide-gray-200">
                                                {classList.students.map((student, index) => {
                                                    const progress = classList.gradingProgress[index];
                                                    const isCurrentStudent = gradingSession.active && index === gradingSession.currentStudentIndex;
                                                    const studentHasDraft = hasDraft(student.id);
                                                    const gradeStatus = getGradeStatus(student.id);

                                                    return (
                                                        <tr
                                                            key={student.id}
                                                            className={`${isCurrentStudent ? 'bg-blue-50 border-l-4 border-blue-500' :
                                                                gradeStatus === 'final' ? 'bg-green-50' :
                                                                    gradeStatus === 'draft' ? 'bg-yellow-50' :
                                                                        'hover:bg-gray-50'} transition-colors`}
                                                        >
                                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                                {index + 1}
                                                            </td>

                                                            {/* UPDATED STATUS COLUMN */}
                                                            <td className="px-6 py-4 whitespace-nowrap">
                                                                {getStatusDisplay(progress, student.id)}
                                                            </td>

                                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                                {student.id}
                                                            </td>
                                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-900">
                                                                {student.name}
                                                                {isCurrentStudent && (
                                                                    <span className="ml-2 inline-flex items-center px-2.5 py-0.5 rounded-full text-xs font-medium bg-blue-100 text-blue-800">
                                                                        Current
                                                                    </span>
                                                                )}
                                                            </td>
                                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                                {student.email}
                                                            </td>
                                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                                {student.program}
                                                            </td>

                                                            {/* UPDATED ACTIONS COLUMN */}
                                                            <td className="px-6 py-4 whitespace-nowrap text-sm">
                                                                <div className="flex items-center gap-2">
                                                                    {/* Main action button */}
                                                                    <button
                                                                        onClick={() => loadStudentForGrading(student)}
                                                                        className={`${gradeStatus === 'final'
                                                                                ? 'text-gray-600 hover:text-gray-800'
                                                                                : 'text-blue-600 hover:text-blue-800'
                                                                            } transition-colors font-medium`}
                                                                    >
                                                                        {gradeStatus === 'final' ? 'View' : 'Edit'}
                                                                    </button>

                                                                    {/* Finalize button for drafts */}
                                                                    {gradeStatus === 'draft' && (
                                                                        <button
                                                                            onClick={() => finalizeGrade(student.id)}
                                                                            className="text-green-600 hover:text-green-800 transition-colors font-medium"
                                                                            title="Mark this grade as final"
                                                                        >
                                                                            Finalize
                                                                        </button>
                                                                    )}

                                                                    {/* Unlock button for final grades */}
                                                                    {gradeStatus === 'final' && (
                                                                        <button
                                                                            onClick={() => unlockGrade(student.id)}
                                                                            className="text-orange-600 hover:text-orange-800 transition-colors font-medium"
                                                                            title="Convert final grade back to draft for editing"
                                                                        >
                                                                            Unlock
                                                                        </button>
                                                                    )}

                                                                    {/* Draft indicator */}
                                                                    {gradeStatus === 'draft' && (
                                                                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-yellow-100 text-yellow-800">
                                                                            Draft Saved
                                                                        </span>
                                                                    )}

                                                                    {/* Final indicator */}
                                                                    {gradeStatus === 'final' && (
                                                                        <span className="inline-flex items-center px-2 py-1 rounded-full text-xs font-medium bg-green-100 text-green-800">
                                                                            Finalized
                                                                        </span>
                                                                    )}
                                                                </div>
                                                            </td>
                                                        </tr>
                                                    );
                                                })}
                                            </tbody>
                                        </table>
                                    </div>
                                </div>

                                {/* Import Summary */}
                                <div className="mt-8 bg-gray-50 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-gray-800 mb-4">Import Summary</h3>
                                    <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                                        <div>
                                            <span className="text-gray-600">File:</span>
                                            <div className="font-medium">{classList.fileName}</div>
                                        </div>
                                        <div>
                                            <span className="text-gray-600">Import Date:</span>
                                            <div className="font-medium">{new Date(classList.importTime).toLocaleDateString()}</div>
                                        </div>
                                        <div>
                                            <span className="text-gray-600">Data Quality:</span>
                                            <div className="font-medium">{classList.validation?.validationScore || 0}%</div>
                                        </div>
                                        <div>
                                            <span className="text-gray-600">Total Students:</span>
                                            <div className="font-medium">{classList.students.length}</div>
                                        </div>
                                    </div>
                                </div>
                            </div>
                        )}
                    </div>
                </div>
            </div>
        </div>
    );
};

export default ClassListManager;
