// Complete TabNavigation.js - Fully Consolidated with ALL Original Features + Late Policy Enhancement
// This version preserves EVERY function and feature from the original while adding late policy support

import React from 'react';
import { useAssessment } from './SharedContext';
import {
    FileText, Users, GraduationCap, HelpCircle, Settings, Database,
    Clock, CheckCircle, AlertTriangle, Sparkles, Edit3, PlayCircle,
    BarChart3, BookOpen, Calculator, Target, Zap, TrendingUp
} from 'lucide-react';

const TabNavigation = () => {
    const {
        activeTab,
        setActiveTab,
        sharedRubric,
        classList,
        gradingSession,
        currentStudent,
        hasDraft,
        getGradeStatus,
        gradingData: persistentFormData,
        finalGrades,
        drafts,
        // NEW: Late policy context
        currentLatePolicy,
        customLatePolicies,
        DEFAULT_LATE_POLICIES
    } = useAssessment();

    // Enhanced tab configuration with late policy awareness
    const tabs = [
        {
            id: 'assignment-prompt-generator',
            name: 'Assignment Generator',
            icon: FileText,
            color: 'blue',
            description: 'Create assignment prompts',
            shortName: 'Assignment'
        },
        {
            id: 'ai-rubric-generator',
            name: 'AI Rubric Generator',
            icon: Sparkles,
            color: 'purple',
            description: 'AI-powered rubric creation',
            shortName: 'AI Rubric'
        },
        {
            id: 'rubric-creator',
            name: 'Rubric Creator',
            icon: Edit3,
            color: 'indigo',
            description: 'Build custom rubrics',
            shortName: 'Rubric'
        },
        {
            id: 'class-manager',
            name: 'Class Manager',
            icon: Users,
            color: 'green',
            description: 'Manage student lists',
            shortName: 'Class'
        },
        {
            id: 'grading-tool',
            name: 'Grading Tool',
            icon: GraduationCap,
            color: 'orange',
            description: 'Grade with late policies',
            shortName: 'Grading'
        },
        {
            id: 'policy-manager',
            name: 'Policy Manager',
            icon: Settings,
            color: 'teal',
            description: 'Configure late policies',
            shortName: 'Policies'
        },
        {
            id: 'help',
            name: 'Help',
            icon: HelpCircle,
            color: 'gray',
            description: 'User guide',
            shortName: 'Help'
        }
    ];

    // ORIGINAL: Active styles for tabs preserved
    const activeStyles = {
        'assignment-prompt-generator': 'border-blue-500 text-blue-700 bg-blue-50',
        'ai-rubric-generator': 'border-purple-500 text-purple-700 bg-purple-50',
        'rubric-creator': 'border-indigo-500 text-indigo-700 bg-indigo-50',
        'class-manager': 'border-green-500 text-green-700 bg-green-50',
        'grading-tool': 'border-orange-500 text-orange-700 bg-orange-50',
        'policy-manager': 'border-teal-500 text-teal-700 bg-teal-50',
        'help': 'border-gray-500 text-gray-700 bg-gray-50',
    };

    // ORIGINAL: Helper functions for status indicators preserved
    const hasRubricData = Boolean(sharedRubric && sharedRubric.assignmentInfo?.title);
    const hasFormData = Boolean(persistentFormData && (
        persistentFormData.student?.name ||
        persistentFormData.course?.name ||
        persistentFormData.assignment?.name ||
        (persistentFormData.attachments && persistentFormData.attachments.length > 0) ||
        (persistentFormData.videoLinks && persistentFormData.videoLinks.length > 0)
    ));
    const hasClassListData = Boolean(classList && classList.students?.length > 0);
    const hasActiveSession = Boolean(gradingSession?.active);

    // NEW: Add grading policy status checks
    const availablePolicies = [...(customLatePolicies || []), ...Object.values(DEFAULT_LATE_POLICIES || {})];
    const hasPolicyData = availablePolicies && availablePolicies.length > 0;
    const currentProgramType = classList?.courseMetadata?.programType || 'degree';
    const activePolicyName = (currentLatePolicy || DEFAULT_LATE_POLICIES?.institutional)?.name || 'Institutional Standard';

    // ORIGINAL: Calculate grading progress preserved
    const getGradingProgress = () => {
        if (!classList || !classList.students) return { completed: 0, total: 0, percentage: 0 };

        const total = classList.students.length;
        let completed = 0;

        classList.students.forEach(student => {
            const status = getGradeStatus ? getGradeStatus(student.id) : 'not_started';
            if (status === 'final') {
                completed++;
            }
        });

        const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;
        return { completed, total, percentage };
    };

    const progress = getGradingProgress();

    // NEW: Calculate policy statistics
    const getPolicyStats = () => {
        const customCount = customLatePolicies ? customLatePolicies.length : 0;
        const defaultCount = DEFAULT_LATE_POLICIES ? Object.keys(DEFAULT_LATE_POLICIES).length : 0;
        return { customCount, defaultCount, totalCount: customCount + defaultCount };
    };

    const policyStats = getPolicyStats();

    return (
        <div style={{
            borderBottom: '1px solid #e5e7eb',
            backgroundColor: 'white',
            boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
        }}>
            <div style={{ maxWidth: '1400px', margin: '0 auto' }}>
                {/* Enhanced Header with Policy Status */}
                <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '0.75rem 1.5rem',
                    borderBottom: '1px solid #f3f4f6'
                }}>
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                        <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#1f2937' }}>
                            GradingPilot Professional Suite
                        </div>

                        {/* NEW: Active Policy Indicator */}
                        <div style={{
                            display: 'flex',
                            alignItems: 'center',
                            gap: '0.5rem',
                            padding: '0.25rem 0.75rem',
                            backgroundColor: '#f0f9ff',
                            borderRadius: '9999px',
                            border: '1px solid #bae6fd'
                        }}>
                            <Clock size={12} color="#0284c7" />
                            <span style={{ fontSize: '0.75rem', color: '#0369a1', fontWeight: '500' }}>
                                Policy: {activePolicyName}
                            </span>
                        </div>
                    </div>

                    {/* Quick Stats */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem', fontSize: '0.75rem', color: '#6b7280' }}>
                        {hasClassListData && (
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                                <Users size={12} />
                                <span>{classList.students.length} students</span>
                            </div>
                        )}

                        {progress.total > 0 && (
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                                <CheckCircle size={12} />
                                <span>{progress.completed}/{progress.total} graded</span>
                            </div>
                        )}

                        {policyStats.customCount > 0 && (
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.25rem' }}>
                                <Settings size={12} />
                                <span>{policyStats.customCount} custom policies</span>
                            </div>
                        )}
                    </div>
                </div>

                {/* Main Tab Navigation */}
                <nav style={{ display: 'flex', overflowX: 'auto', padding: '0 1.5rem' }} aria-label="Tabs">
                    {tabs.map((tab) => {
                        const Icon = tab.icon;
                        const isActive = activeTab === tab.id;

                        // NEW: Enhanced status indicators with policy awareness
                        let statusIndicator = null;
                        let statusCount = 0;

                        if (tab.id === 'rubric-creator' && hasRubricData) {
                            statusIndicator = <CheckCircle size={12} color="#16a34a" />;
                        } else if (tab.id === 'class-manager' && hasClassListData) {
                            statusIndicator = <CheckCircle size={12} color="#16a34a" />;
                            statusCount = classList.students.length;
                        } else if (tab.id === 'grading-tool' && hasFormData) {
                            statusIndicator = <Edit3 size={12} color="#f59e0b" />;
                        } else if (tab.id === 'grading-tool' && hasActiveSession) {
                            statusIndicator = <PlayCircle size={12} color="#3b82f6" />;
                            statusCount = progress.completed;
                        } else if (tab.id === 'policy-manager' && policyStats.customCount > 0) {
                            statusIndicator = <CheckCircle size={12} color="#16a34a" />;
                            statusCount = policyStats.customCount;
                        }

                        return (
                            <button
                                key={tab.id}
                                onClick={() => setActiveTab(tab.id)}
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.5rem',
                                    padding: '1rem 1.25rem',
                                    border: 'none',
                                    borderBottom: isActive ? '2px solid' : '2px solid transparent',
                                    borderColor: isActive ? `var(--${tab.color}-500, #3b82f6)` : 'transparent',
                                    backgroundColor: isActive ? `var(--${tab.color}-50, #f8fafc)` : 'transparent',
                                    color: isActive ? `var(--${tab.color}-700, #1f2937)` : '#6b7280',
                                    fontSize: '0.875rem',
                                    fontWeight: isActive ? '600' : '500',
                                    cursor: 'pointer',
                                    transition: 'all 0.2s ease-in-out',
                                    minWidth: 'fit-content',
                                    whiteSpace: 'nowrap'
                                }}
                                className={isActive ? activeStyles[tab.id] : ''}
                                aria-current={isActive ? 'page' : undefined}
                                title={tab.description}
                            >
                                <Icon size={16} />

                                <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'flex-start' }}>
                                    <span>{window.innerWidth < 768 ? tab.shortName : tab.name}</span>

                                    {/* Enhanced status display */}
                                    {(statusIndicator || statusCount > 0) && (
                                        <div style={{
                                            display: 'flex',
                                            alignItems: 'center',
                                            gap: '0.25rem',
                                            fontSize: '0.625rem',
                                            color: isActive ? `var(--${tab.color}-600, #4b5563)` : '#9ca3af',
                                            marginTop: '0.125rem'
                                        }}>
                                            {statusIndicator}
                                            {statusCount > 0 && <span>{statusCount}</span>}

                                            {/* NEW: Special indicators for policy-aware tabs */}
                                            {tab.id === 'grading-tool' && currentLatePolicy && (
                                                <span style={{
                                                    fontSize: '0.625rem',
                                                    padding: '0.125rem 0.25rem',
                                                    backgroundColor: isActive ? 'rgba(59, 130, 246, 0.1)' : 'rgba(107, 114, 128, 0.1)',
                                                    borderRadius: '0.25rem'
                                                }}>
                                                    Policy Active
                                                </span>
                                            )}
                                        </div>
                                    )}
                                </div>
                            </button>
                        );
                    })}
                </nav>

                {/* ORIGINAL: Enhanced Quick Actions Bar with Policy Integration */}
                <div style={{
                    display: 'flex',
                    justifyContent: 'space-between',
                    alignItems: 'center',
                    padding: '0.75rem 1.5rem',
                    backgroundColor: '#f9fafb',
                    borderTop: '1px solid #f3f4f6'
                }}>
                    {/* Workflow Status */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <div style={{
                                width: '8px',
                                height: '8px',
                                borderRadius: '50%',
                                backgroundColor: hasRubricData ? '#16a34a' : '#e5e7eb'
                            }}></div>
                            <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>Rubric</span>
                        </div>

                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <div style={{
                                width: '8px',
                                height: '8px',
                                borderRadius: '50%',
                                backgroundColor: hasClassListData ? '#16a34a' : '#e5e7eb'
                            }}></div>
                            <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>Class List</span>
                        </div>

                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <div style={{
                                width: '8px',
                                height: '8px',
                                borderRadius: '50%',
                                backgroundColor: currentLatePolicy ? '#16a34a' : '#f59e0b'
                            }}></div>
                            <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                                Policy {currentLatePolicy ? 'Set' : 'Default'}
                            </span>
                        </div>

                        {hasActiveSession && (
                            <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                                <div style={{
                                    width: '8px',
                                    height: '8px',
                                    borderRadius: '50%',
                                    backgroundColor: '#3b82f6',
                                    animation: 'pulse 2s infinite'
                                }}></div>
                                <span style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                                    Active Session ({progress.completed}/{progress.total})
                                </span>
                            </div>
                        )}
                    </div>

                    {/* Context-Aware Quick Actions */}
                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                        {/* NEW: Policy-specific quick actions */}
                        {activeTab === 'grading-tool' && hasClassListData && (
                            <div style={{ display: 'flex', gap: '0.5rem' }}>
                                <button
                                    onClick={() => setActiveTab('policy-manager')}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '0.25rem',
                                        padding: '0.375rem 0.75rem',
                                        fontSize: '0.75rem',
                                        fontWeight: '500',
                                        color: '#0891b2',
                                        backgroundColor: 'white',
                                        border: '1px solid #06b6d4',
                                        borderRadius: '0.375rem',
                                        cursor: 'pointer',
                                        transition: 'all 0.2s'
                                    }}
                                >
                                    <Settings size={12} />
                                    Manage Policies
                                </button>

                                <button
                                    onClick={() => setActiveTab('class-manager')}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '0.25rem',
                                        padding: '0.375rem 0.75rem',
                                        fontSize: '0.75rem',
                                        fontWeight: '500',
                                        color: '#059669',
                                        backgroundColor: 'white',
                                        border: '1px solid #10b981',
                                        borderRadius: '0.375rem',
                                        cursor: 'pointer'
                                    }}
                                >
                                    <Users size={12} />
                                    Manage Class
                                </button>
                            </div>
                        )}

                        {/* ORIGINAL: Classic quick actions preserved */}
                        {activeTab === 'assignment-prompt-generator' && (
                            <button
                                onClick={() => setActiveTab('ai-rubric-generator')}
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.25rem',
                                    padding: '0.375rem 0.75rem',
                                    fontSize: '0.75rem',
                                    fontWeight: '500',
                                    color: '#7c3aed',
                                    backgroundColor: 'white',
                                    border: '1px solid #8b5cf6',
                                    borderRadius: '0.375rem',
                                    cursor: 'pointer'
                                }}
                            >
                                <Sparkles size={12} />
                                Generate Rubric
                            </button>
                        )}

                        {activeTab === 'ai-rubric-generator' && hasRubricData && (
                            <button
                                onClick={() => setActiveTab('class-manager')}
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.25rem',
                                    padding: '0.375rem 0.75rem',
                                    fontSize: '0.75rem',
                                    fontWeight: '500',
                                    color: '#059669',
                                    backgroundColor: 'white',
                                    border: '1px solid #10b981',
                                    borderRadius: '0.375rem',
                                    cursor: 'pointer'
                                }}
                            >
                                <Users size={12} />
                                Import Class
                            </button>
                        )}

                        {activeTab === 'rubric-creator' && hasRubricData && (
                            <button
                                onClick={() => setActiveTab('class-manager')}
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.25rem',
                                    padding: '0.375rem 0.75rem',
                                    fontSize: '0.75rem',
                                    fontWeight: '500',
                                    color: '#059669',
                                    backgroundColor: 'white',
                                    border: '1px solid #10b981',
                                    borderRadius: '0.375rem',
                                    cursor: 'pointer'
                                }}
                            >
                                <Users size={12} />
                                Import Class
                            </button>
                        )}

                        {activeTab === 'class-manager' && hasClassListData && hasRubricData && (
                            <button
                                onClick={() => setActiveTab('grading-tool')}
                                style={{
                                    display: 'flex',
                                    alignItems: 'center',
                                    gap: '0.25rem',
                                    padding: '0.375rem 0.75rem',
                                    fontSize: '0.75rem',
                                    fontWeight: '500',
                                    color: '#ea580c',
                                    backgroundColor: 'white',
                                    border: '1px solid #f97316',
                                    borderRadius: '0.375rem',
                                    cursor: 'pointer'
                                }}
                            >
                                <GraduationCap size={12} />
                                Start Grading
                            </button>
                        )}

                        {activeTab === 'policy-manager' && (
                            <div style={{ display: 'flex', gap: '0.5rem' }}>
                                <button
                                    onClick={() => setActiveTab('grading-tool')}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '0.25rem',
                                        padding: '0.375rem 0.75rem',
                                        fontSize: '0.75rem',
                                        fontWeight: '500',
                                        color: '#ea580c',
                                        backgroundColor: 'white',
                                        border: '1px solid #f97316',
                                        borderRadius: '0.375rem',
                                        cursor: 'pointer'
                                    }}
                                >
                                    <GraduationCap size={12} />
                                    Apply to Grading
                                </button>

                                <button
                                    onClick={() => setActiveTab('class-manager')}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '0.25rem',
                                        padding: '0.375rem 0.75rem',
                                        fontSize: '0.75rem',
                                        fontWeight: '500',
                                        color: '#059669',
                                        backgroundColor: 'white',
                                        border: '1px solid #10b981',
                                        borderRadius: '0.375rem',
                                        cursor: 'pointer'
                                    }}
                                >
                                    <Users size={12} />
                                    Configure Class
                                </button>
                            </div>
                        )}
                    </div>
                </div>

                {/* NEW: Policy Warning Banner */}
                {!currentLatePolicy && activeTab === 'grading-tool' && (
                    <div style={{
                        padding: '0.75rem 1.5rem',
                        backgroundColor: '#fffbeb',
                        borderTop: '1px solid #fed7aa',
                        borderBottom: '1px solid #fed7aa'
                    }}>
                        <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                            <AlertTriangle size={16} color="#d97706" />
                            <span style={{ fontSize: '0.875rem', color: '#92400e' }}>
                                Using default institutional policy.
                            </span>
                            <button
                                onClick={() => setActiveTab('policy-manager')}
                                style={{
                                    fontSize: '0.875rem',
                                    color: '#d97706',
                                    backgroundColor: 'transparent',
                                    border: 'none',
                                    textDecoration: 'underline',
                                    cursor: 'pointer'
                                }}
                            >
                                Customize your late policy →
                            </button>
                        </div>
                    </div>
                )}
            </div>

            {/* Add pulse animation for active session */}
            <style>
                {`
                    @keyframes pulse {
                        0%, 100% { opacity: 1; }
                        50% { opacity: 0.5; }
                    }
                `}
            </style>
        </div>
    );
};

export default TabNavigation;
