// Complete SharedContext.js - Fully Consolidated with ALL Original Features + Late Policy Enhancement
// This version preserves EVERY function and feature from the original while adding late policy support

import React, { createContext, useContext, useState, useCallback, useEffect } from 'react';

const AssessmentContext = createContext();

export const useAssessment = () => {
    const context = useContext(AssessmentContext);
    if (!context) {
        throw new Error('useAssessment must be used within AssessmentProvider');
    }
    return context;
};

// Default late policies
const DEFAULT_LATE_POLICIES = {
    institutional: {
        id: 'institutional',
        name: 'Institutional Standard',
        description: 'Standard institutional late policy',
        levels: {
            none: {
                name: 'On Time',
                multiplier: 1.0,
                description: 'Assignment submitted on time',
                color: '#16a34a',
                timeframe: 'On or before due date'
            },
            within24: {
                name: 'Within 24 Hours',
                multiplier: 0.8,
                description: '20% penalty for submissions within 24 hours of due date',
                color: '#ea580c',
                timeframe: 'Up to 24 hours late'
            },
            within48: {
                name: 'Within 48 Hours',
                multiplier: 0.6,
                description: '40% penalty for submissions within 48 hours of due date',
                color: '#dc2626',
                timeframe: 'Up to 48 hours late'
            },
            beyond48: {
                name: 'Beyond 48 Hours',
                multiplier: 0.0,
                description: 'No credit for submissions beyond 48 hours',
                color: '#991b1b',
                timeframe: 'More than 48 hours late'
            }
        },
        isCustom: false,
        isDefault: true
    },
    graceful: {
        id: 'graceful',
        name: 'Graceful Policy',
        description: 'Lenient late policy with minimal penalties',
        levels: {
            none: {
                name: 'On Time',
                multiplier: 1.0,
                description: 'Assignment submitted on time',
                color: '#16a34a',
                timeframe: 'On or before due date'
            },
            within72: {
                name: 'Within 3 Days',
                multiplier: 0.9,
                description: '10% penalty for submissions within 3 days',
                color: '#f59e0b',
                timeframe: 'Up to 3 days late'
            },
            within7days: {
                name: 'Within 1 Week',
                multiplier: 0.75,
                description: '25% penalty for submissions within 1 week',
                color: '#ea580c',
                timeframe: 'Up to 1 week late'
            },
            beyond7days: {
                name: 'Beyond 1 Week',
                multiplier: 0.5,
                description: '50% penalty for submissions beyond 1 week',
                color: '#dc2626',
                timeframe: 'More than 1 week late'
            }
        },
        isCustom: false,
        isTemplate: true
    }
};

export const AssessmentProvider = ({ children }) => {
    // ORIGINAL: Shared rubric state
    const [sharedRubric, setSharedRubric] = useState(null);
    const [sharedCourseDetails, setSharedCourseDetails] = useState(null);
    const [activeTab, setActiveTab] = useState('assignment-prompt-generator');

    // ORIGINAL: AI Prompt Generator state (for rubrics)
    const [aiPromptFormData, setAIPromptFormData] = useState(null);

    // ORIGINAL: Assignment Prompt Generator state
    const [assignmentPromptFormData, setAssignmentPromptFormData] = useState(null);

    // ORIGINAL: Class list and student management
    const [classList, setClassList] = useState(null);
    const [currentStudent, setCurrentStudent] = useState(null);

    // ORIGINAL: Separate storage for draft and final grades
    const [drafts, setDrafts] = useState({});
    const [finalGrades, setFinalGrades] = useState({});

    // NEW: Late Policy State Management
    const [currentLatePolicy, setCurrentLatePolicy] = useState(null);
    const [customLatePolicies, setCustomLatePolicies] = useState([]);

    // ORIGINAL: Batch grading session state
    const [gradingSession, setGradingSession] = useState({
        active: false,
        startTime: null,
        gradedStudents: [],
        totalStudents: 0,
        currentStudent: null,
        currentStudentIndex: 0
    });

    // ENHANCED: Form data states with late policy support
    const [gradingFormData, setGradingFormData] = useState({
        student: { name: '', id: '', email: '' },
        course: { code: '', name: '', instructor: '', term: '', section: '' },
        assignment: { title: '', dueDate: '', totalPoints: 0 },
        criteriaScores: {},
        overallFeedback: '',
        strengths: [],
        improvements: [],
        totalScore: 0,
        percentage: 0,
        letterGrade: '',
        attachments: [],
        videoLinks: [],
        // NEW: Late policy data
        latePolicy: {
            level: 'none',
            penaltyApplied: false,
            policyId: null,
            originalScore: null,
            adjustedScore: null,
            multiplier: 1.0
        }
    });

    // Load data from localStorage on initialization
    useEffect(() => {
        // Load drafts
        const savedDrafts = localStorage.getItem('gradingDrafts');
        if (savedDrafts) {
            try {
                setDrafts(JSON.parse(savedDrafts));
            } catch (error) {
                console.error('Error loading drafts:', error);
            }
        }

        // Load final grades
        const savedFinalGrades = localStorage.getItem('finalGrades');
        if (savedFinalGrades) {
            try {
                setFinalGrades(JSON.parse(savedFinalGrades));
            } catch (error) {
                console.error('Error loading final grades:', error);
            }
        }

        // Load custom late policies
        const savedPolicies = localStorage.getItem('customLatePolicies');
        if (savedPolicies) {
            try {
                setCustomLatePolicies(JSON.parse(savedPolicies));
            } catch (error) {
                console.error('Error loading custom policies:', error);
            }
        }

        // Load current late policy
        const savedCurrentPolicy = localStorage.getItem('currentLatePolicy');
        if (savedCurrentPolicy) {
            try {
                setCurrentLatePolicy(JSON.parse(savedCurrentPolicy));
            } catch (error) {
                console.error('Error loading current policy:', error);
            }
        }
    }, []);

    // ORIGINAL: Update functions for form data
    const updateStudentInfo = useCallback((field, value) => {
        setGradingFormData(prev => ({
            ...prev,
            student: { ...prev.student, [field]: value }
        }));
    }, []);

    const updateCourseInfo = useCallback((field, value) => {
        setGradingFormData(prev => ({
            ...prev,
            course: { ...prev.course, [field]: value }
        }));
    }, []);

    const updateAssignmentInfo = useCallback((field, value) => {
        setGradingFormData(prev => ({
            ...prev,
            assignment: { ...prev.assignment, [field]: value }
        }));
    }, []);

    // ORIGINAL: Initialize form data functions for different generators
    const initializeAssignmentPromptFormData = useCallback((data) => {
        setAssignmentPromptFormData(data);
        console.log('ðŸ“ Assignment prompt form data initialized:', data);
    }, []);

    const initializeAIPromptFormData = useCallback((data) => {
        setAIPromptFormData(data);
        console.log('ðŸ¤– AI prompt form data initialized:', data);
    }, []);

    // ORIGINAL: Reset/clear functions for generators
    const clearAssignmentPromptFormData = useCallback(() => {
        setAssignmentPromptFormData(null);
        console.log('ðŸ—‘ï¸ Assignment prompt form data cleared');
    }, []);

    const clearAIPromptFormData = useCallback(() => {
        setAIPromptFormData(null);
        console.log('ðŸ—‘ï¸ AI prompt form data cleared');
    }, []);

    // ORIGINAL: Additional utility functions that components may expect
    const updateSharedRubric = useCallback((rubric) => {
        setSharedRubric(rubric);
        console.log('ðŸ“‹ Shared rubric updated:', rubric?.assignmentInfo?.title);
    }, []);

    const clearSharedRubric = useCallback(() => {
        setSharedRubric(null);
        console.log('ðŸ—‘ï¸ Shared rubric cleared');
    }, []);

    const updateSharedCourseDetails = useCallback((details) => {
        setSharedCourseDetails(details);
        console.log('ðŸ« Shared course details updated:', details);
    }, []);

    const clearSharedCourseDetails = useCallback(() => {
        setSharedCourseDetails(null);
        console.log('ðŸ—‘ï¸ Shared course details cleared');
    }, []);

    // ORIGINAL: Clear form data
    const clearGradingFormData = useCallback(() => {
        setGradingFormData({
            student: { name: '', id: '', email: '' },
            course: { code: '', name: '', instructor: '', term: '', section: '' },
            assignment: { title: '', dueDate: '', totalPoints: 0 },
            criteriaScores: {},
            overallFeedback: '',
            strengths: [],
            improvements: [],
            totalScore: 0,
            percentage: 0,
            letterGrade: '',
            attachments: [],
            videoLinks: [],
            latePolicy: {
                level: 'none',
                penaltyApplied: false,
                policyId: null,
                originalScore: null,
                adjustedScore: null,
                multiplier: 1.0
            }
        });
    }, []);

    // NEW: Late Policy Management Functions
    const saveCustomLatePolicy = useCallback((policy) => {
        const newPolicy = {
            ...policy,
            id: policy.id === 'new' ? `custom_${Date.now()}` : policy.id,
            isCustom: true,
            createdAt: policy.createdAt || new Date().toISOString(),
            updatedAt: new Date().toISOString()
        };

        setCustomLatePolicies(prev => {
            const filtered = prev.filter(p => p.id !== newPolicy.id);
            const updated = [...filtered, newPolicy];
            localStorage.setItem('customLatePolicies', JSON.stringify(updated));
            return updated;
        });

        return newPolicy;
    }, []);

    const updateCustomLatePolicy = useCallback((policyId, updates) => {
        setCustomLatePolicies(prev => {
            const updated = prev.map(policy =>
                policy.id === policyId
                    ? { ...policy, ...updates, updatedAt: new Date().toISOString() }
                    : policy
            );
            localStorage.setItem('customLatePolicies', JSON.stringify(updated));
            return updated;
        });
    }, []);

    const deleteCustomLatePolicy = useCallback((policyId) => {
        setCustomLatePolicies(prev => {
            const filtered = prev.filter(policy => policy.id !== policyId);
            localStorage.setItem('customLatePolicies', JSON.stringify(filtered));
            return filtered;
        });

        // Reset current policy if it was deleted
        if (currentLatePolicy?.id === policyId) {
            setCurrentLatePolicy(null);
            localStorage.removeItem('currentLatePolicy');
        }
    }, [currentLatePolicy]);

    // NEW: Late Policy Application Functions
    const applyLatePolicy = useCallback((originalScore, level, policyId = null) => {
        const policy = policyId
            ? [...Object.values(DEFAULT_LATE_POLICIES), ...customLatePolicies].find(p => p.id === policyId)
            : currentLatePolicy || DEFAULT_LATE_POLICIES.institutional;

        if (!policy || !policy.levels[level]) {
            return { adjustedScore: originalScore, multiplier: 1.0, penaltyApplied: false };
        }

        const multiplier = policy.levels[level].multiplier;
        const adjustedScore = Math.round(originalScore * multiplier * 100) / 100;

        return {
            adjustedScore,
            multiplier,
            penaltyApplied: level !== 'none',
            policyName: policy.name,
            levelName: policy.levels[level].name
        };
    }, [currentLatePolicy, customLatePolicies]);

    const calculateScoreWithLatePolicy = useCallback((originalScore, lateLevel = 'none') => {
        return applyLatePolicy(originalScore, lateLevel);
    }, [applyLatePolicy]);

    // ORIGINAL: Draft and final grade management with late policy enhancement
    const saveDraft = useCallback((studentId, gradeData = null) => {
        const dataToSave = gradeData || gradingFormData;

        const draftData = {
            ...dataToSave,
            studentId,
            type: 'draft',
            savedAt: new Date().toISOString(),
            // Include late policy data
            latePolicy: dataToSave.latePolicy || {
                level: 'none',
                penaltyApplied: false,
                policyId: currentLatePolicy?.id || null,
                originalScore: dataToSave.totalScore,
                adjustedScore: dataToSave.totalScore,
                multiplier: 1.0
            }
        };

        setDrafts(prev => {
            const updated = { ...prev, [studentId]: draftData };
            localStorage.setItem('gradingDrafts', JSON.stringify(updated));
            return updated;
        });

        return draftData;
    }, [gradingFormData, currentLatePolicy]);

    const loadDraft = useCallback((studentId) => {
        const draft = drafts[studentId];
        if (draft) {
            setGradingFormData(draft);
            return draft;
        }
        return null;
    }, [drafts]);

    const saveFinalGrade = useCallback((studentId, gradeData = null) => {
        const dataToSave = gradeData || gradingFormData;

        const finalData = {
            ...dataToSave,
            studentId,
            type: 'final',
            finalizedAt: new Date().toISOString(),
            // Include late policy data in final grade
            latePolicy: dataToSave.latePolicy || {
                level: 'none',
                penaltyApplied: false,
                policyId: currentLatePolicy?.id || null,
                originalScore: dataToSave.totalScore,
                adjustedScore: dataToSave.totalScore,
                multiplier: 1.0
            }
        };

        setFinalGrades(prev => {
            const updated = { ...prev, [studentId]: finalData };
            localStorage.setItem('finalGrades', JSON.stringify(updated));
            return updated;
        });

        // Remove from drafts when finalized
        setDrafts(prev => {
            const remainingDrafts = { ...prev };
            delete remainingDrafts[studentId];
            localStorage.setItem('gradingDrafts', JSON.stringify(remainingDrafts));
            return remainingDrafts;
        });

        return finalData;
    }, [gradingFormData, currentLatePolicy]);

    const loadFinalGrade = useCallback((studentId) => {
        return finalGrades[studentId] || null;
    }, [finalGrades]);

    const hasDraft = useCallback((studentId) => {
        return drafts[studentId] || null;
    }, [drafts]);

    const getGradeStatus = useCallback((studentId) => {
        if (finalGrades[studentId]) return 'final';
        if (drafts[studentId]) return 'draft';
        return 'not_started';
    }, [finalGrades, drafts]);

    const finalizeGrade = useCallback((studentId) => {
        const draft = drafts[studentId];
        if (draft) {
            return saveFinalGrade(studentId, draft);
        }
        return null;
    }, [drafts, saveFinalGrade]);

    const unlockGrade = useCallback((studentId) => {
        const finalGrade = finalGrades[studentId];
        if (finalGrade) {
            // Convert final grade back to draft
            setDrafts(prev => ({
                ...prev,
                [studentId]: {
                    ...finalGrade,
                    type: 'draft',
                    unlockedAt: new Date().toISOString()
                }
            }));

            // Remove from final grades
            setFinalGrades(prev => {
                const newFinalGrades = { ...prev };
                delete newFinalGrades[studentId];
                return newFinalGrades;
            });

            // Update localStorage
            const allDrafts = { ...drafts, [studentId]: { ...finalGrade, type: 'draft' } };
            localStorage.setItem('gradingDrafts', JSON.stringify(allDrafts));

            const remainingFinalGrades = { ...finalGrades };
            delete remainingFinalGrades[studentId];
            localStorage.setItem('finalGrades', JSON.stringify(remainingFinalGrades));

            return true;
        }
        return false;
    }, [finalGrades, drafts]);

    // ORIGINAL: Navigation helpers for grading sessions
    const nextStudentInSession = useCallback((saveType = 'draft') => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        const nextIndex = currentIndex + 1;

        if (nextIndex < classList.students.length) {
            const currentStudentId = classList.students[currentIndex]?.id;
            const nextStudent = classList.students[nextIndex];

            // Save current work
            if (currentStudentId) {
                if (saveType === 'final') {
                    saveFinalGrade(currentStudentId);
                } else {
                    saveDraft(currentStudentId);
                }
            }

            // Update session state
            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: nextIndex,
                currentStudent: nextStudent
            }));

            setCurrentStudent(nextStudent);

            // Load next student's data
            const existingData = hasDraft(nextStudent.id) || loadFinalGrade(nextStudent.id);
            if (existingData) {
                setGradingFormData(existingData);
            } else {
                clearGradingFormData();
                updateStudentInfo('name', nextStudent.name || '');
                updateStudentInfo('id', nextStudent.id || '');
                updateStudentInfo('email', nextStudent.email || '');
            }

            return true;
        }

        return false;
    }, [gradingSession, classList, saveFinalGrade, saveDraft, hasDraft, loadFinalGrade, clearGradingFormData, updateStudentInfo]);

    const previousStudentInSession = useCallback((saveType = 'draft') => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        const previousIndex = currentIndex - 1;

        if (previousIndex >= 0) {
            const currentStudentId = classList.students[currentIndex]?.id;
            const previousStudent = classList.students[previousIndex];

            // Save current work
            if (currentStudentId) {
                if (saveType === 'final') {
                    saveFinalGrade(currentStudentId);
                } else {
                    saveDraft(currentStudentId);
                }
            }

            // Update session state
            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: previousIndex,
                currentStudent: previousStudent
            }));

            setCurrentStudent(previousStudent);

            // Load previous student's data
            const existingData = hasDraft(previousStudent.id) || loadFinalGrade(previousStudent.id);
            if (existingData) {
                setGradingFormData(existingData);
            } else {
                clearGradingFormData();
                updateStudentInfo('name', previousStudent.name || '');
                updateStudentInfo('id', previousStudent.id || '');
                updateStudentInfo('email', previousStudent.email || '');
            }

            return true;
        }

        return false;
    }, [gradingSession, classList, saveFinalGrade, saveDraft, hasDraft, loadFinalGrade, clearGradingFormData, updateStudentInfo]);

    // ORIGINAL: Grading session management
    const initializeGradingSession = useCallback((classData) => {
        if (!classData || !classData.students || classData.students.length === 0) {
            console.error('Cannot initialize grading session: No valid class data');
            return false;
        }

        const firstStudent = classData.students[0];

        setGradingSession({
            active: true,
            startTime: new Date().toISOString(),
            gradedStudents: [],
            totalStudents: classData.students.length,
            currentStudent: firstStudent,
            currentStudentIndex: 0
        });

        setCurrentStudent(firstStudent);

        // Load existing data or initialize new form
        const existingData = hasDraft(firstStudent.id) || loadFinalGrade(firstStudent.id);
        if (existingData) {
            setGradingFormData(existingData);
        } else {
            clearGradingFormData();
            updateStudentInfo('name', firstStudent.name || '');
            updateStudentInfo('id', firstStudent.id || '');
            updateStudentInfo('email', firstStudent.email || '');
        }

        return true;
    }, [hasDraft, loadFinalGrade, clearGradingFormData, updateStudentInfo]);

    const updateGradingSession = useCallback((updates) => {
        setGradingSession(prev => ({ ...prev, ...updates }));
    }, []);

    // ORIGINAL: Setters for form data updates
    const setGradingData = useCallback((data) => {
        if (typeof data === 'function') {
            setGradingFormData(prev => data(prev));
        } else {
            setGradingFormData(data);
        }
    }, []);

    // Context value with all functions and state
    const contextValue = {
        // ORIGINAL: Core states
        sharedRubric,
        setSharedRubric,
        sharedCourseDetails,
        setSharedCourseDetails,
        activeTab,
        setActiveTab,
        aiPromptFormData,
        setAIPromptFormData,
        assignmentPromptFormData,
        setAssignmentPromptFormData,
        classList,
        setClassList,
        currentStudent,
        setCurrentStudent,

        // ORIGINAL: Form data and management
        gradingData: gradingFormData,
        setGradingData,
        updateStudentInfo,
        updateCourseInfo,
        updateAssignmentInfo,
        clearGradingFormData,

        // ORIGINAL: Generator initialization and management functions
        initializeAssignmentPromptFormData,
        initializeAIPromptFormData,
        clearAssignmentPromptFormData,
        clearAIPromptFormData,

        // ORIGINAL: Additional utility functions
        updateSharedRubric,
        clearSharedRubric,
        updateSharedCourseDetails,
        clearSharedCourseDetails,

        // ORIGINAL: Draft and final grade management
        drafts,
        finalGrades,
        saveDraft,
        loadDraft,
        saveFinalGrade,
        loadFinalGrade,
        hasDraft,
        getGradeStatus,
        finalizeGrade,
        unlockGrade,

        // ORIGINAL: Grading session management
        gradingSession,
        setGradingSession,
        nextStudentInSession,
        previousStudentInSession,
        updateGradingSession,
        initializeGradingSession,

        // NEW: Late Policy Management
        currentLatePolicy,
        setCurrentLatePolicy,
        customLatePolicies,
        setCustomLatePolicies,
        saveCustomLatePolicy,
        updateCustomLatePolicy,
        deleteCustomLatePolicy,
        applyLatePolicy,
        calculateScoreWithLatePolicy,
        DEFAULT_LATE_POLICIES
    };

    return (
        <AssessmentContext.Provider value={contextValue}>
            {children}
        </AssessmentContext.Provider>
    );
};
