// ClassListManager.js - Privacy-Focused Class List Management with Logo Upload
// PRESERVES ALL EXISTING FUNCTIONALITY while implementing session-only storage and logo upload

import React, { useState, useRef, useCallback, useEffect } from 'react';
import {
    Upload, Users, Download, Trash2, FileText, AlertCircle,
    CheckCircle, Clock, GraduationCap, Settings, Sparkles,
    FileDown, BarChart3, Award, Target, TrendingUp, Lock,
    RefreshCw, Save, Plus, Eye, Unlock, ExternalLink,
    Grid3X3, Filter, Search, SortAsc, SortDesc, FileSpreadsheet,
    CheckCircle2, Shield, Timer, Image, X
} from 'lucide-react';
import { useAssessment, DEFAULT_LATE_POLICY } from './SharedContext';
import * as XLSX from 'xlsx';

/**
 * Letter grade calculation function with multiple grading scales
 * Uses original hardcoded scales for reliability
 */
const getLetterGrade = (percentage, programType = 'degree') => {
    // Original hardcoded scales (preserved exactly)
    const scales = {
        degree: [
            { min: 90, grade: 'A+' },
            { min: 80, grade: 'A' },
            { min: 75, grade: 'B+' },
            { min: 70, grade: 'B' },
            { min: 65, grade: 'C+' },
            { min: 60, grade: 'C' },
            { min: 0, grade: 'F' }
        ],
        diploma: [
            { min: 90, grade: 'A+' },
            { min: 80, grade: 'A' },
            { min: 75, grade: 'B+' },
            { min: 70, grade: 'B' },
            { min: 65, grade: 'C+' },
            { min: 60, grade: 'C' },
            { min: 50, grade: 'P' },
            { min: 0, grade: 'F' }
        ],
        certificate: [
            { min: 80, grade: 'P' },
            { min: 0, grade: 'F' }
        ]
    };

    const scale = scales[programType] || scales.degree;

    for (const threshold of scale) {
        if (percentage >= threshold.min) {
            return threshold.grade;
        }
    }

    return 'F';
};

const ClassListManager = () => {
    const {
        classList,
        setClassList,
        setActiveTab,
        sharedRubric,
        currentStudent,
        setCurrentStudent,
        drafts,
        finalGrades,
        getGradeStatus,
        loadDraft,
        loadFinalGrade,
        saveFinalGrade,
        saveDraft,
        unlockGrade: contextUnlockGrade,
        finalizeGrade: contextFinalizeGrade,
        sessionActive,
        currentLatePolicy
    } = useAssessment();

    // Local state
    const [importStatus, setImportStatus] = useState('idle');
    const [previewData, setPreviewData] = useState(null);
    const [validationErrors, setValidationErrors] = useState([]);
    const [showDetails, setShowDetails] = useState(false);
    const [selectedStudents, setSelectedStudents] = useState(new Set());
    const [currentPage, setCurrentPage] = useState(1);
    const [studentsPerPage] = useState(20);
    const [searchTerm, setSearchTerm] = useState('');
    const [sortBy, setSortBy] = useState('name');
    const [sortOrder, setSortOrder] = useState('asc');
    const [viewMode, setViewMode] = useState('table');

    // NEW: Logo upload state
    const [sessionLogo, setSessionLogo] = useState(null);
    const [showLogoUpload, setShowLogoUpload] = useState(false);
    const [logoPreview, setLogoPreview] = useState(null);

    // File input refs
    const fileInputRef = useRef(null);
    const logoInputRef = useRef(null);

    // PRESERVED: Excel file parser
    const parseExcelFile = async (file) => {
        return new Promise((resolve) => {
            const reader = new FileReader();

            reader.onload = (e) => {
                try {
                    const data = new Uint8Array(e.target.result);
                    const workbook = XLSX.read(data, { type: 'array' });
                    const sheetName = workbook.SheetNames[0];
                    const worksheet = workbook.Sheets[sheetName];
                    const jsonData = XLSX.utils.sheet_to_json(worksheet, { header: 1 });

                    if (jsonData.length < 2) {
                        resolve({ success: false, error: 'File must contain at least a header row and one data row' });
                        return;
                    }

                    const headers = jsonData[0].map(h => h ? h.toString().trim().toLowerCase() : '');
                    const dataRows = jsonData.slice(1).filter(row => row.some(cell => cell !== null && cell !== ''));

                    const students = [];
                    let courseMetadata = {};

                    // Extract course metadata from first few rows
                    for (let i = 0; i < Math.min(10, dataRows.length); i++) {
                        const row = dataRows[i];
                        if (row.length > 1) {
                            const firstCell = row[0] ? row[0].toString().toLowerCase() : '';
                            const secondCell = row[1] ? row[1].toString() : '';

                            if (firstCell.includes('course') && secondCell) {
                                courseMetadata.courseCode = secondCell;
                            } else if (firstCell.includes('instructor') || firstCell.includes('professor')) {
                                courseMetadata.instructor = secondCell;
                            } else if (firstCell.includes('section')) {
                                courseMetadata.section = secondCell;
                            } else if (firstCell.includes('term') || firstCell.includes('semester')) {
                                courseMetadata.term = secondCell;
                            } else if (firstCell.includes('program')) {
                                courseMetadata.program = secondCell;
                            }
                        }
                    }

                    // Find required column indices
                    const firstNameCol = headers.findIndex(h => h.includes('first') && h.includes('name'));
                    const lastNameCol = headers.findIndex(h => h.includes('last') && h.includes('name'));
                    const nameCol = headers.findIndex(h => h === 'name' || h === 'student name');
                    const idCol = headers.findIndex(h => h.includes('id') || h.includes('number'));
                    const emailCol = headers.findIndex(h => h.includes('email') || h.includes('mail'));
                    const programCol = headers.findIndex(h => h.includes('program'));

                    if ((firstNameCol === -1 || lastNameCol === -1) && nameCol === -1) {
                        resolve({ success: false, error: 'Must have either "First Name" and "Last Name" columns or a "Name" column' });
                        return;
                    }

                    if (idCol === -1) {
                        resolve({ success: false, error: 'Must have a column containing "ID" or "Number"' });
                        return;
                    }

                    // Process student data
                    dataRows.forEach((row, index) => {
                        let name = '';

                        if (nameCol !== -1 && row[nameCol]) {
                            name = row[nameCol].toString().trim();
                        } else if (firstNameCol !== -1 && lastNameCol !== -1) {
                            const firstName = row[firstNameCol] ? row[firstNameCol].toString().trim() : '';
                            const lastName = row[lastNameCol] ? row[lastNameCol].toString().trim() : '';
                            name = `${firstName} ${lastName}`.trim();
                        }

                        const studentId = row[idCol] ? row[idCol].toString().trim() : '';
                        const email = emailCol !== -1 && row[emailCol] ? row[emailCol].toString().trim() : '';
                        const program = programCol !== -1 && row[programCol] ? row[programCol].toString().trim() : '';

                        if (name && studentId) {
                            students.push({
                                id: studentId,
                                name: name,
                                email: email || '',
                                program: program || '',
                                rowNumber: index + 2
                            });
                        }
                    });

                    resolve({
                        success: true,
                        students: students,
                        courseMetadata: courseMetadata,
                        totalRows: dataRows.length,
                        headers: headers
                    });

                } catch (error) {
                    resolve({ success: false, error: `Error parsing Excel file: ${error.message}` });
                }
            };

            reader.onerror = () => {
                resolve({ success: false, error: 'Error reading file' });
            };

            reader.readAsArrayBuffer(file);
        });
    };

    // PRESERVED: Validate student data
    const validateStudentData = (students) => {
        const errors = [];
        const duplicateIds = [];
        const duplicateNames = [];
        const seenIds = new Set();
        const seenNames = new Set();

        students.forEach((student, index) => {
            if (!student.name || student.name.trim() === '') {
                errors.push(`Row ${index + 1}: Missing student name`);
            }

            if (!student.id || student.id.trim() === '') {
                errors.push(`Row ${index + 1}: Missing student ID`);
            }

            if (seenIds.has(student.id)) {
                duplicateIds.push(student.id);
            } else {
                seenIds.add(student.id);
            }

            if (seenNames.has(student.name.toLowerCase())) {
                duplicateNames.push(student.name);
            } else {
                seenNames.add(student.name.toLowerCase());
            }
        });

        if (duplicateIds.length > 0) {
            errors.push(`Duplicate student IDs found: ${duplicateIds.join(', ')}`);
        }

        if (duplicateNames.length > 0) {
            errors.push(`Duplicate student names found: ${duplicateNames.join(', ')}`);
        }

        return {
            valid: errors.length === 0,
            errors: errors,
            duplicateIds: duplicateIds,
            duplicateNames: duplicateNames
        };
    };

