    const calculateStudentGrade = (studentId) => {
        const gradeStatus = getGradeStatus(studentId);
        let gradeData = null;

        if (gradeStatus === 'final') {
            gradeData = loadFinalGrade(studentId);
        } else if (gradeStatus === 'draft') {
            gradeData = loadDraft(studentId);
        }

        if (!gradeData || !sharedRubric) {
            return {
                score: 'N/A',
                letter: 'N/A',
                percentage: 'N/A',
                status: gradeStatus || 'pending',
                lastModified: null
            };
        }

        // Calculate raw score
        let totalScore = 0;
        let totalPossible = 0;

        if (gradeData.rubricGrading && sharedRubric.criteria) {
            sharedRubric.criteria.forEach(criterion => {
                const grading = gradeData.rubricGrading[criterion.id];
                if (grading && grading.selectedLevel !== undefined) {
                    const levelIndex = grading.selectedLevel;
                    const points = sharedRubric.levels[levelIndex]?.points || 0;
                    totalScore += points * criterion.multiplier;
                }
                totalPossible += (sharedRubric.levels[sharedRubric.levels.length - 1]?.points || 0) * criterion.multiplier;
            });
        }

        // Apply late policy
        let finalScore = totalScore;
        const activeLevels = currentLatePolicy?.levels || DEFAULT_LATE_POLICY.levels;

        if (gradeData.studentInfo?.lateSubmission && gradeData.studentInfo?.lateSubmissionLevel) {
            const lateLevel = activeLevels[gradeData.studentInfo.lateSubmissionLevel];
            if (lateLevel && lateLevel.action === 'percentage') {
                finalScore = totalScore * (lateLevel.value / 100);
            } else if (lateLevel && lateLevel.action === 'deduction') {
                finalScore = Math.max(0, totalScore - lateLevel.value);
            }
        }

        const percentage = totalPossible > 0 ? (finalScore / totalPossible) * 100 : 0;

        // Get student program for grading policy
        const student = classList?.students?.find(s => s.id === studentId);
        const programType = student?.program?.toLowerCase() || 'degree';

        try {
            const letterGrade = getLetterGrade(percentage, programType);

            return {
                score: finalScore.toFixed(1),
                letter: letterGrade,
                percentage: percentage.toFixed(1),
                status: gradeStatus,
                lastModified: gradeData.lastModified || null,
                rawScore: totalScore.toFixed(1),
                penaltyApplied: finalScore !== totalScore
            };
        } catch (error) {
            console.error('Error calculating grade:', error);
            return {
                score: finalScore.toFixed(1),
                letter: 'Error',
                percentage: percentage.toFixed(1),
                status: gradeStatus,
                lastModified: gradeData.lastModified || null,
                rawScore: totalScore.toFixed(1),
                penaltyApplied: finalScore !== totalScore
            };
        }
    };

