        body { font-family: -apple-system, BlinkMacSystemFont, 'Segoe UI', Roboto, sans-serif; margin: 0; padding: 20px; background: #f8f9fa; color: #333; line-height: 1.6; }
        .container { max-width: 800px; margin: 0 auto; background: white; border-radius: 12px; overflow: hidden; box-shadow: 0 4px 6px rgba(0,0,0,0.1); }
        .header { background: linear-gradient(135deg, #667eea 0%, #764ba2 100%); color: white; padding: 2rem; text-align: center; }
        .header h1 { margin: 0 0 0.5rem 0; font-size: 2rem; font-weight: 700; }
        .header .subtitle { opacity: 0.9; font-size: 1.1rem; }
        .content { padding: 2rem; }
        .score-section { background: #e7f3ff; border: 1px solid #b3d9ff; border-radius: 8px; padding: 1.5rem; margin: 1.5rem 0; text-align: center; }
        .score-big { font-size: 3rem; font-weight: 700; color: #0066cc; margin: 0; }
        .score-details { display: grid; grid-template-columns: repeat(auto-fit, minmax(150px, 1fr)); gap: 1rem; margin-top: 1rem; }
        .score-item { background: white; padding: 1rem; border-radius: 6px; text-align: center; }
        .score-item .label { font-size: 0.875rem; color: #666; margin-bottom: 0.25rem; }
        .score-item .value { font-size: 1.25rem; font-weight: 600; color: #333; }
        .section { margin: 2rem 0; }
        .section h2 { font-size: 1.25rem; font-weight: 600; margin-bottom: 1rem; color: #333; border-bottom: 2px solid #eee; padding-bottom: 0.5rem; }
        .info-grid { display: grid; grid-template-columns: repeat(auto-fit, minmax(200px, 1fr)); gap: 1rem; }
        .info-item { background: #f8f9fa; padding: 1rem; border-radius: 6px; }
        .info-item .label { font-size: 0.875rem; color: #666; margin-bottom: 0.25rem; }
        .info-item .value { font-weight: 500; }
        .feedback-section { background: #f8f9fa; border-radius: 8px; padding: 1.5rem; margin: 1rem 0; }
        .feedback-section h3 { margin: 0 0 1rem 0; color: #495057; }
        .feedback-content { background: white; padding: 1rem; border-radius: 4px; border-left: 4px solid #007bff; }
        .attachments-grid { display: grid; grid-template-columns: repeat(auto-fill, minmax(200px, 1fr)); gap: 1rem; margin-top: 1rem; }
        .attachment-item { background: #f8f9fa; border: 1px solid #e9ecef; border-radius: 8px; padding: 1rem; text-align: center; }
        table { width: 100%; border-collapse: collapse; margin: 1rem 0; }
        th, td { border: 1px solid #dee2e6; padding: 0.75rem; text-align: left; }
        th { background: #f8f9fa; font-weight: 600; }
        .footer { text-align: center; padding: 1rem; border-top: 1px solid #eee; color: #666; font-size: 0.875rem; }
        @media print { 
            body { background: white; } 
            .container { box-shadow: none; } 
            @page { margin: 0.5in; }
        }
    </style>
</head>
<body>
    <div class="container">
        ${logoSection}
        <div class="header">
            <h1>📊 Grade Report</h1>
            <div class="subtitle">${sharedRubric.assignmentInfo?.title || 'Assessment'}</div>
        </div>

        <div class="content">
            <div class="section">
                <div class="info-grid">
                    <div class="info-item">
                        <div class="label">Student Name</div>
                        <div class="value">${student.name}</div>
                    </div>
                    <div class="info-item">
                        <div class="label">Student ID</div>
                        <div class="value">${student.id}</div>
                    </div>
                    <div class="info-item">
                        <div class="label">Course</div>
                        <div class="value">${gradeData.course?.code || 'N/A'} - ${gradeData.course?.name || 'N/A'}</div>
                    </div>
                    <div class="info-item">
                        <div class="label">Assignment</div>
                        <div class="value">${sharedRubric.assignmentInfo?.title || 'N/A'}</div>
                    </div>
                </div>
            </div>

            <div class="score-section">
                <div class="score-big">${totalFinalScore.toFixed(1)}/${totalMaxScore}</div>
                <div class="score-details">
                    <div class="score-item">
                        <div class="label">Raw Score</div>
                        <div class="value">${totalRawScore.toFixed(1)}</div>
                    </div>
                    <div class="score-item">
                        <div class="label">Final Score</div>
                        <div class="value">${totalFinalScore.toFixed(1)}</div>
                    </div>
                    <div class="score-item">
                        <div class="label">Percentage</div>
                        <div class="value">${percentage.toFixed(1)}%</div>
                    </div>
                    <div class="score-item">
                        <div class="label">Letter Grade</div>
                        <div class="value">${getLetterGrade(percentage, student.program?.toLowerCase() || 'degree')}</div>
                    </div>
                </div>
                ${penaltyApplied ? '<div style="margin-top: 1rem; color: #dc3545; font-weight: 500;">⚠️ Late penalty applied</div>' : ''}
            </div>

            ${rubricTableHTML ? `
                <div class="section">
                    <h2>📋 Detailed Rubric Assessment</h2>
                    ${rubricTableHTML}
                </div>
            ` : ''}

            ${gradeData.feedback?.general ? `
                <div class="section">
                    <h2>💬 General Feedback</h2>
                    <div class="feedback-section">
                        <div class="feedback-content">${gradeData.feedback.general}</div>
                    </div>
                </div>
            ` : ''}

            ${gradeData.feedback?.strengths ? `
                <div class="section">
                    <h2>✅ Strengths</h2>
                    <div class="feedback-section">
                        <div class="feedback-content">${gradeData.feedback.strengths}</div>
                    </div>
                </div>
            ` : ''}

            ${gradeData.feedback?.improvements ? `
                <div class="section">
                    <h2>🎯 Areas for Improvement</h2>
                    <div class="feedback-section">
                        <div class="feedback-content">${gradeData.feedback.improvements}</div>
                    </div>
                </div>
            ` : ''}

            ${attachmentsHTML ? `
                <div class="section">
                    <h2>📎 Attachments</h2>
                    <div class="attachments-grid">
                        ${attachmentsHTML}
                    </div>
                </div>
            ` : ''}

            ${videoLinksHTML ? `
                <div class="section">
                    <h2>🎥 Video Links</h2>
                    ${videoLinksHTML}
                </div>
            ` : ''}
        </div>

        <div class="footer">
            Generated on ${new Date().toLocaleDateString()} • GradingPilot Session Report
        </div>
    </div>
</body>
</html>`;
        };

        // Generate and export the HTML file
        try {
            const htmlContent = generateStudentReportHTML();
            const htmlBlob = new Blob([htmlContent], { type: 'text/html' });
            const url = URL.createObjectURL(htmlBlob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `grade_report_${student.name.replace(/[^a-zA-Z0-9]/g, '_')}_${sharedRubric.assignmentInfo?.title?.replace(/[^a-zA-Z0-9]/g, '_') || 'assignment'}.html`;
            link.click();
            URL.revokeObjectURL(url);

            console.log(`✅ Individual grade report exported for student: ${student.name}`);
        } catch (error) {
            console.error('Error generating individual grade report:', error);
            alert('Error generating grade report. Please check the console for details.');
        }
    };

    // PRIVACY: Modified file upload handling - NO AUTO-PERSISTENCE
    const handleFileUpload = async (event) => {
        const file = event.target.files[0];
        if (!file) return;

        // PRIVACY: Check session status
        if (!sessionActive) {
            alert('Session has expired. Please refresh the page to start a new session.');
            return;
        }

        setImportStatus('processing');

        try {
            // Use Excel parser instead of manual CSV parsing
            const result = await parseExcelFile(file);

            if (!result.success) {
                throw new Error(result.error || 'Failed to parse Excel file');
            }

            // Validate the parsed student data
            const validation = validateStudentData(result.students);

            if (result.students.length === 0) {
                throw new Error('No valid student records found in Excel file');
            }

            // Create grading progress tracking for each student
            const gradingProgress = result.students.map(() => ({
                status: 'pending',
                lastModified: null,
                gradeType: null
            }));

            // PRIVACY: Create the class list data structure with session-only storage
            const classListData = {
                students: result.students,
                gradingProgress,
                fileName: file.name,
                fileSize: file.size,
                importTime: new Date().toISOString(),
                courseMetadata: {
                    courseCode: result.courseMetadata?.courseCode || 'IMPORTED',
                    courseName: result.courseMetadata?.courseName || 'Excel Import',
                    section: result.courseMetadata?.section || '',
                    instructor: result.courseMetadata?.instructor || '',
                    term: result.courseMetadata?.term || '',
                    program: result.courseMetadata?.program || ''
                }
            };

            // PRIVACY: Set class list in session-only context
            setClassList(classListData);
            setValidationErrors(validation.errors);
            setImportStatus('success');

            console.log(`✅ Imported ${result.students.length} students from ${file.name}`);
            console.log('📊 Class list stored in session-only memory (auto-clears in 1 hour)');

        } catch (error) {
            console.error('Import error:', error);
            setImportStatus('error');
            setValidationErrors([error.message]);
        }
    };

    // PRESERVED: Calculate grading progress
    const getGradingProgress = () => {
        if (!classList) return { completed: 0, total: 0, percentage: 0, final: 0, draft: 0 };

        const final = classList.students.filter(student => getGradeStatus(student.id) === 'final').length;
        const draft = classList.students.filter(student => getGradeStatus(student.id) === 'draft').length;
        const completed = final + draft;
        const total = classList.students.length;
        const percentage = total > 0 ? Math.round((completed / total) * 100) : 0;

        return { completed, total, percentage, final, draft };
    };

    // PRESERVED: Export functions
    const exportClassList = () => {
        if (!classList || !classList.students) {
            alert('No class list to export');
            return;
        }

        const exportData = {
            ...classList,
            exportedAt: new Date().toISOString(),
            version: '1.0',
            sessionExport: true,
            sessionLogo: sessionLogo // Include logo in JSON export
        };

        const blob = new Blob([JSON.stringify(exportData, null, 2)], { type: 'application/json' });
        const url = URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.download = `class_list_${classList.courseMetadata?.courseCode || 'course'}_${new Date().toISOString().split('T')[0]}.json`;
        link.click();
        URL.revokeObjectURL(url);
    };

    const exportToExcel = () => {
        if (!classList || !classList.students) {
            alert('No class list to export');
            return;
        }

        const worksheetData = [];

        // Header row
        worksheetData.push(['Student ID', 'Student Name', 'Email', 'Program', 'Grading Status']);

        // Student rows
        classList.students.forEach((student, index) => {
            const progress = classList.gradingProgress[index] || {};
            const gradeStatus = getGradeStatus(student.id);

            worksheetData.push([
                student.id,
                student.name,
                student.email || '',
                student.program || '',
                gradeStatus === 'final' ? 'Final' : gradeStatus === 'draft' ? 'Draft' : 'Pending'
            ]);
        });

        const wb = XLSX.utils.book_new();
        const ws = XLSX.utils.aoa_to_sheet(worksheetData);
        XLSX.utils.book_append_sheet(wb, ws, 'Class List');

        XLSX.writeFile(wb, `class_list_${classList.courseMetadata?.courseCode || 'course'}_${new Date().toISOString().split('T')[0]}.xlsx`);
    };

    // PRESERVED: Clear class list
    const clearClassList = () => {
        if (window.confirm('Are you sure you want to clear the current class list? This action cannot be undone.')) {
            setClassList(null);
            setValidationErrors([]);
            setImportStatus('idle');
            setCurrentStudent(null);
            console.log('🗑️ Class list cleared from session');
        }
    };

    // PRESERVED: Load student for grading
    const loadStudentForGrading = (studentId) => {
        const student = classList.students.find(s => s.id === studentId);
        if (student && sharedRubric) {
            setCurrentStudent(student);
            setActiveTab('grading');
            console.log(`👤 Loading student for grading: ${student.name}`);
        } else if (!sharedRubric) {
            alert('Please create or load a rubric before grading students.');
        }
    };

    // PRESERVED: Grade status helpers
    const getGradeStatusDisplay = (studentId) => {
        const status = getGradeStatus(studentId);

        if (status === 'final') {
            return (
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-green-500 rounded-full"></div>
                    <span className="text-xs bg-green-100 text-green-800 px-2 py-1 rounded-full font-medium">
                        Final
                    </span>
                </div>
            );
        } else if (status === 'draft') {
            return (
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-yellow-500 rounded-full"></div>
                    <span className="text-xs bg-yellow-100 text-yellow-800 px-2 py-1 rounded-full font-medium">
                        Draft
                    </span>
                </div>
            );
        } else {
            return (
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 bg-gray-300 rounded-full"></div>
                    <span className="text-xs bg-gray-100 text-gray-600 px-2 py-1 rounded-full">
                        Pending
                    </span>
                </div>
            );
        }
    };

    const finalizeGrade = (studentId) => {
        contextFinalizeGrade(studentId);
    };

    const unlockGrade = (studentId) => {
        contextUnlockGrade(studentId);
    };

    // PRESERVED: Filtering and pagination
    const filteredStudents = classList?.students?.filter(student => {
        const searchLower = searchTerm.toLowerCase();
        return (
            student.name.toLowerCase().includes(searchLower) ||
            student.id.toLowerCase().includes(searchLower) ||
            (student.email && student.email.toLowerCase().includes(searchLower))
        );
    }) || [];

    const sortedStudents = [...filteredStudents].sort((a, b) => {
        let aValue, bValue;

        switch (sortBy) {
            case 'name':
                aValue = a.name.toLowerCase();
                bValue = b.name.toLowerCase();
                break;
            case 'id':
                aValue = a.id.toLowerCase();
                bValue = b.id.toLowerCase();
                break;
            case 'email':
                aValue = (a.email || '').toLowerCase();
                bValue = (b.email || '').toLowerCase();
                break;
            case 'status':
                aValue = getGradeStatus(a.id);
                bValue = getGradeStatus(b.id);
                break;
            default:
                return 0;
        }

        if (aValue < bValue) return sortOrder === 'asc' ? -1 : 1;
        if (aValue > bValue) return sortOrder === 'asc' ? 1 : -1;
        return 0;
    });

    const totalPages = Math.ceil(sortedStudents.length / studentsPerPage);
    const startIndex = (currentPage - 1) * studentsPerPage;
    const paginatedStudents = sortedStudents.slice(startIndex, startIndex + studentsPerPage);

    // Early return if no class list
    if (!classList) {
        return (
            <div className="min-h-screen bg-gray-50">
                {/* PRIVACY: Session warning banner */}
                <div className="bg-gradient-to-r from-amber-50 to-orange-50 border-l-4 border-amber-400 p-4">
                    <div className="flex items-center">
                        <div className="flex-shrink-0">
                            <AlertCircle className="h-5 w-5 text-amber-400" />
                        </div>
                        <div className="ml-3">
                            <p className="text-sm text-amber-700">
                                <strong>Privacy Notice:</strong> All imported data is stored in session only and will be automatically cleared after 1 hour
                                or when the application restarts.
                            </p>
                        </div>
                    </div>
                </div>

                {/* Import Section - Compact but Rich Layout */}
                <div className="p-6">
                    <div className="grid grid-cols-7 gap-6">
                        {/* Main Upload Area - 5 columns */}
                        <div className="col-span-5">
                            <div className="bg-white rounded-xl shadow-lg border border-gray-100 overflow-hidden">
                                {/* Header with Logo Integration */}
                                <div className="bg-gradient-to-r from-blue-600 to-indigo-600 px-6 py-4">
                                    <div className="flex items-center justify-between">
                                        <div className="flex items-center gap-3">
                                            <div className="bg-white/20 rounded-lg p-2">
                                                <FileSpreadsheet size={24} className="text-white" />
                                            </div>
                                            <div>
                                                <h2 className="text-xl font-bold text-white">Import Student List</h2>
                                                <p className="text-blue-100 text-sm">Upload your Excel file to get started</p>
                                            </div>
                                        </div>

                                        {/* Logo Section in Header */}
                                        <div className="flex items-center gap-3">
                                            {sessionLogo ? (
                                                <div className="flex items-center gap-2">
                                                    <img
                                                        src={sessionLogo}
                                                        alt="School Logo"
                                                        className="h-10 w-auto object-contain bg-white/20 rounded px-2 py-1 backdrop-blur-sm"
                                                    />
                                                    <button
                                                        onClick={removeLogo}
                                                        className="text-white/80 hover:text-white p-1 bg-white/10 rounded"
                                                    >
                                                        <X size={16} />
                                                    </button>
                                                </div>
                                            ) : (
                                                <div className="flex items-center gap-2">
                                                    <Image size={18} className="text-white/80" />
                                                    <input
                                                        type="file"
                                                        ref={logoInputRef}
                                                        accept="image/png,image/jpeg,image/jpg,image/svg+xml"
                                                        onChange={handleLogoUpload}
                                                        className="hidden"
                                                    />
                                                    <button
                                                        onClick={() => logoInputRef.current?.click()}
                                                        className="text-white hover:text-white bg-white/10 hover:bg-white/20 px-3 py-1.5 rounded text-sm flex items-center gap-1 transition-colors"
                                                    >
                                                        <Upload size={14} />
                                                        Add Logo
                                                    </button>
                                                </div>
                                            )}
                                        </div>
                                    </div>
                                </div>

                                {/* Upload Area */}
                                <div className="p-6">
                                    <div className="border-2 border-dashed border-blue-300 rounded-xl p-8 text-center bg-blue-50/50 hover:bg-blue-50 transition-colors">
                                        <div className="flex flex-col items-center space-y-4">
                                            <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center">
                                                <Upload size={24} className="text-blue-600" />
                                            </div>
                                            <div>
                                                <h3 className="text-lg font-semibold text-gray-800 mb-2">
                                                    Upload Your Excel Class List
                                                </h3>
                                                <p className="text-gray-600 mb-4">
                                                    Drag and drop your .xlsx or .xls file here, or click to browse
                                                </p>
                                            </div>
                                            <div>
                                                <input
                                                    type="file"
                                                    ref={fileInputRef}
                                                    accept=".xlsx,.xls"
                                                    onChange={handleFileUpload}
                                                    className="hidden"
                                                    disabled={importStatus === 'processing'}
                                                />
                                                <button
                                                    onClick={() => fileInputRef.current?.click()}
                                                    disabled={importStatus === 'processing'}
                                                    className="bg-blue-600 hover:bg-blue-700 disabled:bg-blue-400 text-white px-6 py-3 rounded-lg font-medium transition-colors flex items-center gap-2"
                                                >
                                                    {importStatus === 'processing' ? (
                                                        <>
                                                            <RefreshCw size={18} className="animate-spin" />
                                                            Processing...
                                                        </>
                                                    ) : (
                                                        <>
                                                            <FileSpreadsheet size={18} />
                                                            Choose Excel File
                                                        </>
                                                    )}
                                                </button>
                                            </div>
                                        </div>
                                    </div>

                                    {/* Error Messages */}
                                    {importStatus === 'error' && (
                                        <div className="mt-4 p-4 bg-red-50 border border-red-200 rounded-lg">
                                            <div className="flex items-center gap-2 text-red-700">
                                                <AlertCircle size={18} />
                                                <h4 className="font-medium">Import Failed</h4>
                                            </div>
                                            <div className="mt-2 text-sm text-red-600">
                                                {validationErrors.map((error, index) => (
                                                    <div key={index}>{error}</div>
                                                ))}
                                            </div>
                                        </div>
                                    )}

                                    {/* Logo Info */}
                                    {sessionLogo && (
                                        <div className="mt-4 p-3 bg-green-50 border border-green-200 rounded-lg">
                                            <div className="flex items-center gap-2 text-green-700">
                                                <CheckCircle size={16} />
                                                <span className="text-sm font-medium">Logo will be included in all exported reports</span>
                                            </div>
                                        </div>
                                    )}
                                </div>
                            </div>
                        </div>

                        {/* Info Sidebar - 2 columns */}
                        <div className="col-span-2 space-y-4">
                            {/* Format Requirements */}
                            <div className="bg-white rounded-lg shadow-lg border border-gray-100 p-4">
                                <h3 className="text-lg font-semibold text-gray-800 mb-3 flex items-center gap-2">
                                    <CheckCircle2 size={18} className="text-green-600" />
                                    Required Format
                                </h3>
                                <div className="space-y-2 text-sm text-gray-600">
                                    <div className="flex items-start gap-2">
                                        <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2"></div>
                                        <div>
                                            <span className="font-medium">Headers required:</span> Student ID, First Name, Last Name (or Name)
                                        </div>
                                    </div>
                                    <div className="flex items-start gap-2">
                                        <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2"></div>
                                        <div>
                                            <span className="font-medium">Optional:</span> Email, Program columns
                                        </div>
                                    </div>
                                    <div className="flex items-start gap-2">
                                        <div className="w-1.5 h-1.5 bg-blue-500 rounded-full mt-2"></div>
                                        <div>
                                            <span className="font-medium">Course metadata:</span> Add course info in first rows
                                        </div>
                                    </div>
                                </div>
                            </div>

                            {/* Privacy Information */}
                            <div className="bg-amber-50 rounded-lg border border-amber-200 p-4">
                                <h3 className="text-lg font-semibold text-amber-800 mb-3 flex items-center gap-2">
                                    <Shield size={18} />
                                    Privacy Protection
                                </h3>
                                <div className="space-y-2 text-sm text-amber-700">
                                    <div className="flex items-center gap-2">
                                        <Timer size={14} />
                                        <span>Data stored in session only (1 hour max)</span>
                                    </div>
                                    <div className="flex items-center gap-2">
                                        <Lock size={14} />
                                        <span>No persistent browser storage</span>
                                    </div>
                                    <div className="flex items-center gap-2">
                                        <RefreshCw size={14} />
                                        <span>Automatic data clearing on restart</span>
                                    </div>
                                </div>
                            </div>

                            {/* Quick Start Guide */}
                            <div className="bg-blue-50 rounded-lg border border-blue-200 p-4">
                                <h3 className="text-lg font-semibold text-blue-800 mb-3 flex items-center gap-2">
                                    <GraduationCap size={18} />
                                    Quick Start
                                </h3>
                                <div className="space-y-2 text-sm text-blue-700">
                                    <div className="flex items-start gap-2">
                                        <span className="bg-blue-200 text-blue-800 rounded-full w-5 h-5 flex items-center justify-center text-xs font-medium flex-shrink-0 mt-0.5">1</span>
                                        <span>Upload your Excel class list</span>
                                    </div>
                                    <div className="flex items-start gap-2">
                                        <span className="bg-blue-200 text-blue-800 rounded-full w-5 h-5 flex items-center justify-center text-xs font-medium flex-shrink-0 mt-0.5">2</span>
                                        <span>Create or import a rubric</span>
                                    </div>
                                    <div className="flex items-start gap-2">
                                        <span className="bg-blue-200 text-blue-800 rounded-full w-5 h-5 flex items-center justify-center text-xs font-medium flex-shrink-0 mt-0.5">3</span>
                                        <span>Start grading students</span>
                                    </div>
                                    <div className="flex items-start gap-2">
                                        <span className="bg-blue-200 text-blue-800 rounded-full w-5 h-5 flex items-center justify-center text-xs font-medium flex-shrink-0 mt-0.5">4</span>
                                        <span>Export reports with your logo</span>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

    // Main interface with class list loaded
    const progress = getGradingProgress();

    return (
        <div className="min-h-screen bg-gray-50">
            {/* PRIVACY: Session warning banner */}
            <div className="bg-gradient-to-r from-amber-50 to-orange-50 border-l-4 border-amber-400 p-4">
                <div className="flex items-center">
                    <div className="flex-shrink-0">
                        <AlertCircle className="h-5 w-5 text-amber-400" />
                    </div>
                    <div className="ml-3">
                        <p className="text-sm text-amber-700">
                            <strong>Privacy Notice:</strong> All imported data is stored in session only and will be automatically cleared after 1 hour
                            or when the application restarts.
                        </p>
                    </div>
                </div>
            </div>

            <div className="p-6">
                {/* Header Section */}
                <div className="bg-white rounded-2xl shadow-xl border border-gray-100 mb-8 overflow-hidden">
                    {/* Header Bar */}
                    <div className="bg-gradient-to-r from-blue-700 to-indigo-700 px-8 py-6">
                        <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between">
                            <div className="flex items-center space-x-4">
                                <div className="bg-white/30 rounded-lg p-3 backdrop-blur-sm">
                                    <Users size={32} className="text-white" />
                                </div>
                                <div>
                                    <h1 className="text-3xl font-bold text-white drop-shadow-lg">Class Manager</h1>
                                    <p className="text-blue-100 font-medium text-lg drop-shadow-md">
                                        {classList.students.length} students imported
                                    </p>
                                </div>
                            </div>

                            {/* Logo Display */}
                            {sessionLogo && (
                                <div className="mt-4 lg:mt-0 text-center">
                                    <img
                                        src={sessionLogo}
                                        alt="School Logo"
                                        className="max-h-12 max-w-32 mx-auto object-contain bg-white/20 rounded-lg p-2 backdrop-blur-sm"
                                    />
                                </div>
                            )}
                        </div>
                    </div>

                    {/* Action Bar */}
                    <div className="px-8 py-6 bg-gray-50 border-b border-gray-200">
                        <div className="flex flex-wrap items-center justify-between gap-4">
                            <div className="flex flex-wrap items-center gap-3">
                                <button
                                    onClick={exportClassList}
                                    className="flex items-center gap-2 px-4 py-2 bg-green-600 hover:bg-green-700 text-white rounded-lg transition-colors"
                                >
                                    <Download size={16} />
                                    Export JSON
                                </button>
                                <button
                                    onClick={exportToExcel}
                                    className="flex items-center gap-2 px-4 py-2 bg-emerald-600 hover:bg-emerald-700 text-white rounded-lg transition-colors"
                                >
                                    <FileSpreadsheet size={16} />
                                    Export Excel
                                </button>
                                <button
                                    onClick={clearClassList}
                                    className="flex items-center gap-2 px-4 py-2 bg-red-600 hover:bg-red-700 text-white rounded-lg transition-colors"
                                >
                                    <Trash2 size={16} />
                                    Clear List
                                </button>
                            </div>

                            <div className="flex items-center gap-3">
                                {!sessionLogo && (
                                    <button
                                        onClick={() => logoInputRef.current?.click()}
                                        className="flex items-center gap-2 px-3 py-2 bg-blue-600 hover:bg-blue-700 text-white rounded-lg transition-colors text-sm"
                                    >
                                        <Image size={16} />
                                        Add Logo
                                    </button>
                                )}
                                {sessionLogo && (
                                    <button
                                        onClick={removeLogo}
                                        className="flex items-center gap-2 px-3 py-2 bg-gray-600 hover:bg-gray-700 text-white rounded-lg transition-colors text-sm"
                                    >
                                        <X size={16} />
                                        Remove Logo
                                    </button>
                                )}
                            </div>
                        </div>

                        {/* Hidden logo input */}
                        <input
                            type="file"
                            ref={logoInputRef}
                            accept="image/png,image/jpeg,image/jpg,image/svg+xml"
                            onChange={handleLogoUpload}
                            className="hidden"
                        />
                    </div>

                    {/* Import Summary */}
                    <div className="px-8 py-6">
                        <h3 className="text-lg font-semibold text-gray-800 mb-4">Import Summary</h3>
                        <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-sm">
                            <div>
                                <span className="text-gray-600">File:</span>
                                <div className="font-medium">{classList.fileName}</div>
                            </div>
                            <div>
                                <span className="text-gray-600">Import Date:</span>
                                <div className="font-medium">
                                    {new Date(classList.importTime).toLocaleDateString()}
                                </div>
                            </div>
                            <div>
                                <span className="text-gray-600">Storage:</span>
                                <div className="font-medium text-orange-600 flex items-center gap-1">
                                    <Shield size={12} />
                                    Session Only
                                </div>
                            </div>
                            <div>
                                <span className="text-gray-600">Total Students:</span>
                                <div className="font-medium">{classList.students.length}</div>
                            </div>
                        </div>
                    </div>
                </div>

                {/* Progress and Export Section */}
                <div className="grid lg:grid-cols-4 gap-6 mb-8">
                    {/* Progress Card */}
                    <div className="lg:col-span-3 bg-white rounded-xl shadow-lg border border-gray-100 p-6">
                        <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
                            <BarChart3 size={20} />
                            Grading Progress
                        </h3>

                        <div className="grid grid-cols-1 md:grid-cols-4 gap-4 mb-4">
                            <div className="text-center">
                                <div className="text-2xl font-bold text-gray-800">{progress.total}</div>
                                <div className="text-sm text-gray-600">Total Students</div>
                            </div>
                            <div className="text-center">
                                <div className="text-2xl font-bold text-green-600">{progress.final}</div>
                                <div className="text-sm text-gray-600">Final Grades</div>
                            </div>
                            <div className="text-center">
                                <div className="text-2xl font-bold text-yellow-600">{progress.draft}</div>
                                <div className="text-sm text-gray-600">Draft Grades</div>
                            </div>
                            <div className="text-center">
                                <div className="text-2xl font-bold text-blue-600">{progress.percentage}%</div>
                                <div className="text-sm text-gray-600">Complete</div>
                            </div>
                        </div>

                        <div className="w-full bg-gray-200 rounded-full h-3">
                            <div
                                className="bg-gradient-to-r from-blue-500 to-green-500 h-3 rounded-full transition-all duration-500"
                                style={{ width: `${progress.percentage}%` }}
                            ></div>
                        </div>
                    </div>

                    {/* Export Reports Card */}
                    <div className="bg-white rounded-xl shadow-lg border border-gray-100 p-6">
                        <h3 className="text-lg font-semibold text-gray-800 mb-4 flex items-center gap-2">
                            <FileDown size={20} />
                            Export Reports
                        </h3>

                        <div className="space-y-3">
                            <button
                                onClick={exportClassGradesHTML}
                                disabled={!sharedRubric}
                                className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white rounded-lg transition-colors text-sm"
                            >
                                <FileText size={16} />
                                HTML Report
                            </button>
                            <button
                                onClick={exportClassGradesPortraitPDF}
                                disabled={!sharedRubric}
                                className="w-full flex items-center justify-center gap-2 px-4 py-3 bg-red-600 hover:bg-red-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white rounded-lg transition-colors text-sm"
                            >
                                <FileText size={16} />
                                PDF Report
                            </button>
                            {!sharedRubric && (
                                <p className="text-xs text-gray-500 text-center">
                                    Load a rubric to enable exports
                                </p>
                            )}
                            {sessionLogo && (
                                <p className="text-xs text-green-600 text-center flex items-center justify-center gap-1">
                                    <CheckCircle size={12} />
                                    Logo will be included
                                </p>
                            )}
                        </div>
                    </div>
                </div>

                {/* Search and Filter Bar */}
                <div className="bg-white rounded-xl shadow-lg border border-gray-100 p-6 mb-6">
                    <div className="flex flex-col lg:flex-row lg:items-center lg:justify-between gap-4">
                        <div className="flex-1 relative">
                            <Search size={20} className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400" />
                            <input
                                type="text"
                                placeholder="Search students by name, ID, or email..."
                                value={searchTerm}
                                onChange={(e) => setSearchTerm(e.target.value)}
                                className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            />
                        </div>

                        <div className="flex items-center gap-3">
                            <select
                                value={sortBy}
                                onChange={(e) => setSortBy(e.target.value)}
                                className="px-3 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                            >
                                <option value="name">Sort by Name</option>
                                <option value="id">Sort by ID</option>
                                <option value="email">Sort by Email</option>
                                <option value="status">Sort by Status</option>
                            </select>

                            <button
                                onClick={() => setSortOrder(sortOrder === 'asc' ? 'desc' : 'asc')}
                                className="p-2 border border-gray-300 rounded-lg hover:bg-gray-50 transition-colors"
                            >
                                {sortOrder === 'asc' ? <SortAsc size={20} /> : <SortDesc size={20} />}
                            </button>
                        </div>
                    </div>
                </div>

                {/* Student List */}
                <div className="bg-white rounded-xl shadow-lg border border-gray-100 overflow-hidden">
                    <div className="overflow-x-auto">
                        <table className="table-auto w-full">
                            <thead className="bg-gray-50">
                                <tr>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        #
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Status
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Student ID
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Name
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Email
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Program
                                    </th>
                                    <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                                        Actions
                                    </th>
                                </tr>
                            </thead>
                            <tbody className="bg-white divide-y divide-gray-200">
                                {paginatedStudents.map((student, index) => {
                                    const actualIndex = startIndex + index + 1;
                                    const gradeStatus = getGradeStatus(student.id);

                                    return (
                                        <tr key={student.id} className="hover:bg-gray-50 transition-colors">
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                                {actualIndex}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap">
                                                {getGradeStatusDisplay(student.id)}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                                {student.id}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium text-gray-900">
                                                {student.name}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                {student.email || '—'}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                                                {student.program || '—'}
                                            </td>
                                            <td className="px-6 py-4 whitespace-nowrap text-sm font-medium">
                                                <div className="flex items-center gap-2">
                                                    <button
                                                        onClick={() => loadStudentForGrading(student.id)}
                                                        disabled={!sharedRubric}
                                                        className="flex items-center gap-1 px-3 py-1 bg-blue-600 hover:bg-blue-700 disabled:bg-gray-400 disabled:cursor-not-allowed text-white rounded text-sm transition-colors"
                                                    >
                                                        <GraduationCap size={14} />
                                                        Grade
                                                    </button>

                                                    {gradeStatus === 'final' && (
                                                        <>
                                                            <button
                                                                onClick={() => unlockGrade(student.id)}
                                                                className="flex items-center gap-1 px-3 py-1 bg-yellow-600 hover:bg-yellow-700 text-white rounded text-sm transition-colors"
                                                            >
                                                                <Unlock size={14} />
                                                                Unlock
                                                            </button>
                                                            <button
                                                                onClick={() => exportStudentGrade(student.id)}
                                                                className="flex items-center gap-1 px-3 py-1 bg-green-600 hover:bg-green-700 text-white rounded text-sm transition-colors"
                                                            >
                                                                <Download size={14} />
                                                                Export
                                                            </button>
                                                        </>
                                                    )}

                                                    {gradeStatus === 'draft' && (
                                                        <button
                                                            onClick={() => finalizeGrade(student.id)}
                                                            className="flex items-center gap-1 px-3 py-1 bg-green-600 hover:bg-green-700 text-white rounded text-sm transition-colors"
                                                        >
                                                            <Lock size={14} />
                                                            Finalize
                                                        </button>
                                                    )}
                                                </div>
                                            </td>
                                        </tr>
                                    );
                                })}
                            </tbody>
                        </table>
                    </div>

                    {/* Pagination */}
                    {totalPages > 1 && (
                        <div className="px-6 py-4 border-t border-gray-200 flex items-center justify-between">
                            <div className="text-sm text-gray-700">
                                Showing {startIndex + 1} to {Math.min(startIndex + studentsPerPage, sortedStudents.length)} of {sortedStudents.length} students
                            </div>
                            <div className="flex items-center gap-2">
                                <button
                                    onClick={() => setCurrentPage(currentPage - 1)}
                                    disabled={currentPage === 1}
                                    className="px-3 py-1 border border-gray-300 rounded disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50 transition-colors"
                                >
                                    Previous
                                </button>
                                <span className="px-3 py-1 text-sm">
                                    Page {currentPage} of {totalPages}
                                </span>
                                <button
                                    onClick={() => setCurrentPage(currentPage + 1)}
                                    disabled={currentPage === totalPages}
                                    className="px-3 py-1 border border-gray-300 rounded disabled:opacity-50 disabled:cursor-not-allowed hover:bg-gray-50 transition-colors"
                                >
                                    Next
                                </button>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default ClassListManager;
