// Updated SharedContext.js with Enhanced initializeGradingSession function
// This ensures proper course information transfer and rubric integration

import React, { createContext, useContext, useState, useCallback, useEffect } from 'react';

const AssessmentContext = createContext();

export const useAssessment = () => {
    const context = useContext(AssessmentContext);
    if (!context) {
        throw new Error('useAssessment must be used within AssessmentProvider');
    }
    return context;
};

export const AssessmentProvider = ({ children }) => {
    // Shared rubric state
    const [sharedRubric, setSharedRubric] = useState(null);
    const [sharedCourseDetails, setSharedCourseDetails] = useState(null);
    const [activeTab, setActiveTab] = useState('assignment-prompt-generator');

    // AI Prompt Generator state (for rubrics)
    const [aiPromptFormData, setAIPromptFormData] = useState(null);

    // Assignment Prompt Generator state
    const [assignmentPromptFormData, setAssignmentPromptFormData] = useState(null);

    // Class list and student management
    const [classList, setClassList] = useState(null);
    const [currentStudent, setCurrentStudent] = useState(null);

    // ENHANCED: Separate storage for draft and final grades
    const [drafts, setDrafts] = useState({});
    const [finalGrades, setFinalGrades] = useState({});

    // Batch grading session state
    const [gradingSession, setGradingSession] = useState({
        active: false,
        startTime: null,
        gradedStudents: [],
        totalStudents: 0,
        currentStudent: null,
        currentStudentIndex: 0
    });

    // Form data states
    const [gradingFormData, setGradingFormData] = useState({
        student: { name: '', id: '', email: '' },
        course: { code: '', name: '', instructor: '', term: '' },
        assignment: { name: '', dueDate: '', maxPoints: 100 },
        feedback: { general: '', strengths: '', improvements: '' },
        attachments: [],
        videoLinks: [],
        latePolicy: { level: 'none', penaltyApplied: false },
        rubricGrading: {},
        metadata: {
            gradedBy: '',
            gradedDate: '',
            aiAssisted: false,
            rubricIntegrated: false
        }
    });

    // Rubric form data state (for RubricCreator)
    const [rubricFormData, setRubricFormData] = useState(null);

    // ENHANCED: Initialize Grading Session with proper course info transfer
    const initializeGradingSession = useCallback((classListData) => {
        console.log('üöÄ initializeGradingSession called with:', classListData);

        // 1. Guard against missing or empty class list
        if (!classListData || !classListData.students?.length) {
            console.error('‚ùå Cannot initialize grading session: No class list or empty student list');
            return false;
        }

        // 2. Extract the students array and the imported course metadata
        const { students, courseMetadata } = classListData;
        const firstStudent = students[0];

        console.log('üìã Course metadata:', courseMetadata);
        console.log('üë§ First student:', firstStudent);

        // 3. Build and set the grading session state
        const session = {
            active: true,
            startTime: new Date().toISOString(),
            gradedStudents: [],
            totalStudents: students.length,
            currentStudent: firstStudent,
            currentStudentIndex: 0
        };

        setGradingSession(session);
        setCurrentStudent(firstStudent);

        // 4. ENHANCED: Prefill the grading form with student + course details from class list
        const courseInfo = {
            code: courseMetadata?.courseCode || courseMetadata?.courseName || '',
            name: courseMetadata?.courseName || '',
            instructor: courseMetadata?.instructor || courseMetadata?.professors || '',
            term: courseMetadata?.term || ''
        };

        // 5. ENHANCED: Use assignment info from rubric if available
        const assignmentInfo = sharedRubric?.assignmentInfo ? {
            name: sharedRubric.assignmentInfo.title || '',
            dueDate: sharedRubric.assignmentInfo.dueDate || '',
            maxPoints: sharedRubric.assignmentInfo.totalPoints || 100
        } : {
            name: '',
            dueDate: '',
            maxPoints: 100
        };

        // 6. Update the grading form data
        setGradingFormData(prev => ({
            ...prev,
            student: {
                name: firstStudent.name || '',
                id: firstStudent.id || '',
                email: firstStudent.email || ''
            },
            course: courseInfo,
            assignment: assignmentInfo,
            // Reset other fields for new grading session
            feedback: { general: '', strengths: '', improvements: '' },
            attachments: [],
            videoLinks: [],
            latePolicy: { level: 'none', penaltyApplied: false },
            rubricGrading: {},
            metadata: {
                gradedBy: '',
                gradedDate: '',
                aiAssisted: false,
                rubricIntegrated: !!sharedRubric
            }
        }));

        console.log('‚úÖ Grading session initialized:', {
            sessionActive: true,
            totalStudents: students.length,
            firstStudent: firstStudent.name,
            courseInfo: courseInfo,
            assignmentInfo: assignmentInfo,
            rubricLoaded: !!sharedRubric
        });

        return true;
    }, [sharedRubric]);

    // Navigation helpers for grading sessions
    const nextStudentInSession = useCallback((saveType = 'draft') => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        const students = classList.students;

        if (currentIndex < students.length - 1) {
            const nextIndex = currentIndex + 1;
            const nextStudent = students[nextIndex];

            // Update session state
            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: nextIndex,
                currentStudent: nextStudent,
                gradedStudents: [...prev.gradedStudents, currentStudent?.id]
            }));

            setCurrentStudent(nextStudent);

            // ENHANCED: Update grading form with next student info and preserve course/assignment data
            setGradingFormData(prev => ({
                ...prev,
                student: {
                    name: nextStudent.name || '',
                    id: nextStudent.id || '',
                    email: nextStudent.email || ''
                },
                // Keep course and assignment info, reset grading-specific data
                feedback: { general: '', strengths: '', improvements: '' },
                attachments: [],
                videoLinks: [],
                latePolicy: { level: 'none', penaltyApplied: false },
                rubricGrading: {},
                metadata: {
                    ...prev.metadata,
                    gradedBy: '',
                    gradedDate: ''
                }
            }));

            console.log(`‚û°Ô∏è Moved to next student: ${nextStudent.name} (${nextIndex + 1}/${students.length})`);
            return true;
        }

        // End of session
        setGradingSession(prev => ({
            ...prev,
            active: false,
            gradedStudents: [...prev.gradedStudents, currentStudent?.id]
        }));

        console.log('üèÅ Grading session completed');
        return false;
    }, [gradingSession, classList, currentStudent]);

    const previousStudentInSession = useCallback(() => {
        if (!gradingSession?.active || !classList) return false;

        const currentIndex = gradingSession.currentStudentIndex;
        if (currentIndex > 0) {
            const prevIndex = currentIndex - 1;
            const prevStudent = classList.students[prevIndex];

            setGradingSession(prev => ({
                ...prev,
                currentStudentIndex: prevIndex,
                currentStudent: prevStudent
            }));

            setCurrentStudent(prevStudent);

            // ENHANCED: Update grading form with previous student info
            setGradingFormData(prev => ({
                ...prev,
                student: {
                    name: prevStudent.name || '',
                    id: prevStudent.id || '',
                    email: prevStudent.email || ''
                },
                // Keep course and assignment info, reset grading-specific data
                feedback: { general: '', strengths: '', improvements: '' },
                attachments: [],
                videoLinks: [],
                latePolicy: { level: 'none', penaltyApplied: false },
                rubricGrading: {},
                metadata: {
                    ...prev.metadata,
                    gradedBy: '',
                    gradedDate: ''
                }
            }));

            console.log(`‚¨ÖÔ∏è Moved to previous student: ${prevStudent.name} (${prevIndex + 1}/${classList.students.length})`);
            return true;
        }
        return false;
    }, [gradingSession, classList]);

    // Update grading session
    const updateGradingSession = useCallback((updates) => {
        setGradingSession(prev => ({
            ...prev,
            ...updates
        }));
    }, []);

    // Draft and Final Grade Management
    const saveDraft = useCallback((studentId, gradeData) => {
        setDrafts(prev => ({
            ...prev,
            [studentId]: {
                ...gradeData,
                savedAt: new Date().toISOString(),
                type: 'draft'
            }
        }));
        console.log(`üíæ Draft saved for student: ${studentId}`);
    }, []);

    const loadDraft = useCallback((studentId) => {
        return drafts[studentId] || null;
    }, [drafts]);

    const saveFinalGrade = useCallback((studentId, gradeData) => {
        setFinalGrades(prev => ({
            ...prev,
            [studentId]: {
                ...gradeData,
                finalizedAt: new Date().toISOString(),
                type: 'final'
            }
        }));
        // Remove from drafts when finalized
        setDrafts(prev => {
            const newDrafts = { ...prev };
            delete newDrafts[studentId];
            return newDrafts;
        });
        console.log(`‚úÖ Final grade saved for student: ${studentId}`);
    }, []);

    const loadFinalGrade = useCallback((studentId) => {
        return finalGrades[studentId] || null;
    }, [finalGrades]);

    const getGradeStatus = useCallback((studentId) => {
        if (finalGrades[studentId]) return 'final';
        if (drafts[studentId]) return 'draft';
        return 'not_started';
    }, [finalGrades, drafts]);

    const hasDraft = useCallback((studentId) => {
        return !!drafts[studentId];
    }, [drafts]);

    const finalizeGrade = useCallback((studentId) => {
        const draft = drafts[studentId];
        if (draft) {
            saveFinalGrade(studentId, draft);
            return true;
        }
        return false;
    }, [drafts, saveFinalGrade]);

    const unlockGrade = useCallback((studentId) => {
        const finalGrade = finalGrades[studentId];
        if (finalGrade) {
            // Move final grade back to draft
            setDrafts(prev => ({
                ...prev,
                [studentId]: {
                    ...finalGrade,
                    savedAt: new Date().toISOString(),
                    type: 'draft'
                }
            }));
            // Remove from final grades
            setFinalGrades(prev => {
                const newFinals = { ...prev };
                delete newFinals[studentId];
                return newFinals;
            });
            console.log(`üîì Grade unlocked for student: ${studentId}`);
            return true;
        }
        return false;
    }, [finalGrades]);

    // Form update functions
    const updateStudentInfo = useCallback((studentInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            student: { ...prev.student, ...studentInfo }
        }));
    }, []);

    const updateCourseInfo = useCallback((courseInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            course: { ...prev.course, ...courseInfo }
        }));
    }, []);

    const updateAssignmentInfo = useCallback((assignmentInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            assignment: { ...prev.assignment, ...assignmentInfo }
        }));
    }, []);

    const updateFeedbackInfo = useCallback((feedbackInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            feedback: { ...prev.feedback, ...feedbackInfo }
        }));
    }, []);

    const updateAttachments = useCallback((attachments) => {
        setGradingFormData(prev => ({
            ...prev,
            attachments
        }));
    }, []);

    const updateVideoLinks = useCallback((videoLinks) => {
        setGradingFormData(prev => ({
            ...prev,
            videoLinks
        }));
    }, []);

    const updateLatePolicyInfo = useCallback((latePolicyInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            latePolicy: { ...prev.latePolicy, ...latePolicyInfo }
        }));
    }, []);

    const updateRubricGrading = useCallback((rubricGradingInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            rubricGrading: { ...prev.rubricGrading, ...rubricGradingInfo }
        }));
    }, []);

    const updateMetadata = useCallback((metadataInfo) => {
        setGradingFormData(prev => ({
            ...prev,
            metadata: { ...prev.metadata, ...metadataInfo }
        }));
    }, []);

    // Rubric management functions
    const transferRubricToGrading = useCallback(() => {
        if (sharedRubric) {
            setActiveTab('grading-tool');
        }
    }, [sharedRubric]);

    const transferRubricToGradingWithDetails = useCallback((rubricData) => {
        if (rubricData) {
            setSharedRubric(rubricData);
            setActiveTab('grading-tool');
        }
    }, []);

    // Clear functions
    const clearSharedRubric = useCallback(() => {
        setSharedRubric(null);
    }, []);

    const clearGradingFormData = useCallback(() => {
        setGradingFormData({
            student: { name: '', id: '', email: '' },
            course: { code: '', name: '', instructor: '', term: '' },
            assignment: { name: '', dueDate: '', maxPoints: 100 },
            feedback: { general: '', strengths: '', improvements: '' },
            attachments: [],
            videoLinks: [],
            latePolicy: { level: 'none', penaltyApplied: false },
            rubricGrading: {},
            metadata: {
                gradedBy: '',
                gradedDate: '',
                aiAssisted: false,
                rubricIntegrated: false
            }
        });
    }, []);

    const clearRubricFormData = useCallback(() => {
        setRubricFormData(null);
    }, []);

    const clearAllData = useCallback(() => {
        clearSharedRubric();
        clearGradingFormData();
        clearRubricFormData();
        setClassList(null);
        setCurrentStudent(null);
        setGradingSession({
            active: false,
            startTime: null,
            gradedStudents: [],
            totalStudents: 0,
            currentStudent: null,
            currentStudentIndex: 0
        });
        setDrafts({});
        setFinalGrades({});
        setAIPromptFormData(null);
        setAssignmentPromptFormData(null);
    }, [clearSharedRubric, clearGradingFormData, clearRubricFormData]);

    // Session management
    const exportSession = useCallback(() => {
        const sessionData = {
            sharedRubric,
            classList,
            drafts,
            finalGrades,
            activeTab,
            currentStudent,
            gradingSession,
            sharedCourseDetails,
            aiPromptFormData,
            assignmentPromptFormData,
        };
        const dataStr = JSON.stringify(sessionData, null, 2);
        const dataBlob = new Blob([dataStr], { type: 'application/json' });
        const url = URL.createObjectURL(dataBlob);
        const link = document.createElement('a');
        link.href = url;
        const timestamp = new Date().toISOString().split('T')[0];
        link.download = `assessment_session_${timestamp}.json`;
        link.click();
        URL.revokeObjectURL(url);
    }, [sharedRubric, classList, drafts, finalGrades, activeTab, currentStudent, gradingSession, sharedCourseDetails, aiPromptFormData, assignmentPromptFormData]);

    const importSession = useCallback((sessionData) => {
        try {
            if (sessionData && typeof sessionData === 'object') {
                setSharedRubric(sessionData.sharedRubric || null);
                setClassList(sessionData.classList || null);
                setDrafts(sessionData.drafts || {});
                setFinalGrades(sessionData.finalGrades || {});
                setActiveTab(sessionData.activeTab || 'assignment-prompt-generator');
                setCurrentStudent(sessionData.currentStudent || null);
                setGradingSession(sessionData.gradingSession || {
                    active: false,
                    startTime: null,
                    gradedStudents: [],
                    totalStudents: 0,
                    currentStudent: null,
                    currentStudentIndex: 0
                });
                setSharedCourseDetails(sessionData.sharedCourseDetails || null);
                setAIPromptFormData(sessionData.aiPromptFormData || null);
                setAssignmentPromptFormData(sessionData.assignmentPromptFormData || null);
                return true;
            } else {
                throw new Error('Invalid session data format');
            }
        } catch (error) {
            console.error('Failed to import session:', error);
            return false;
        }
    }, []);

    // Context value
    const value = {
        // Core state
        sharedRubric,
        setSharedRubric,
        sharedCourseDetails,
        setSharedCourseDetails,
        activeTab,
        setActiveTab,

        // AI and Assignment Prompt Generators
        aiPromptFormData,
        setAIPromptFormData,
        assignmentPromptFormData,
        setAssignmentPromptFormData,

        // Grading form data
        gradingData: gradingFormData,
        setGradingData: setGradingFormData,
        updateStudentInfo,
        updateCourseInfo,
        updateAssignmentInfo,
        updateFeedbackInfo,
        updateAttachments,
        updateVideoLinks,
        updateLatePolicyInfo,
        updateRubricGrading,
        updateMetadata,

        // Draft and Final Grade Management
        drafts,
        finalGrades,
        saveDraft,
        loadDraft,
        saveFinalGrade,
        loadFinalGrade,
        getGradeStatus,
        hasDraft,
        finalizeGrade,
        unlockGrade,

        // Class management
        classList,
        setClassList,
        currentStudent,
        setCurrentStudent,

        // Grading session
        gradingSession,
        setGradingSession,
        nextStudentInSession,
        previousStudentInSession,
        updateGradingSession,
        initializeGradingSession,

        // Rubric form data
        rubricFormData,
        setRubricFormData,

        // Utility functions
        transferRubricToGrading,
        transferRubricToGradingWithDetails,
        clearSharedRubric,
        clearGradingFormData,
        clearRubricFormData,
        clearAllData,

        // Session management
        exportSession,
        importSession,

        // Legacy compatibility
        persistentFormData: gradingFormData,
        updatePersistentFormData: setGradingFormData
    };

    return (
        <AssessmentContext.Provider value={value}>
            {children}
        </AssessmentContext.Provider>
    );
};

export default AssessmentProvider;
