import React, { useState, useRef, useEffect } from 'react';
import {
  Download, Upload, FileText, Video, Plus, X, Save, FileDown, Bot,
  ChevronDown, ChevronUp, ArrowLeft, ArrowRight, Users, PlayCircle,
  CheckCircle, Clock, SkipForward, SkipBack, Pause, RotateCcw, AlertTriangle
} from 'lucide-react';
import { useAssessment, DEFAULT_LATE_POLICY } from './SharedContext';

const GradingTemplate = () => {
  // Get shared context
  const {
    sharedRubric,
    sharedCourseDetails,
    setSharedCourseDetails,
    gradingData: sharedGradingData,
    setGradingData: setSharedGradingData,
    updateStudentInfo,
    updateCourseInfo,
    updateAssignmentInfo,
    clearGradingFormData,
    classList,
    setClassList,
    setCurrentStudent,
    gradingSession,
    setGradingSession,
    nextStudentInSession,
    previousStudentInSession,
    updateGradingSession,
    initializeGradingSession,
    setActiveTab,
    currentStudent,
    saveDraft,
    loadDraft,
    saveFinalGrade,
    loadFinalGrade,
    finalGrades,
    getGradeStatus,
    currentLatePolicy,
    customLatePolicies
  } = useAssessment();

  // Helper function to convert HTML content to readable text format
  const renderFormattedContent = (htmlContent) => {
    if (!htmlContent) return '';
    let textContent = htmlContent
      .replace(/<br\s*\/?>/gi, '\n')
      .replace(/<\/p>/gi, '\n')
      .replace(/<p[^>]*>/gi, '')
      .replace(/<strong[^>]*>(.*?)<\/strong>/gi, '**$1**')
      .replace(/<b[^>]*>(.*?)<\/b>/gi, '**$1**')
      .replace(/<em[^>]*>(.*?)<\/em>/gi, '*$1*')
      .replace(/<i[^>]*>(.*?)<\/i>/gi, '*$1*')
      .replace(/<u[^>]*>(.*?)<\/u>/gi, '_$1_')
      .replace(/<h3[^>]*>(.*?)<\/h3>/gi, '### $1\n')
      .replace(/<li[^>]*>(.*?)<\/li>/gi, 'â€¢ $1\n')
      .replace(/<\/ul>/gi, '\n')
      .replace(/<ul[^>]*>/gi, '')
      .replace(/<\/ol>/gi, '\n')
      .replace(/<ol[^>]*>/gi, '')
      .replace(/<[^>]*>/g, '')
      .replace(/\n\s*\n/g, '\n')
      .trim();
    return textContent;
  };

  // State for all grading data
  const [localGradingData, setLocalGradingData] = useState(() => {
    if (currentStudent?.id) {
      const draft = loadDraft(currentStudent.id);
      if (draft) return draft;
    }
    if (sharedGradingData) {
      return {
        ...sharedGradingData,
        student: currentStudent || sharedGradingData.student || { name: '', id: '', email: '' }
      };
    }
    return {
      student: currentStudent || { name: '', id: '', email: '' },
      course: sharedCourseDetails?.course || { code: '', name: '', instructor: '', term: '' },
      assignment: sharedCourseDetails?.assignment || { name: '', dueDate: '', maxPoints: 100 },
      feedback: { general: '', strengths: '', improvements: '' },
      attachments: [],
      videoLinks: [],
      latePolicy: { level: 'none', penaltyApplied: false },
      metadata: { gradedBy: '', gradedDate: '', aiAssisted: false, rubricIntegrated: false },
      rubricGrading: {}
    };
  });

  const gradingData = localGradingData;
  const setGradingData = setLocalGradingData;

  // Rubric state - **MODIFIED: No default rubric**
  const [loadedRubric, setLoadedRubric] = useState(() => sharedRubric || null);
  const [showRubricComments, setShowRubricComments] = useState({});

  const fileInputRef = useRef(null);
  const rubricInputRef = useRef(null);
  const [videoLinkInput, setVideoLinkInput] = useState('');
  const [videoTitle, setVideoTitle] = useState('');

  // Sync with shared context
  useEffect(() => {
    if (sharedRubric && sharedRubric !== loadedRubric) {
      setLoadedRubric(sharedRubric);
    }
  }, [sharedRubric, loadedRubric]);

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setSharedGradingData(gradingData);
      setSharedCourseDetails({
        student: gradingData.student,
        course: gradingData.course,
        assignment: gradingData.assignment
      });
    }, 1000);
    return () => clearTimeout(timeoutId);
  }, [gradingData, setSharedGradingData, setSharedCourseDetails]);

  useEffect(() => {
    if (!currentStudent?.id) return;
    const status = getGradeStatus(currentStudent.id);
    let saved = (status === 'final' && loadFinalGrade) ? loadFinalGrade(currentStudent.id) : (status === 'draft' ? loadDraft(currentStudent.id) : null);
    if (saved) {
      setLocalGradingData(prev => ({
        ...saved,
        student: currentStudent,
        course: prev.course,
        assignment: prev.assignment,
        metadata: prev.metadata
      }));
    } else {
      resetGradingForm();
    }
  }, [currentStudent, getGradeStatus, loadDraft, loadFinalGrade]);

  // Safely access late policy levels
  const getPolicyById = (policyId) => {
    if (!policyId) return null;
    if (policyId === DEFAULT_LATE_POLICY.id) return DEFAULT_LATE_POLICY;
    return customLatePolicies.find(p => p.id === policyId) || null;
  };

  const getSafeLatePolicy = (level, policyId) => {
    const policy = getPolicyById(policyId) || currentLatePolicy || DEFAULT_LATE_POLICY;
    const activeLevels = policy.levels || DEFAULT_LATE_POLICY.levels;
    if (!level || typeof level !== "string" || !activeLevels[level]) {
      return activeLevels.none;
    }
    return activeLevels[level];
  };


  // Calculate total score
  const calculateTotalScore = () => {
    let rawScore = 0;
    // âœ… FIX: Check for loadedRubric and its criteria before calculating
    if (loadedRubric && loadedRubric.criteria && gradingData.rubricGrading) {
      rawScore = Object.values(gradingData.rubricGrading).reduce((total, grading) => {
        const criterion = loadedRubric.criteria.find(c => c.id === grading.criterionId);
        if (criterion && grading.selectedLevel) {
          const level = loadedRubric.rubricLevels.find(l => l.level === grading.selectedLevel);
          if (level) {
            return total + (criterion.maxPoints * level.multiplier);
          }
        }
        return total;
      }, 0);
    }
    const policyId = gradingData.latePolicy?.policyId;
    const latePolicyLevel = getSafeLatePolicy(gradingData.latePolicy?.level, policyId);

    const finalScore = rawScore * latePolicyLevel.multiplier;
    return {
      rawScore,
      finalScore,
      penaltyApplied: gradingData.latePolicy.level !== 'none',
      latePolicyDescription: latePolicyLevel.description
    };
  };

