    // Open inline editor for text editing - FIXED to preserve content across edits
    const openInlineEditor = (content, field, onSave, criterionId = null, level = null, type = 'assignment') => {
        // Initialize the ref with current content (preserve HTML)
        richTextContentRef.current = content || '';

        setInlineEditor({
            show: true,
            content: content || '',
            field,
            onSave,
            criterionId,
            level,
            type
        });

        // Focus the editor after it's rendered and ensure content is set
        setTimeout(() => {
            const editor = editorRef.current;
            if (editor) {
                // FIXED: Preserve HTML formatting when opening editor
                editor.innerHTML = content || '';
                richTextContentRef.current = content || '';

                // Remove any existing paste handler to prevent duplicates
                if (editor._pasteHandlerAttached) {
                    editor._pasteHandlerAttached = false;
                }

                editor.focus();
                // Place cursor at end
                const range = document.createRange();
                const selection = window.getSelection();
                range.selectNodeContents(editor);
                range.collapse(false);
                selection.removeAllRanges();
                selection.addRange(range);
            }
        }, 100);
    };

    // Setup simple editor functionality (exact copy from working editor.js)
    useEffect(() => {
        const editor = editorRef.current;
        if (editor && inlineEditor.show) {
            // Prevent duplicate event listeners
            if (editor._pasteHandlerAttached) return;
            editor._pasteHandlerAttached = true;

            const handlePaste = (e) => {
                e.preventDefault();

                const clipboard = (e.clipboardData || window.clipboardData);
                const htmlData = clipboard.getData('text/html');
                const textData = clipboard.getData('text/plain');

                let clean;
                if (htmlData) {
                    clean = sanitizeHtml(htmlData);
                } else {
                    // fallback: plain text → convert line breaks to <br>
                    clean = textData
                        .replace(/&/g, '&amp;')
                        .replace(/</g, '&lt;')
                        .replace(/>/g, '&gt;')
                        .replace(/\r?\n/g, '<br>');
                }

                // Insert the cleaned-up HTML at the caret
                document.execCommand('insertHTML', false, clean);
                richTextContentRef.current = editor.innerHTML;
            };

            const handleInput = () => {
                richTextContentRef.current = editor.innerHTML;
            };

            editor.addEventListener('paste', handlePaste);
            editor.addEventListener('input', handleInput);

            return () => {
                editor.removeEventListener('paste', handlePaste);
                editor.removeEventListener('input', handleInput);
                editor._pasteHandlerAttached = false;
            };
        }
    }, [inlineEditor.show]);

