                        {/* File Attachments */}
                        <div style={{ marginBottom: '2rem' }}>
                            <h3 style={{ fontSize: '1.125rem', fontWeight: '600', marginBottom: '1rem', color: '#374151' }}>
                                ðŸ“Ž File Attachments
                            </h3>
                            <div style={{
                                border: '2px dashed #d1d5db',
                                borderRadius: '0.5rem',
                                padding: '1.5rem',
                                textAlign: 'center'
                            }}>
                                <input
                                    ref={fileInputRef}
                                    type="file"
                                    multiple
                                    onChange={(e) => handleFileUpload(e.target.files)}
                                    style={{ display: 'none' }}
                                />
                                <button
                                    onClick={() => fileInputRef.current?.click()}
                                    style={{
                                        display: 'flex',
                                        alignItems: 'center',
                                        gap: '0.5rem',
                                        background: '#3b82f6',
                                        color: 'white',
                                        padding: '0.75rem 1.5rem',
                                        borderRadius: '0.5rem',
                                        border: 'none',
                                        cursor: 'pointer',
                                        margin: '0 auto'
                                    }}
                                >
                                    <Plus size={20} />
                                    Add Files
                                </button>
                                <p style={{ color: '#6b7280', marginTop: '0.5rem', fontSize: '0.875rem' }}>
                                    Upload images, documents, or other files relevant to the assignment
                                </p>
                            </div>

                            {gradingData.attachments.length > 0 && (
                                <div style={{ marginTop: '1rem' }}>
                                    <h4 style={{ fontWeight: '600', color: '#374151', marginBottom: '0.75rem' }}>
                                        Uploaded Files ({gradingData.attachments.length})
                                    </h4>
                                    <div style={{
                                        display: 'grid',
                                        gridTemplateColumns: 'repeat(auto-fill, minmax(200px, 1fr))',
                                        gap: '1rem'
                                    }}>
                                        {gradingData.attachments.map((file) => (
                                            <div key={file.id} style={{
                                                border: '1px solid #d1d5db',
                                                borderRadius: '0.5rem',
                                                overflow: 'hidden',
                                                background: 'white'
                                            }}>
                                                {file.type.startsWith('image/') ? (
                                                    <div style={{ position: 'relative' }}>
                                                        <img
                                                            src={file.base64Data || URL.createObjectURL(file.file)}
                                                            alt={file.name}
                                                            style={{
                                                                width: '100%',
                                                                height: '150px',
                                                                objectFit: 'cover'
                                                            }}
                                                        />
                                                        <button
                                                            onClick={() => removeAttachment(file.id)}
                                                            style={{
                                                                position: 'absolute',
                                                                top: '0.5rem',
                                                                right: '0.5rem',
                                                                background: '#dc2626',
                                                                color: 'white',
                                                                borderRadius: '50%',
                                                                width: '24px',
                                                                height: '24px',
                                                                border: 'none',
                                                                cursor: 'pointer',
                                                                display: 'flex',
                                                                alignItems: 'center',
                                                                justifyContent: 'center'
                                                            }}
                                                        >
                                                            <X size={12} />
                                                        </button>
                                                    </div>
                                                ) : (
                                                    <div style={{ padding: '1rem' }}>
                                                        <FileText size={40} style={{ color: '#6b7280', margin: '0 auto 0.5rem auto', display: 'block' }} />
                                                    </div>
                                                )}
                                                <div style={{ padding: '0.75rem' }}>
                                                    <p style={{ fontWeight: '500', fontSize: '0.875rem', marginBottom: '0.25rem' }}>
                                                        {file.name}
                                                    </p>
                                                    <p style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                                                        {(file.size / 1024).toFixed(1)} KB
                                                    </p>
                                                    {!file.type.startsWith('image/') && (
                                                        <button
                                                            onClick={() => removeAttachment(file.id)}
                                                            style={{
                                                                marginTop: '0.5rem',
                                                                color: '#dc2626',
                                                                background: 'none',
                                                                border: 'none',
                                                                fontSize: '0.75rem',
                                                                cursor: 'pointer'
                                                            }}
                                                        >
                                                            Remove
                                                        </button>
                                                    )}
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>

                        {/* Video Links Section */}
                        <div style={{ marginBottom: '2rem' }}>
                            <h3 style={{ fontSize: '1.125rem', fontWeight: '600', marginBottom: '1rem', color: '#374151' }}>
                                ðŸŽ¥ Video Review Links
                            </h3>
                            <div style={{
                                background: 'linear-gradient(135deg, #faf5ff 0%, #f3e8ff 100%)',
                                border: '1px solid #e9d5ff',
                                borderRadius: '0.5rem',
                                padding: '1.5rem'
                            }}>
                                <div style={{ display: 'flex', gap: '1rem', marginBottom: '1rem', flexWrap: 'wrap' }}>
                                    <div style={{ flex: 1, minWidth: '200px' }}>
                                        <label style={{
                                            display: 'block',
                                            fontSize: '0.875rem',
                                            fontWeight: '500',
                                            color: '#374151',
                                            marginBottom: '0.25rem'
                                        }}>
                                            Video URL
                                        </label>
                                        <input
                                            type="url"
                                            value={videoLinkInput}
                                            onChange={(e) => setVideoLinkInput(e.target.value)}
                                            placeholder="https://example.com/video..."
                                            style={{
                                                width: '100%',
                                                padding: '0.75rem',
                                                border: '1px solid #d1d5db',
                                                borderRadius: '0.5rem',
                                                fontSize: '0.875rem'
                                            }}
                                        />
                                    </div>
                                    <div style={{ minWidth: '150px' }}>
                                        <label style={{
                                            display: 'block',
                                            fontSize: '0.875rem',
                                            fontWeight: '500',
                                            color: '#374151',
                                            marginBottom: '0.25rem'
                                        }}>
                                            Title (Optional)
                                        </label>
                                        <input
                                            type="text"
                                            value={videoTitle}
                                            onChange={(e) => setVideoTitle(e.target.value)}
                                            placeholder="Video title"
                                            style={{
                                                width: '100%',
                                                padding: '0.75rem',
                                                border: '1px solid #d1d5db',
                                                borderRadius: '0.5rem',
                                                fontSize: '0.875rem'
                                            }}
                                        />
                                    </div>
                                    <div style={{ display: 'flex', alignItems: 'end' }}>
                                        <button
                                            onClick={addVideoLink}
                                            disabled={!videoLinkInput.trim()}
                                            style={{
                                                display: 'flex',
                                                alignItems: 'center',
                                                gap: '0.5rem',
                                                background: videoLinkInput.trim() ? '#7c3aed' : '#9ca3af',
                                                color: 'white',
                                                padding: '0.75rem 1rem',
                                                borderRadius: '0.5rem',
                                                border: 'none',
                                                cursor: videoLinkInput.trim() ? 'pointer' : 'not-allowed'
                                            }}
                                        >
                                            <Plus size={18} />
                                            Add Video
                                        </button>
                                    </div>
                                </div>
                            </div>

                            {gradingData.videoLinks.length > 0 && (
                                <div style={{ marginTop: '1.5rem' }}>
                                    <h4 style={{ fontWeight: '600', color: '#374151', marginBottom: '0.75rem' }}>
                                        Added Video Links ({gradingData.videoLinks.length})
                                    </h4>
                                    <div style={{ display: 'flex', flexDirection: 'column', gap: '1rem' }}>
                                        {gradingData.videoLinks.map((videoLink) => (
                                            <div key={videoLink.id} style={{
                                                border: '1px solid #d1d5db',
                                                borderRadius: '0.5rem',
                                                overflow: 'hidden'
                                            }}>
                                                <div style={{
                                                    background: '#f9fafb',
                                                    padding: '1rem',
                                                    display: 'flex',
                                                    alignItems: 'center',
                                                    justifyContent: 'space-between'
                                                }}>
                                                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.75rem' }}>
                                                        <div style={{
                                                            width: '32px',
                                                            height: '32px',
                                                            background: '#7c3aed',
                                                            borderRadius: '0.25rem',
                                                            display: 'flex',
                                                            alignItems: 'center',
                                                            justifyContent: 'center'
                                                        }}>
                                                            <Video size={16} style={{ color: 'white' }} />
                                                        </div>
                                                        <div>
                                                            <p style={{ fontWeight: '500', color: '#374151' }}>{videoLink.title}</p>
                                                            <p style={{ fontSize: '0.875rem', color: '#6b7280' }}>External Link</p>
                                                        </div>
                                                    </div>
                                                    <div style={{ display: 'flex', alignItems: 'center', gap: '0.5rem' }}>
                                                        <a
                                                            href={videoLink.url}
                                                            target="_blank"
                                                            rel="noopener noreferrer"
                                                            style={{
                                                                background: '#3b82f6',
                                                                color: 'white',
                                                                padding: '0.25rem 0.75rem',
                                                                borderRadius: '0.25rem',
                                                                fontSize: '0.875rem',
                                                                textDecoration: 'none'
                                                            }}
                                                        >
                                                            Open Link
                                                        </a>
                                                        <button
                                                            onClick={() => removeVideoLink(videoLink.id)}
                                                            style={{
                                                                color: '#dc2626',
                                                                background: 'none',
                                                                border: 'none',
                                                                cursor: 'pointer',
                                                                padding: '0.25rem'
                                                            }}
                                                        >
                                                            <X size={16} />
                                                        </button>
                                                    </div>
                                                </div>
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default GradingTemplate;
