import React, { useState } from 'react';
import { Download, Sparkles, FileText, ArrowRight, Lightbulb, BookOpen, Code, Plus, Minus } from 'lucide-react';
import { useAssessment } from './SharedContext';

const AssignmentPromptGenerator = () => {
    const {
        assignmentPromptFormData,
        updateAssignmentPromptFormData,
        initializeAssignmentPromptFormData
    } = useAssessment();

    // Initialize form data if it doesn't exist
    React.useEffect(() => {
        if (!assignmentPromptFormData) {
            initializeAssignmentPromptFormData();
        }
    }, [assignmentPromptFormData, initializeAssignmentPromptFormData]);

    const [generatedPrompt, setGeneratedPrompt] = useState('');
    const [showPrompt, setShowPrompt] = useState(false);
    const [generatedHTML, setGeneratedHTML] = useState('');
    const [showHTML, setShowHTML] = useState(false);

    const handleInputChange = (field, value) => {
        updateAssignmentPromptFormData(field, value);
    };

    // CLO management functions
    const addCLO = () => {
        const currentCLOs = formData.clos || [];
        if (currentCLOs.length < 10) {
            const newCLO = { id: Date.now(), text: '' };
            const updatedCLOs = [...currentCLOs, newCLO];
            handleInputChange('clos', updatedCLOs);
        }
    };

    const removeCLO = (cloId) => {
        const currentCLOs = formData.clos || [];
        if (currentCLOs.length > 1) {
            const updatedCLOs = currentCLOs.filter(clo => clo.id !== cloId);
            handleInputChange('clos', updatedCLOs);
        }
    };

    const updateCLO = (cloId, text) => {
        const currentCLOs = formData.clos || [];
        const updatedCLOs = currentCLOs.map(clo =>
            clo.id === cloId ? { ...clo, text } : clo
        );
        handleInputChange('clos', updatedCLOs);
    };

    // Use shared form data, fallback to defaults if not available
    const formData = assignmentPromptFormData || {
        assignmentTitle: '',
        assignmentNumber: '',
        weightPercentage: '',
        courseCode: '',
        programLevel: '',
        subjectArea: '',
        assignmentDescription: '',
        rationale: 'This assignment will evaluate the following Course Learning Outcomes:',
        clos: [
            { id: 1, text: '' },
            { id: 2, text: '' },
            { id: 3, text: '' },
            { id: 4, text: '' }
        ],
        planningPhase: '',
        productionPhase: '',
        technicalRequirements: '',
        submissionRequirements: '',
        gradingBreakdown: '',
        keySuccessCriteria: '',
        tipsForSuccess: '',
        gradingMethod: 'rubric',
        gradingDetails: '',
        dueWeek: '',
        dueDateCustom: '',
        submissionFolder: 'Assignment',
        assignmentName: '',
        specialInstructions: ''
    };

    const generateAIPrompt = () => {
        // Generate CLO text for the prompt
        const clos = formData.clos || [];
        const closText = clos
            .map((clo, index) => `- CLO${index + 1}: ${clo.text}`)
            .join('\n');

        // Generate due date text
        const dueText = formData.dueWeek ?
            `(Week ${formData.dueWeek})${formData.dueDateCustom ? ` - ${formData.dueDateCustom}` : ''}` :
            formData.dueDateCustom || 'See your Instructional Plan for exact due dates.';

        const prompt = `Create HTML code for an assignment details page that matches EXACTLY the structure and format of the provided template for D2L. Use the following assignment information:

**Assignment Information:**
- Assignment Title: ${formData.assignmentTitle}
- Assignment Number: ${formData.assignmentNumber}
- Weight: ${formData.weightPercentage}%
- Course Code: ${formData.courseCode}
- Program Level: ${formData.programLevel}
- Subject Area: ${formData.subjectArea}
- Due Date: ${dueText}
- Submission Folder: ${formData.submissionFolder} ${formData.assignmentNumber}${formData.assignmentName ? ': ' + formData.assignmentName : ''}

**Assignment Content:**
- Description: ${formData.assignmentDescription}
- Rationale: ${formData.rationale}
- Course Learning Outcomes:
${closText}
- Planning Phase: ${formData.planningPhase}
- Production Phase: ${formData.productionPhase}
- Technical Requirements: ${formData.technicalRequirements}
- Submission Requirements: ${formData.submissionRequirements}
- Grading Breakdown: ${formData.gradingBreakdown}
- Key Success Criteria: ${formData.keySuccessCriteria}
- Tips for Success: ${formData.tipsForSuccess}
- Grading Method: ${formData.gradingMethod}
- Grading Details: ${formData.gradingDetails}
- Special Instructions: ${formData.specialInstructions}

**CRITICAL REQUIREMENTS:**
1. Use EXACTLY this HTML structure for D2L content area (NO DOCTYPE, html, head, or body tags):

\`\`\`html
<div class="container-fluid">
<div class="row justify-content-center">
<div class="col-sm-12 col-md-10">
<h1>[ASSIGNMENT TITLE]</h1>
<p><strong>Due:</strong>[DUE DATE]</p>
<p><strong>Weight:</strong> [WEIGHT]% of final grade</p>
<p><strong>Submit to: </strong>The <strong>[SUBMISSION FOLDER]</strong> submission folder. Submission folders can be accessed by selecting <strong>Course Tools</strong> and then <strong>Assignments</strong> on the course navigation bar.</p>
<h2>Description</h2>
<p>[DESCRIPTION]</p>
<h3>Rationale</h3>
<p>[RATIONALE TEXT]</p>
<ul>
[CLO LIST ITEMS]
</ul>
<h2>Directions</h2>
<h3>Planning Phase:</h3>
[PLANNING PHASE CONTENT]
<h3>Production Phase:</h3>
[PRODUCTION PHASE CONTENT]
<h3>Technical Requirements:</h3>
[TECHNICAL REQUIREMENTS]
<h3>Submission Requirements:</h3>
[SUBMISSION REQUIREMENTS]
<h2>How Your Assignment Will be Graded</h2>
[GRADING BREAKDOWN]
<p><strong>Key Success Criteria:</strong></p>
[KEY SUCCESS CRITERIA]
<p>See your Program Handbook for the late policy.</p>
<h3>Tips for Success:</h3>
[TIPS FOR SUCCESS]
</div>
</div>
</div>
\`\`\`

2. Replace all bracketed placeholders with the appropriate information provided above
3. This is for D2L content area - do NOT include DOCTYPE, html, head, or body tags
4. Follow the content structure exactly as shown in the example provided
5. Use proper HTML formatting with &lt;strong&gt;, &lt;h1&gt;, &lt;h2&gt;, &lt;h3&gt;, &lt;ul&gt;, &lt;li&gt;, &lt;ol&gt; tags as appropriate
6. For the CLO list, create &lt;li&gt; tags with &lt;strong&gt;CLO#:&lt;/strong&gt; format for each Course Learning Outcome
7. Convert any bulleted or numbered lists in the content to proper HTML lists
8. For grading section: if rubric is selected, use standard rubric text; if not, use the custom grading details

Generate complete, ready-to-paste HTML code for D2L content area.`;

        setGeneratedPrompt(prompt);
        setShowPrompt(true);
        setShowHTML(false);
    };

    const generateAssignmentHTML = () => {
        // Generate CLO list HTML
        const clos = formData.clos || [];
        const closHTML = clos
            .filter(clo => clo.text.trim() !== '')
            .map((clo, index) => `<li><strong>CLO ${index + 1}:</strong> ${clo.text}</li>`)
            .join('\n');

        // Generate due date text
        const dueText = formData.dueWeek ?
            `(Week ${formData.dueWeek})${formData.dueDateCustom ? ` - ${formData.dueDateCustom}` : ''}` :
            formData.dueDateCustom || 'See your Instructional Plan for exact due dates.';

        const assignmentHTML = `<div class="container-fluid">
<div class="row justify-content-center">
<div class="col-sm-12 col-md-10">
<h1>${formData.assignmentTitle}</h1>
<p><strong>Due:</strong>${dueText}</p>
<p><strong>Weight:</strong> ${formData.weightPercentage}% of final grade</p>
<p><strong>Submit to: </strong>The <strong>${formData.submissionFolder} ${formData.assignmentNumber}${formData.assignmentName ? ': ' + formData.assignmentName : ''}</strong> submission folder. Submission folders can be accessed by selecting <strong>Course Tools</strong> and then <strong>Assignments</strong> on the course navigation bar.</p>
<h2>Description</h2>
<p>${formData.assignmentDescription || '[Please provide a short description of the assignment]'}</p>
<h3>Rationale</h3>
<p>${formData.rationale || 'This assignment will evaluate the following Course Learning Outcomes:'}</p>
<ul>
${closHTML || '<li><strong>CLO 1:</strong> Course Learning Outcome 1</li>\n<li><strong>CLO 2:</strong> Course Learning Outcome 2</li>\n<li><strong>CLO 3:</strong> Course Learning Outcome 3</li>\n<li><strong>CLO 4:</strong> Course Learning Outcome 4</li>'}
</ul>
<h2>Directions</h2>
${formData.planningPhase ? `<h3>Planning Phase:</h3>\n${formData.planningPhase}\n` : ''}
${formData.productionPhase ? `<h3>Production Phase:</h3>\n${formData.productionPhase}\n` : ''}
${formData.technicalRequirements ? `<h3>Technical Requirements:</h3>\n${formData.technicalRequirements}\n` : ''}
${formData.submissionRequirements ? `<h3>Submission Requirements:</h3>\n${formData.submissionRequirements}\n` : ''}
<h2>How Your Assignment Will be Graded</h2>
${formData.gradingMethod === 'rubric' ?
                '<p>A rubric has been created and can be found attached to the submission folder. Submission folders can be accessed by selecting <strong>Course Tools</strong> and then <strong>Assignments</strong> on the course navigation bar.</p>' :
                `<p><strong>Grading Details:</strong> ${formData.gradingDetails || 'Provide details as appropriate.'}</p>`
            }
${formData.gradingBreakdown ? `${formData.gradingBreakdown}\n` : ''}
${formData.keySuccessCriteria ? `<p><strong>Key Success Criteria:</strong></p>\n${formData.keySuccessCriteria}\n` : ''}
<p>See your Program Handbook for the late policy.</p>
${formData.tipsForSuccess ? `<h3>Tips for Success:</h3>\n${formData.tipsForSuccess}` : ''}
${formData.specialInstructions ? `<h3>Additional Information:</h3>\n<p>${formData.specialInstructions}</p>` : ''}
</div>
</div>
</div>`;

        setGeneratedHTML(assignmentHTML);
        setShowHTML(true);
        setShowPrompt(false);
    };

    const exportPrompt = () => {
        const element = document.createElement('a');
        const file = new Blob([generatedPrompt], { type: 'text/plain' });
        element.href = URL.createObjectURL(file);
        element.download = `assignment-ai-prompt-${formData.assignmentTitle.replace(/\s+/g, '-').toLowerCase()}-${formData.assignmentNumber}.txt`;
        document.body.appendChild(element);
        element.click();
        document.body.removeChild(element);
    };

    const exportHTML = () => {
        const element = document.createElement('a');
        const file = new Blob([generatedHTML], { type: 'text/html' });
        element.href = URL.createObjectURL(file);
        element.download = `${formData.submissionFolder}_${formData.assignmentNumber}_${formData.assignmentTitle.replace(/\s+/g, '_').toLowerCase()}_D2L.html`;
        document.body.appendChild(element);
        element.click();
        document.body.removeChild(element);
    };

    const copyToClipboard = (content) => {
        navigator.clipboard.writeText(content);
        alert('Content copied to clipboard!');
    };

    const isFormValid = formData.assignmentTitle && formData.assignmentNumber &&
        formData.weightPercentage && formData.subjectArea && formData.assignmentDescription &&
        formData.clos && formData.clos.some(clo => clo.text.trim() !== '') &&
        (formData.dueWeek || formData.dueDateCustom);

    return (
        <div className="min-h-screen bg-gray-50 p-6">
            <div className="max-w-4xl mx-auto bg-white rounded-lg shadow-lg">
                {/* Header */}
                <div className="bg-orange-900 text-white p-6 rounded-t-lg">
                    <div className="flex items-center gap-3">
                        <BookOpen className="w-8 h-8" />
                        <div>
                            <h1 className="text-2xl font-bold">Assignment Prompt AI Generator</h1>
                            <p className="text-orange-100">Generate AI prompts and HTML for assignment detail pages</p>
                        </div>
                    </div>
                </div>

                <div className="p-6">
                    {!showPrompt && !showHTML ? (
                        <div>
                            {/* Workflow Guide */}
                            <div className="bg-orange-50 border border-orange-200 rounded-lg p-4 mb-6">
                                <div className="flex items-start gap-3">
                                    <Lightbulb className="w-5 h-5 text-orange-600 mt-0.5" />
                                    <div>
                                        <h3 className="font-semibold text-orange-900 mb-2">How This Works</h3>
                                        <div className="flex items-center gap-2 text-sm text-orange-800 mb-2 flex-wrap">
                                            <span className="bg-orange-200 rounded-full w-6 h-6 flex items-center justify-center font-bold">1</span>
                                            <span>Fill assignment details & CLOs</span>
                                            <ArrowRight className="w-4 h-4" />
                                            <span className="bg-orange-200 rounded-full w-6 h-6 flex items-center justify-center font-bold">2</span>
                                            <span>Add detailed sections</span>
                                            <ArrowRight className="w-4 h-4" />
                                            <span className="bg-orange-200 rounded-full w-6 h-6 flex items-center justify-center font-bold">3</span>
                                            <span>Generate D2L-ready content</span>
                                        </div>
                                        <p className="text-xs text-orange-700">
                                             <strong>Note:</strong> This creates comprehensive assignment pages in the exact format for D2L content areas. The HTML output can be pasted directly into D2L's HTML editor. All form data is auto-saved when switching tabs.
                                        </p>
                                    </div>
                                </div>
                            </div>

                            {/* Assignment Information Form */}
                            <div className="space-y-6">
                                {/* Basic Assignment Info */}
                                <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-blue-800 mb-4">Basic Assignment Information</h3>
                                    <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Assignment Title *</label>
                                            <input
                                                type="text"
                                                value={formData.assignmentTitle}
                                                onChange={(e) => handleInputChange('assignmentTitle', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                                placeholder="e.g., Digital Portfolio Project"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Assignment Number *</label>
                                            <input
                                                type="text"
                                                value={formData.assignmentNumber}
                                                onChange={(e) => handleInputChange('assignmentNumber', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                                placeholder="e.g., 3"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Weight (% of Final Grade) *</label>
                                            <input
                                                type="number"
                                                value={formData.weightPercentage}
                                                onChange={(e) => handleInputChange('weightPercentage', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                                placeholder="e.g., 25"
                                                min="0"
                                                max="100"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Course Code</label>
                                            <input
                                                type="text"
                                                value={formData.courseCode}
                                                onChange={(e) => handleInputChange('courseCode', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                                placeholder="e.g., DSGN1140"
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Program Level</label>
                                            <select
                                                value={formData.programLevel}
                                                onChange={(e) => handleInputChange('programLevel', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                            >
                                                <option value="">Select Level</option>
                                                <option value="Certificate">Certificate</option>
                                                <option value="Diploma">Diploma</option>
                                                <option value="Advanced Diploma">Advanced Diploma</option>
                                                <option value="Degree">Degree</option>
                                                <option value="Graduate Certificate">Graduate Certificate</option>
                                                <option value="Post-Graduate">Post-Graduate</option>
                                            </select>
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Subject Area *</label>
                                            <input
                                                type="text"
                                                value={formData.subjectArea}
                                                onChange={(e) => handleInputChange('subjectArea', e.target.value)}
                                                className="w-full p-3 border rounded-lg"
                                                placeholder="e.g., Graphic Design, Web Development"
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Assignment Content */}
                                <div className="bg-green-50 border border-green-200 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-green-800 mb-4">Assignment Content</h3>
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Assignment Description *</label>
                                            <textarea
                                                value={formData.assignmentDescription}
                                                onChange={(e) => handleInputChange('assignmentDescription', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-24"
                                                placeholder="Provide a clear description of what students need to create or submit..."
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Course Learning Outcomes */}
                                <div className="bg-purple-50 border border-purple-200 rounded-lg p-6">
                                    <div className="flex items-center justify-between mb-4">
                                        <h3 className="text-lg font-semibold text-purple-800">Course Learning Outcomes</h3>
                                        <div className="flex gap-2">
                                            <button
                                                type="button"
                                                onClick={addCLO}
                                                disabled={(formData.clos || []).length >= 10}
                                                className={`flex items-center gap-2 px-3 py-1 text-sm rounded-lg font-medium ${(formData.clos || []).length >= 10
                                                        ? 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                                        : 'bg-purple-600 text-white hover:bg-purple-700'
                                                    }`}
                                            >
                                                <Plus size={16} />
                                                Add CLO
                                            </button>
                                            <span className="text-sm text-purple-600 self-center">
                                                {(formData.clos || []).length}/10 CLOs
                                            </span>
                                        </div>
                                    </div>

                                    <div className="mb-4">
                                        <label className="block text-sm font-medium text-gray-700 mb-1">
                                            Rationale Statement
                                        </label>
                                        <textarea
                                            value={formData.rationale}
                                            onChange={(e) => handleInputChange('rationale', e.target.value)}
                                            className="w-full p-3 border rounded-lg h-20"
                                            placeholder="This assignment will evaluate the following Course Learning Outcomes:"
                                        />
                                        <p className="text-xs text-gray-500 mt-1">
                                            This text will appear above the CLO list in your assignment.
                                        </p>
                                    </div>

                                    <div className="space-y-3">
                                        {(formData.clos || []).map((clo, index) => (
                                            <div key={clo.id} className="flex gap-3 items-start">
                                                <div className="flex-shrink-0 w-16 pt-3">
                                                    <span className="text-sm font-medium text-purple-700">
                                                        CLO{index + 1}:
                                                    </span>
                                                </div>
                                                <div className="flex-grow">
                                                    <input
                                                        type="text"
                                                        value={clo.text}
                                                        onChange={(e) => updateCLO(clo.id, e.target.value)}
                                                        className="w-full p-3 border rounded-lg"
                                                        placeholder={`Course Learning Outcome ${index + 1}`}
                                                    />
                                                </div>
                                                <button
                                                    type="button"
                                                    onClick={() => removeCLO(clo.id)}
                                                    disabled={(formData.clos || []).length === 1}
                                                    className={`flex-shrink-0 mt-3 w-8 h-8 rounded-lg flex items-center justify-center ${(formData.clos || []).length === 1
                                                            ? 'bg-gray-200 text-gray-400 cursor-not-allowed'
                                                            : 'bg-red-100 text-red-600 hover:bg-red-200'
                                                        }`}
                                                >
                                                    <Minus size={14} />
                                                </button>
                                            </div>
                                        ))}
                                    </div>

                                    <div className="mt-4 p-3 bg-purple-50 border border-purple-200 rounded">
                                        <p className="text-sm text-purple-700">
                                            <strong> Tip:</strong> Add up to 10 Course Learning Outcomes. Each CLO should clearly describe what students will achieve by completing this assignment.
                                        </p>
                                    </div>
                                </div>

                                {/* Assignment Details */}
                                <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-yellow-800 mb-4">Assignment Instructions & Due Date</h3>
                                    <div className="space-y-4">
                                        <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-1">Due Week *</label>
                                                <select
                                                    value={formData.dueWeek}
                                                    onChange={(e) => handleInputChange('dueWeek', e.target.value)}
                                                    className="w-full p-3 border rounded-lg"
                                                >
                                                    <option value="">Select Week</option>
                                                    {[...Array(15)].map((_, i) => (
                                                        <option key={i + 1} value={i + 1}>Week {i + 1}</option>
                                                    ))}
                                                </select>
                                            </div>
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-1">Custom Due Date (Optional)</label>
                                                <input
                                                    type="date"
                                                    value={formData.dueDateCustom}
                                                    onChange={(e) => handleInputChange('dueDateCustom', e.target.value)}
                                                    className="w-full p-3 border rounded-lg"
                                                />
                                            </div>
                                        </div>

                                        <div className="grid grid-cols-1 md:grid-cols-3 gap-4">
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-1">Submission Folder</label>
                                                <input
                                                    type="text"
                                                    value={formData.submissionFolder}
                                                    onChange={(e) => handleInputChange('submissionFolder', e.target.value)}
                                                    className="w-full p-3 border rounded-lg"
                                                    placeholder="Assignment"
                                                />
                                            </div>
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-1">Assignment Name (Optional)</label>
                                                <input
                                                    type="text"
                                                    value={formData.assignmentName}
                                                    onChange={(e) => handleInputChange('assignmentName', e.target.value)}
                                                    className="w-full p-3 border rounded-lg"
                                                    placeholder="e.g., Ball Bounce"
                                                />
                                            </div>
                                        </div>
                                    </div>
                                </div>

                                {/* Detailed Assignment Sections */}
                                <div className="bg-indigo-50 border border-indigo-200 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-indigo-800 mb-4">Assignment Sections</h3>
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Planning Phase</label>
                                            <textarea
                                                value={formData.planningPhase}
                                                onChange={(e) => handleInputChange('planningPhase', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-32"
                                                placeholder="Describe the planning steps students should take..."
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Production Phase</label>
                                            <textarea
                                                value={formData.productionPhase}
                                                onChange={(e) => handleInputChange('productionPhase', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-32"
                                                placeholder="Describe the production/creation steps..."
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Technical Requirements</label>
                                            <textarea
                                                value={formData.technicalRequirements}
                                                onChange={(e) => handleInputChange('technicalRequirements', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-24"
                                                placeholder="List technical specifications, software, formats, etc..."
                                            />
                                        </div>
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Submission Requirements</label>
                                            <textarea
                                                value={formData.submissionRequirements}
                                                onChange={(e) => handleInputChange('submissionRequirements', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-24"
                                                placeholder="List what files/formats students must submit..."
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Grading Information */}
                                <div className="bg-red-50 border border-red-200 rounded-lg p-6">
                                    <h3 className="text-lg font-semibold text-red-800 mb-4">Grading Information</h3>
                                    <div className="space-y-4">
                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Grading Method</label>
                                            <div className="flex gap-4">
                                                <label className="flex items-center">
                                                    <input
                                                        type="radio"
                                                        name="gradingMethod"
                                                        value="rubric"
                                                        checked={formData.gradingMethod === 'rubric'}
                                                        onChange={(e) => handleInputChange('gradingMethod', e.target.value)}
                                                        className="mr-2"
                                                    />
                                                    Rubric-based grading
                                                </label>
                                                <label className="flex items-center">
                                                    <input
                                                        type="radio"
                                                        name="gradingMethod"
                                                        value="other"
                                                        checked={formData.gradingMethod === 'other'}
                                                        onChange={(e) => handleInputChange('gradingMethod', e.target.value)}
                                                        className="mr-2"
                                                    />
                                                    Other grading method
                                                </label>
                                            </div>
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Grading Breakdown (Optional)</label>
                                            <textarea
                                                value={formData.gradingBreakdown}
                                                onChange={(e) => handleInputChange('gradingBreakdown', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-32"
                                                placeholder="E.g., Technical Execution (60%), Planning & Process (25%), Professional Presentation (15%)..."
                                            />
                                        </div>

                                        {formData.gradingMethod === 'other' && (
                                            <div>
                                                <label className="block text-sm font-medium text-gray-700 mb-1">Custom Grading Details</label>
                                                <textarea
                                                    value={formData.gradingDetails}
                                                    onChange={(e) => handleInputChange('gradingDetails', e.target.value)}
                                                    className="w-full p-3 border rounded-lg h-24"
                                                    placeholder="Describe how the assignment will be graded..."
                                                />
                                            </div>
                                        )}

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Key Success Criteria</label>
                                            <textarea
                                                value={formData.keySuccessCriteria}
                                                onChange={(e) => handleInputChange('keySuccessCriteria', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-24"
                                                placeholder="List the key points that indicate successful completion..."
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Tips for Success</label>
                                            <textarea
                                                value={formData.tipsForSuccess}
                                                onChange={(e) => handleInputChange('tipsForSuccess', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-24"
                                                placeholder="Provide helpful tips and advice for students..."
                                            />
                                        </div>

                                        <div>
                                            <label className="block text-sm font-medium text-gray-700 mb-1">Special Instructions (Optional)</label>
                                            <textarea
                                                value={formData.specialInstructions}
                                                onChange={(e) => handleInputChange('specialInstructions', e.target.value)}
                                                className="w-full p-3 border rounded-lg h-20"
                                                placeholder="Any additional instructions or notes..."
                                            />
                                        </div>
                                    </div>
                                </div>

                                {/* Action Buttons */}
                                <div className="flex gap-4 pt-6">
                                    <button
                                        onClick={generateAIPrompt}
                                        disabled={!isFormValid}
                                        className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium ${isFormValid
                                                ? 'bg-orange-600 text-white hover:bg-orange-700'
                                                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                            }`}
                                    >
                                        <Sparkles className="w-5 h-5" />
                                        Generate AI Prompt
                                    </button>

                                    <button
                                        onClick={generateAssignmentHTML}
                                        disabled={!isFormValid}
                                        className={`flex items-center gap-2 px-6 py-3 rounded-lg font-medium ${isFormValid
                                                ? 'bg-green-600 text-white hover:bg-green-700'
                                                : 'bg-gray-300 text-gray-500 cursor-not-allowed'
                                            }`}
                                    >
                                        <Code className="w-5 h-5" />
                                        Generate D2L HTML
                                    </button>
                                </div>

                                {!isFormValid && (
                                    <div className="mt-4 p-3 bg-yellow-50 border border-yellow-200 rounded">
                                        <p className="text-sm text-yellow-700">
                                            <strong> Required:</strong> Complete assignment title, number, weight, subject area, description, at least one CLO, and due date (week or custom).
                                        </p>
                                    </div>
                                )}
                            </div>
                        </div>
                    ) : (
                        <div>
                            {/* Generated Content Display */}
                            <div className="mb-6">
                                <div className="flex items-center justify-between mb-4">
                                    <h3 className="text-lg font-semibold text-gray-800">
                                        {showPrompt ? 'Generated AI Prompt' : 'Generated D2L Assignment HTML'}
                                    </h3>
                                    <div className="flex gap-2">
                                        <button
                                            onClick={() => {
                                                setShowPrompt(false);
                                                setShowHTML(false);
                                            }}
                                            className="px-4 py-2 text-gray-600 hover:text-gray-800"
                                        >
                                             Back to Form
                                        </button>
                                    </div>
                                </div>

                                <div className="bg-gray-900 text-gray-100 p-4 rounded-lg mb-4 max-h-96 overflow-y-auto">
                                    <pre className="whitespace-pre-wrap text-sm">
                                        {showPrompt ? generatedPrompt : generatedHTML}
                                    </pre>
                                </div>

                                <div className="flex gap-4">
                                    {showPrompt && (
                                        <>
                                            <button
                                                onClick={exportPrompt}
                                                className="flex items-center gap-2 bg-orange-600 text-white px-4 py-2 rounded-lg hover:bg-orange-700"
                                            >
                                                <Download className="w-4 h-4" />
                                                Export Prompt (.txt)
                                            </button>
                                            <button
                                                onClick={() => copyToClipboard(generatedPrompt)}
                                                className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                                            >
                                                <FileText className="w-4 h-4" />
                                                Copy to Clipboard
                                            </button>
                                        </>
                                    )}

                                    {showHTML && (
                                        <>
                                            <button
                                                onClick={exportHTML}
                                                className="flex items-center gap-2 bg-green-600 text-white px-4 py-2 rounded-lg hover:bg-green-700"
                                            >
                                                <Download className="w-4 h-4" />
                                                Export D2L HTML
                                            </button>
                                            <button
                                                onClick={() => copyToClipboard(generatedHTML)}
                                                className="flex items-center gap-2 bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700"
                                            >
                                                <Code className="w-4 h-4" />
                                                Copy for D2L
                                            </button>
                                        </>
                                    )}
                                </div>
                            </div>
                        </div>
                    )}
                </div>
            </div>
        </div>
    );
};

export default AssignmentPromptGenerator;
