                                key={level}
                                className={`late-policy-card ${level} ${gradingData.latePolicy.level === level ? 'selected' : ''}`}
                                onClick={() => updateLatePolicy(level)}
                            >
                                <div className="late-policy-header">
                                    <input
                                        type="checkbox"
                                        checked={gradingData.latePolicy.level === level}
                                        onChange={() => updateLatePolicy(level)}
                                        className="late-policy-checkbox"
                                        onClick={(e) => e.stopPropagation()}
                                    />
                                    <span
                                        className="late-policy-icon"
                                        style={{ backgroundColor: policy.color }}
                                    ></span>
                                    <h5 className="late-policy-title" style={{ color: policy.color }}>
                                        {policy.name}
                                    </h5>
                                    {level !== 'none' && (
                                        <span className="late-policy-multiplier">
                                            (Ã—{policy.multiplier === 0 ? '0' : policy.multiplier})
                                        </span>
                                    )}
                                </div>
                                <p className="late-policy-description">{policy.description}</p>
                            </div>
                        ))}
                    </div>

                    {calculateTotalScore().penaltyApplied && (
                        <div className="late-policy-calculation">
                            <h5 className="font-medium text-red-800 mb-2">âš ï¸ Late Penalty Calculation</h5>
                            <div className="text-sm text-red-700 space-y-1">
                                <p><strong>Raw Score:</strong> {Math.round(calculateTotalScore().rawScore * 10) / 10} / {loadedRubric ? loadedRubric.assignmentInfo.totalPoints : gradingData.assignment.maxPoints}</p>
                                <p><strong>Late Penalty:</strong> Ã—{latePolicyLevels[gradingData.latePolicy.level].multiplier}</p>
                                <p><strong>Final Score:</strong> {Math.round(calculateTotalScore().finalScore * 10) / 10} / {loadedRubric ? loadedRubric.assignmentInfo.totalPoints : gradingData.assignment.maxPoints}</p>
                                <p className="text-xs mt-2 italic">{latePolicyLevels[gradingData.latePolicy.level].description}</p>
                            </div>
                        </div>
                    )}
                </div>

                {/* File Attachments */}
                <div className="mb-8">
                    <h3 className="text-lg font-semibold text-gray-800 mb-4">File Attachments</h3>
                    <div className="border-2 border-dashed border-gray-300 rounded-lg p-6 text-center">
                        <input
                            ref={fileInputRef}
                            type="file"
                            multiple
                            onChange={handleFileUpload}
                            className="hidden"
                        />
                        <button
                            onClick={() => fileInputRef.current?.click()}
                            className="bg-blue-600 hover:bg-blue-700 text-white px-6 py-3 rounded flex items-center gap-2 mx-auto"
                        >
                            <Plus size={20} />
                            Add Files
                        </button>
                        <p className="text-gray-500 mt-2">
                            Upload student work files, screenshots, or documentation
                        </p>
                    </div>

                    {gradingData.attachments.length > 0 && (
                        <div className="mt-4 grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                            {gradingData.attachments.map((file) => (
                                <div key={file.id} className="border rounded p-3 flex items-center justify-between">
                                    <div className="flex items-center gap-2">
                                        {file.type.startsWith('image/') && <FileText size={20} className="text-blue-600" />}
                                        {file.type === 'application/pdf' && <FileText size={20} className="text-red-600" />}
                                        {file.type.startsWith('video/') && <Video size={20} className="text-green-600" />}
                                        <div>
                                            <p className="font-medium text-sm">{file.name}</p>
                                            <p className="text-xs text-gray-500">{(file.size / 1024).toFixed(1)} KB</p>
                                        </div>
                                    </div>
                                    <button
                                        onClick={() => removeAttachment(file.id)}
                                        className="text-red-600 hover:text-red-800"
                                    >
                                        <X size={16} />
                                    </button>
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                {/* Video Links Section */}
                <div className="mb-8">
                    <h3 className="text-lg font-semibold text-gray-800 mb-4">Video Review Links</h3>
                    <div className="bg-gradient-to-r from-purple-50 to-blue-50 border border-purple-200 rounded-lg p-6">
                        <div className="flex flex-col lg:flex-row gap-4 mb-4">
                            <div className="flex-1">
                                <label className="block text-sm font-medium text-gray-700 mb-1">Video URL</label>
                                <input
                                    type="url"
                                    value={videoLinkInput}
                                    onChange={(e) => setVideoLinkInput(e.target.value)}
                                    placeholder="https://syncsketch.com/sketch/... or https://[school].hosted.panopto.com/..."
                                    className="w-full p-3 border rounded-lg"
                                />
                                <p className="text-xs text-gray-500 mt-1">
                                    Supports: SyncSketch, Panopto, YouTube, Vimeo, and generic video links
                                </p>
                            </div>
                            <div className="lg:w-64">
                                <label className="block text-sm font-medium text-gray-700 mb-1">Title (Optional)</label>
                                <input
                                    type="text"
                                    value={videoTitle}
                                    onChange={(e) => setVideoTitle(e.target.value)}
                                    placeholder="Video title or description"
                                    className="w-full p-3 border rounded-lg"
                                />
                            </div>
                            <div className="flex items-end">
                                <button
                                    onClick={addVideoLink}
                                    disabled={!videoLinkInput.trim()}
                                    className="bg-purple-600 hover:bg-purple-700 disabled:bg-gray-400 text-white px-6 py-3 rounded-lg flex items-center gap-2"
                                >
                                    <Plus size={18} />
                                    Add Video
                                </button>
                            </div>
                        </div>
                    </div>

                    {/* Display Video Links */}
                    {gradingData.videoLinks.length > 0 && (
                        <div className="mt-6 space-y-4">
                            {gradingData.videoLinks.map((videoLink) => (
                                <div key={videoLink.id} className="border rounded-lg overflow-hidden">
                                    <div className="bg-gray-50 p-4 flex items-center justify-between">
                                        <div className="flex items-center gap-3">
                                            <div className="flex items-center gap-2">
                                                <div className="w-8 h-8 bg-blue-500 rounded flex items-center justify-center">
                                                    <Video size={16} className="text-white" />
                                                </div>
                                            </div>
                                            <div>
                                                <p className="font-medium text-gray-800">{videoLink.title}</p>
                                                <p className="text-sm text-gray-500 capitalize">
                                                    {videoLink.platform} â€¢ {videoLink.type === 'embed' ? 'Embedded' : 'External Link'}
                                                </p>
                                            </div>
                                        </div>
                                        <div className="flex items-center gap-2">
                                            {videoLink.type === 'link' ? (
                                                <a
                                                    href={videoLink.url}
                                                    target="_blank"
                                                    rel="noopener noreferrer"
                                                    className="bg-blue-600 hover:bg-blue-700 text-white px-3 py-1 rounded text-sm"
                                                >
                                                    Open Link
                                                </a>
                                            ) : (
                                                <span className="text-green-600 text-sm font-medium">Embedded Below</span>
                                            )}
                                            <button
                                                onClick={() => removeVideoLink(videoLink.id)}
                                                className="text-red-600 hover:text-red-800 p-1"
                                            >
                                                <X size={16} />
                                            </button>
                                        </div>
                                    </div>

                                    {/* Embedded Video Display */}
                                    {videoLink.type === 'embed' && (
                                        <div className="p-4 bg-black">
                                            <div className="relative w-full" style={{ paddingBottom: '56.25%' }}>
                                                <iframe
                                                    src={videoLink.embedUrl}
                                                    title={videoLink.title}
                                                    className="absolute top-0 left-0 w-full h-full rounded"
                                                    frameBorder="0"
                                                    allow="accelerometer; autoplay; clipboard-write; encrypted-media; gyroscope; picture-in-picture"
                                                    allowFullScreen
                                                />
                                            </div>
                                        </div>
                                    )}
                                </div>
                            ))}
                        </div>
                    )}
                </div>
            </div>

            {/* Hidden file inputs */}
            <input
                ref={rubricInputRef}
                type="file"
                accept=".json"
                onChange={loadRubric}
                className="hidden"
            />
        </div>
    );
};

export default GradingTemplate;
