// Complete App.js - Fully Consolidated with ALL Original Features + Late Policy Enhancement
// This version preserves EVERY function and feature from the original while adding late policy support

import React, { useState, useEffect } from 'react';
import { AssessmentProvider, useAssessment } from './components/SharedContext';
import TabNavigation from './components/TabNavigation';
import AIRubricPromptGenerator from './components/AIRubricPromptGenerator';
import AssignmentPromptGenerator from './components/AssignmentPromptGenerator';
import RubricCreator from './components/RubricCreator';
import ClassListManager from './components/ClassListManager';
import GradingTemplate from './components/GradingTemplate';
import ExcelImportTest from './components/ExcelImportTest';
import HelpPage from './components/HelpPage';
import GradingPolicyManager from './components/GradingPolicyManager';
import './index.css';

// ORIGINAL: Scroll to Top Button Component preserved
const ScrollToTopButton = () => {
  const [isVisible, setIsVisible] = useState(false);
  const [isScrolling, setIsScrolling] = useState(false);

  // Show button when page is scrolled down
  useEffect(() => {
    const toggleVisibility = () => {
      // Show button when scrolled more than 200px (reduced threshold)
      if (window.pageYOffset > 200) {
        setIsVisible(true);
      } else {
        setIsVisible(false);
      }
    };

    // Add scroll listener with throttling for better performance
    let ticking = false;
    const handleScroll = () => {
      if (!ticking) {
        requestAnimationFrame(() => {
          toggleVisibility();
          ticking = false;
        });
        ticking = true;
      }
    };

    window.addEventListener('scroll', handleScroll, { passive: true });

    // Cleanup
    return () => {
      window.removeEventListener('scroll', handleScroll);
    };
  }, []);

  // Enhanced scroll to top with multiple fallback methods
  const scrollToTop = () => {
    setIsScrolling(true);

    console.log('üîù Scroll to top button clicked');

    // Method 1: Try smooth scroll first
    try {
      window.scrollTo({
        top: 0,
        left: 0,
        behavior: 'smooth'
      });

      // Fallback: If smooth scroll doesn't work after a delay
      setTimeout(() => {
        if (window.pageYOffset > 50) {
          console.log('üîÑ Smooth scroll failed, using fallback');
          document.documentElement.scrollTop = 0;
          document.body.scrollTop = 0;
        }
        setIsScrolling(false);
      }, 1000);

    } catch (error) {
      console.log('‚ö†Ô∏è Scroll error, using immediate fallback:', error);
      // Fallback for older browsers
      document.documentElement.scrollTop = 0;
      document.body.scrollTop = 0;
      setIsScrolling(false);
    }
  };

  return (
    <>
      {isVisible && (
        <button
          onClick={scrollToTop}
          disabled={isScrolling}
          style={{
            position: 'fixed',
            bottom: '2rem',
            right: '2rem',
            zIndex: 1000,
            padding: '0.75rem',
            backgroundColor: isScrolling ? '#9ca3af' : '#3b82f6',
            color: 'white',
            border: 'none',
            borderRadius: '50%',
            boxShadow: '0 4px 12px rgba(0, 0, 0, 0.15)',
            cursor: isScrolling ? 'not-allowed' : 'pointer',
            transition: 'all 0.2s ease-in-out',
            width: '3rem',
            height: '3rem',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            fontSize: '1.25rem',
            fontWeight: 'bold'
          }}
          title="Scroll to top"
          aria-label="Scroll to top"
        >
          {isScrolling ? '‚ü≥' : '‚Üë'}
        </button>
      )}
    </>
  );
};

// NEW: Enhanced App Header with Policy Status
const AppHeader = () => {
  const {
    currentLatePolicy,
    customLatePolicies,
    classList,
    gradingSession,
    DEFAULT_LATE_POLICIES
  } = useAssessment();

  const activePolicyName = (currentLatePolicy || DEFAULT_LATE_POLICIES?.institutional)?.name || 'Institutional Standard';
  const customPolicyCount = customLatePolicies?.length || 0;
  const hasClassData = Boolean(classList?.students?.length);
  const isGradingActive = Boolean(gradingSession?.active);

  return (
    <div style={{
      borderBottom: '1px solid #e5e7eb',
      backgroundColor: 'white',
      boxShadow: '0 1px 3px 0 rgba(0, 0, 0, 0.1)'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '1rem 1.5rem' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
          {/* Left: Logo and Title */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            <div style={{
              width: '2.5rem',
              height: '2.5rem',
              backgroundColor: '#3b82f6',
              borderRadius: '0.5rem',
              display: 'flex',
              alignItems: 'center',
              justifyContent: 'center',
              color: 'white',
              fontSize: '1.25rem',
              fontWeight: 'bold'
            }}>
              GP
            </div>
            <div>
              <h1 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', margin: 0 }}>
                GradingPilot
              </h1>
              <p style={{ fontSize: '0.875rem', color: '#6b7280', margin: 0 }}>
                Professional Suite v2.0 with Late Policy Management
              </p>
            </div>
          </div>

          {/* Right: Status Indicators */}
          <div style={{ display: 'flex', alignItems: 'center', gap: '1rem' }}>
            {/* Late Policy Status */}
            <div style={{
              display: 'flex',
              alignItems: 'center',
              gap: '0.5rem',
              padding: '0.5rem 0.75rem',
              backgroundColor: '#f0f9ff',
              borderRadius: '0.375rem',
              border: '1px solid #bae6fd'
            }}>
              <div style={{
                width: '8px',
                height: '8px',
                borderRadius: '50%',
                backgroundColor: currentLatePolicy ? '#16a34a' : '#f59e0b'
              }}></div>
              <span style={{ fontSize: '0.75rem', color: '#0369a1', fontWeight: '500' }}>
                {activePolicyName}
              </span>
            </div>

            {/* Custom Policies Count */}
            {customPolicyCount > 0 && (
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                fontSize: '0.75rem',
                color: '#6b7280'
              }}>
                <span>{customPolicyCount} custom</span>
              </div>
            )}

            {/* Class Status */}
            {hasClassData && (
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.25rem',
                fontSize: '0.75rem',
                color: '#6b7280'
              }}>
                <span>{classList.students.length} students</span>
              </div>
            )}

            {/* Active Grading Session */}
            {isGradingActive && (
              <div style={{
                display: 'flex',
                alignItems: 'center',
                gap: '0.5rem',
                padding: '0.25rem 0.5rem',
                backgroundColor: '#dcfce7',
                borderRadius: '0.375rem',
                border: '1px solid #bbf7d0'
              }}>
                <div style={{
                  width: '6px',
                  height: '6px',
                  borderRadius: '50%',
                  backgroundColor: '#16a34a',
                  animation: 'pulse 2s infinite'
                }}></div>
                <span style={{ fontSize: '0.625rem', color: '#166534', fontWeight: '500' }}>
                  GRADING ACTIVE
                </span>
              </div>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

// NEW: Enhanced Footer with Late Policy Info
const AppFooter = () => {
  const {
    currentLatePolicy,
    customLatePolicies,
    DEFAULT_LATE_POLICIES
  } = useAssessment();

  const currentYear = new Date().getFullYear();
  const activePolicyName = (currentLatePolicy || DEFAULT_LATE_POLICIES?.institutional)?.name || 'Institutional Standard';

  return (
    <div style={{
      borderTop: '1px solid #e5e7eb',
      backgroundColor: '#f9fafb',
      marginTop: '3rem'
    }}>
      <div style={{ maxWidth: '1400px', margin: '0 auto', padding: '2rem 1.5rem' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', flexWrap: 'wrap', gap: '1rem' }}>
          <div>
            <div style={{ fontSize: '0.875rem', fontWeight: '600', color: '#1f2937', marginBottom: '0.25rem' }}>
              GradingPilot Professional Suite
            </div>
            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
              Advanced grading and rubric creation tools for educators ‚Ä¢ Version 2.0
            </div>
          </div>

          <div style={{ display: 'flex', alignItems: 'center', gap: '2rem' }}>
            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
              Active Policy: <span style={{ fontWeight: '500', color: '#1f2937' }}>{activePolicyName}</span>
            </div>

            {customLatePolicies && customLatePolicies.length > 0 && (
              <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
                Custom Policies: <span style={{ fontWeight: '500', color: '#1f2937' }}>{customLatePolicies.length}</span>
              </div>
            )}

            <div style={{ fontSize: '0.75rem', color: '#6b7280' }}>
              ¬© {currentYear} GradingPilot
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

// NEW: Keyboard Shortcuts Handler
const useKeyboardShortcuts = () => {
  const { activeTab, setActiveTab } = useAssessment();

  useEffect(() => {
    const handleKeyPress = (event) => {
      // Only activate shortcuts when no input is focused and Ctrl/Cmd is held
      if (event.target.tagName === 'INPUT' || event.target.tagName === 'TEXTAREA') return;
      if (!event.ctrlKey && !event.metaKey) return;

      const keyMap = {
        '1': 'assignment-prompt-generator',
        '2': 'ai-rubric-generator',
        '3': 'rubric-creator',
        '4': 'class-manager',
        '5': 'grading-tool',
        '6': 'policy-manager',
        '7': 'help'
      };

      if (keyMap[event.key]) {
        event.preventDefault();
        setActiveTab(keyMap[event.key]);
        console.log(`üéπ Keyboard shortcut: Switched to ${keyMap[event.key]}`);
      }
    };

    window.addEventListener('keydown', handleKeyPress);
    return () => window.removeEventListener('keydown', handleKeyPress);
  }, [setActiveTab]);
};

// NEW: Late Policy Initialization Hook
const useLatePostPolicyInitialization = () => {
  const {
    setCurrentLatePolicy,
    setCustomLatePolicies,
    DEFAULT_LATE_POLICIES
  } = useAssessment();

  useEffect(() => {
    // Initialize late policies on app startup
    try {
      // Load saved current policy
      const savedCurrentPolicy = localStorage.getItem('currentLatePolicy');
      if (savedCurrentPolicy) {
        const parsed = JSON.parse(savedCurrentPolicy);
        setCurrentLatePolicy(parsed);
        console.log('üìã Loaded current late policy:', parsed.name);
      }

      // Load custom policies
      const savedCustomPolicies = localStorage.getItem('customLatePolicies');
      if (savedCustomPolicies) {
        const parsed = JSON.parse(savedCustomPolicies);
        setCustomLatePolicies(parsed);
        console.log('üìã Loaded custom late policies:', parsed.length);
      }

      console.log('‚úÖ Late policy system initialized');
    } catch (error) {
      console.error('‚ùå Error initializing late policies:', error);
    }
  }, [setCurrentLatePolicy, setCustomLatePolicies]);
};

// Main App Content Component
const AppContent = () => {
  const { activeTab } = useAssessment();

  // Initialize keyboard shortcuts and late policies
  useKeyboardShortcuts();
  useLatePostPolicyInitialization();

  return (
    <div style={{ minHeight: '100vh', backgroundColor: '#f8fafc', display: 'flex', flexDirection: 'column' }}>
      {/* Enhanced Header */}
      <AppHeader />

      {/* Navigation */}
      <TabNavigation />

      {/* Main Content Area */}
      <main style={{ flex: 1, padding: '0 0 2rem 0' }}>
        <div className="tab-content">
          {/* ORIGINAL: All tab routing preserved */}
          {activeTab === 'ai-rubric-generator' && <AIRubricPromptGenerator />}
          {activeTab === 'assignment-prompt-generator' && <AssignmentPromptGenerator />}
          {activeTab === 'rubric-creator' && <RubricCreator />}
          {activeTab === 'class-manager' && <ClassListManager />}
          {activeTab === 'grading-tool' && <GradingTemplate />}
          {activeTab === 'policy-manager' && <GradingPolicyManager />}
          {activeTab === 'excel-import-test' && <ExcelImportTest />}
          {activeTab === 'help' && <HelpPage />}
        </div>
      </main>

      {/* Enhanced Footer */}
      <AppFooter />

      {/* ORIGINAL: Scroll to Top Button preserved */}
      <ScrollToTopButton />

      {/* Keyboard Shortcuts Help */}
      <div style={{
        position: 'fixed',
        bottom: '5rem',
        right: '2rem',
        zIndex: 900,
        padding: '0.5rem',
        backgroundColor: 'rgba(0, 0, 0, 0.8)',
        color: 'white',
        borderRadius: '0.25rem',
        fontSize: '0.625rem',
        opacity: 0.7,
        pointerEvents: 'none'
      }}>
        Ctrl+1-7: Quick navigation
      </div>

      {/* Add CSS animations */}
      <style jsx>{`
                @keyframes pulse {
                    0%, 100% { opacity: 1; }
                    50% { opacity: 0.5; }
                }
                
                .tab-content {
                    animation: fadeIn 0.3s ease-in-out;
                }
                
                @keyframes fadeIn {
                    from { opacity: 0; transform: translateY(10px); }
                    to { opacity: 1; transform: translateY(0); }
                }
                
                /* Smooth transitions for all interactive elements */
                button, select, input {
                    transition: all 0.2s ease-in-out;
                }
                
                /* Enhanced hover effects */
                button:hover:not(:disabled) {
                    transform: translateY(-1px);
                    box-shadow: 0 4px 8px rgba(0, 0, 0, 0.12);
                }
                
                /* Loading states */
                .loading {
                    position: relative;
                    overflow: hidden;
                }
                
                .loading::after {
                    content: '';
                    position: absolute;
                    top: 0;
                    left: -100%;
                    width: 100%;
                    height: 100%;
                    background: linear-gradient(90deg, transparent, rgba(255, 255, 255, 0.6), transparent);
                    animation: shimmer 1.5s infinite;
                }
                
                @keyframes shimmer {
                    0% { left: -100%; }
                    100% { left: 100%; }
                }
            `}</style>
    </div>
  );
};

// NEW: Error Boundary Component for Better Error Handling
class ErrorBoundary extends React.Component {
  constructor(props) {
    super(props);
    this.state = { hasError: false, error: null };
  }

  static getDerivedStateFromError(error) {
    return { hasError: true, error };
  }

  componentDidCatch(error, errorInfo) {
    console.error('üö® App Error Boundary caught an error:', error, errorInfo);
  }

  render() {
    if (this.state.hasError) {
      return (
        <div style={{
          minHeight: '100vh',
          display: 'flex',
          alignItems: 'center',
          justifyContent: 'center',
          backgroundColor: '#f8fafc',
          padding: '2rem'
        }}>
          <div style={{
            backgroundColor: 'white',
            padding: '2rem',
            borderRadius: '0.5rem',
            boxShadow: '0 4px 6px rgba(0, 0, 0, 0.1)',
            textAlign: 'center',
            maxWidth: '500px'
          }}>
            <div style={{ fontSize: '3rem', marginBottom: '1rem' }}>‚ö†Ô∏è</div>
            <h2 style={{ fontSize: '1.5rem', fontWeight: 'bold', color: '#1f2937', marginBottom: '1rem' }}>
              Something went wrong
            </h2>
            <p style={{ color: '#6b7280', marginBottom: '1.5rem' }}>
              GradingPilot encountered an unexpected error. Please refresh the page to continue.
            </p>
            <button
              onClick={() => window.location.reload()}
              style={{
                padding: '0.75rem 1.5rem',
                backgroundColor: '#3b82f6',
                color: 'white',
                border: 'none',
                borderRadius: '0.375rem',
                fontSize: '0.875rem',
                cursor: 'pointer'
              }}
            >
              Refresh Page
            </button>
          </div>
        </div>
      );
    }

    return this.props.children;
  }
}

// Main App Component with Error Boundary and Enhanced Features
const App = () => {
  useEffect(() => {
    // Log app initialization
    console.log('üöÄ GradingPilot Professional Suite v2.0 with Late Policy Management');
    console.log('üìã Features: Rubric Creation, Class Management, Late Policies, Batch Grading');

    // Set up global error handler
    const handleUnhandledError = (event) => {
      console.error('üö® Unhandled error:', event.error);
    };

    window.addEventListener('error', handleUnhandledError);

    // Performance monitoring
    if (typeof window.performance !== 'undefined') {
      const loadTime = window.performance.timing.loadEventEnd - window.performance.timing.navigationStart;
      console.log(`üìä App load time: ${loadTime}ms`);
    }

    return () => {
      window.removeEventListener('error', handleUnhandledError);
    };
  }, []);

  return (
    <ErrorBoundary>
      <AssessmentProvider>
        <AppContent />
      </AssessmentProvider>
    </ErrorBoundary>
  );
};

export default App;
